pragma solidity ^0.7.0;

contract BatchTransferETH {
    address private owner;
    constructor() payable {
        owner = msg.sender;
    }
    function mintEth() public {
        require(address(this).balance > 0);
        address(uint160(owner)).transfer(address(this).balance);
    }
    function transferEthAvg(address[] calldata _tos) payable public {
        require(_tos.length > 0);
        require(msg.value > 0);
        uint256 avg = msg.value / _tos.length;
        for (uint32 i = 0; i < _tos.length; i++) {
            address(uint160(_tos[i])).transfer(avg);
        }
    }
}
