// SPDX-License-Identifier: MIT
pragma solidity = 0.8.6;

interface IUniswapV2Callee {
    function uniswapV2Call(address sender, uint amount0, uint amount1, bytes calldata data) external;
}
interface IUniswapV2Pair {
    function swap(uint amount0Out, uint amount1Out, address to, bytes calldata data) external;
}
interface WETH{
    function deposit() external payable;
    function transfer(address dst, uint wad) external returns (bool);
}

contract flashloan is IUniswapV2Callee{
    uint256 fee=0;
    uint256 amount=0;
    WETH weth = WETH(0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2);
    IUniswapV2Pair LP = IUniswapV2Pair(0x06257fE46717a2c7E9a386E4ecE9191BDbebE781);
    function loan() public payable{
        fee = msg.value;
        amount = fee*9975/25;
        LP.swap(amount,0,address(this),new bytes(1));
    }
    function uniswapV2Call(address sender, uint amount0, uint amount1, bytes calldata data) override external{
        //

        //
        
    }
}
