    "contracts/interfaces/liquidityManagement/insuranceFund/IFund.sol": {
      "content": "// SPDX-License-Identifier: GPL-3.0
pragma solidity 0.8.22;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {IPool} from "contracts/interfaces/liquidityManagement/liquidityPool/IPool.sol";
import {FundDebt} from "./Debt.sol";

interface IFund {
    /**
     * @notice Emitted when tokens are supplied to the insurance fund.
     * @dev Emitted from [`supply()`](#supply).
     * @param amount The amount of tokens supplied.
     */
    event Supplied(uint256 indexed amount);

    /**
     * @notice Emitted when tokens are forwarded to the liquidity pool.
     * @dev Emitted from [`forward()`](#forward).
     * @param amount The amount of tokens forwarded.
     */
    event Forwarded(uint256 indexed amount);

    /**
     * @notice Emitted when a debt is successfully authorized and forwarded.
     * @dev Emitted from [`authorize()`](#authorize).
     * @param id The identifier of the authorized debt.
     */
    event DebtPaid(bytes32 indexed id);

    /**
     * @notice Error indicating that a debt with the specified ID does not exist.
     * @dev Fired from [`authorize()`](#authorize).
     */
    error DebtDoesNotExists(bytes32 id);

    /**
     * @notice Error indicating that the sum of the debt amount and rewards exceeds the contract's token balance.
     * @dev Fired from [`authorize()`](#authorize).
     */
    error DebtAmountExceedsBalance(bytes32 id);

    /**
     * @notice Error indicating that the account sent transaction has zero token balance on the contract.
     * @dev Fired from [`withdrawAll()`](#withdrawall).
     */
    error NothingToWithdraw();

    /**
     * @notice Allows authorized addresses to supply tokens in Insurance Fund.
     * @dev Requires `msg.sender` to have the `keccak256('FORWARDER_ROLE')` role within the fund contract,
     *      otherwise, it throws an `"AccessControl: account msg.sender is missing role keccak256('FORWARDER_ROLE')"` error.
     * @dev Emits a [`Supplied()`](#supplied) event on successful supply.
     * @param amount of tokens to supply.
     * @param beneficiary optional address which may receive surplus from the future spending.
     */
    function supply(uint256 amount, address beneficiary) external;

    /**
     * @notice Allows authorized addresses to forward tokens in Liquidity Pool
     *      (see [`IPool.returnAndDistribute()`](/interfaces/liquidityManagement/liquidityPool/IPool.sol/interface.IPool.html#returnanddistribute)).
     * @dev Requires `msg.sender` to have the `keccak256('FORWARDER_ROLE')` role within the fund contract,
     *      otherwise, it throws an `"AccessControl: account msg.sender is missing role keccak256('FORWARDER_ROLE')"` error.
     * @dev Emits a [`Forwarded()`](#forwarded) event on success.
     * @param amount of token to return to liquidity pool.
     * @param rewards amount of rewards to add to liquidity pool.
     */
    function forward(uint256 amount, uint256 rewards) external;

    /**
     * @notice Allows authorized addresses to approve a debt and potentially [`forward()`](#forward) it.
     * @dev Throws a [`DebtDoesNotExists()`](#debtdoesnotexists) error if the corresponding `amount` and `reward` are zero.
     * @dev Throws a [`DebtAmountExceedsBalance()`](#debtamountexceedsbalance) error if the sum of `amount` and `reward` exceeds the contract's token balance.
     * @dev Emits a [`DebtPaid()`](#debtpaid) event on success.
     * @param id Identifier of the debt to authorize.
     */
    function authorize(bytes32 id) external;

    /**
     * @notice Allows account owner to withdraw its whole token balance.
     * @dev Throws a [`NothingToWithdraw()`](#nothingtowithdraw) error if the account has zero balance.
     */
    function withdrawAll() external;

    /**
     * @notice Returns contract balance.
     * @return returns The internal token balance of the contract.
     */
    function balance() external view returns (uint256);

    /**
     * @notice Returns account balance.
     * @param account The address of the account to check the balance of.
     * @return returns The balance of the account.
     */
    function balanceOf(address account) external view returns (uint256);

    /**
     * @notice Returns the details of a debt.
     * @param id Identifier of the debt.
     * @return A [`FundDebt`](../Debt.sol/struct.FundDebt.html) struct containing information about the debt.
     */
    function getDebt(bytes32 id) external view returns (FundDebt memory);

    /**
     * @notice Liquidity pool associated with this insurance fund.
     */
    function pool() external view returns (IPool);

    /**
     * @notice ERC20 token associated with the pool associated with this fund.
     */
    function token() external view returns (IERC20);
}
"
    },
