    "contracts/interfaces/liquidityManagement/liquidityPool/IPool.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.22;

/**
 * @title IPool
 * @notice Interface for a liquidity pool.
 */
interface IPool {
    /**
     * @notice Emitted when tokens are borrowed from the pool.
     * @param amount The amount of tokens borrowed.
     * @dev Emitted from [`borrow()`](#borrow).
     */
    event Borrowed(uint256 amount);

    /**
     * @notice Emitted when tokens are claimed.
     * @param recipient The address of the recipient.
     * @param amount The amount of rewards claimed.
     * @dev Emitted from [`claim()`](#claim).
     */
    event Claimed(address indexed recipient, uint256 amount);

    /**
     * @notice Emitted when tokens are deposited into a liquidity pool.
     * @param depositor The address of the depositor.
     * @param amount The amount of tokens deposited.
     * @dev Emitted from [`deposit()`](#deposit).
     */
    event Deposited(address indexed depositor, uint256 amount);

    /**
     * @notice Emitted when tokens are returned to the pool, repaying debts, and triggering an epoch switch.
     * @param distributed The amount of tokens distributed as rewards in the current epoch.
     * @param returned The amount of tokens used to repay debts.
     * @dev Emitted from [`returnAndDistribute()`](#returnanddistribute).
     */
    event Returned(uint256 returned, uint256 distributed);

    /**
     * @notice Emitted when tokens are withdrawn from the pool.
     * @param amount The amount of tokens withdrawn.
     * @param recipient The address of the recipient.
     * @dev Emitted from [`withdraw()`](#withdraw).
     */
    event Withdrawn(address indexed recipient, uint256 amount);

    /**
     * @notice Fired from [`borrow()`](#borrow).
     * @dev Error indicating that the borrower's requested amount exceeds the available balance in the pool.
     */
    error BorrowAmountExceedsBalance();

    /**
     * @notice Error indicating that the Merkle tree verification failed.
     * @dev Fired from [`deposit()`](#deposit).
     */
    error MerkleTreeVerificationFailed();

    /**
     * @notice Error indicating that the total deposit threshold has been exceeded by the user's deposit.
     * @dev Fired from [`deposit()`](#deposit).
     */
    error TotalDepositThresholdExceeded();

    /**
     * @notice Error indicating that there are no pending rewards for the user.
     * @dev Fired from [`claim()`](#claim).
     */
    error NoPendingRewards();

    /**
     * @notice Error indicating that the user is trying to withdraw an amount greater than their position's value.
     * @dev Fired from [`withdraw()`](#withdraw).
     */
    error WithdrawAmountExceedsBalance();

    /**
     * @notice Allows whitelisted addresses to deposit tokens into the liquidity pool.
     * @dev If verification is not disabled (see [`IMerkleTreeWhitelist.setRoot()`](/interfaces/liquidityManagement/liquidityPool/IMerkleTreeWhitelist.sol/interface.IMerkleTreeWhitelist.html#setroot)),
     *      it requires a valid Merkle Proof for `msg.sender` to confirm deposit authorization.
     * @dev Throws a [`TotalDepositThresholdExceeded()`](#totaldepositthresholdexceeded) error if `amount` + `token.balanceOf(pool)` exceeds [`thresholdOnTotalDeposit()`](#thresholdontotaldeposit).
     * @dev Emits a [`Deposited()`](#deposited) event on successful deposit.
     * @param amount The amount of tokens to deposit.
     * @param permission Merkle Proof for `msg.sender`, confirming deposit authorization.
     */
    function deposit(uint128 amount, bytes32[] calldata permission) external;

    /**
     * @notice Allows a user to withdraw tokens from the liquidity pool.
     * @dev If the pool has a sufficient token balance, tokens will be sent immediately to the specified `recipient`.
     *      Otherwise, the withdrawal amount will be added to the debt queue for processing during [`returnAndDistribute()`](#returnanddistribute).
     * @dev If `recipient` is `address(0)`, it will be changed to `msg.sender`.
     * @dev Throws a [`WithdrawAmountExceedsBalance()`](#withdrawamountexceedsbalance) error if the user has an insufficient balance.
     * @dev Emits a [`Withdrawn()`](#withdrawn) event on successful withdrawal.
     * @param amount The amount of tokens to withdraw.
     * @param recipient The address that will receive the withdrawn tokens. If `address(0)` is passed, it will be changed to `msg.sender`.
     */
    function withdraw(uint128 amount, address recipient) external;

    /**
     * @notice Allows a user to claim rewards earned in the liquidity pool.
     * @dev If the pool has a sufficient token balance, rewards will be sent immediately to the `recipient`.
     *      Otherwise, the rewards will be added to the debt queue for processing during [`returnAndDistribute()`](#returnanddistribute).
     * @dev If `recipient` is `address(0)`, it will be changed to `msg.sender`.
     * @dev Throws a [`NoPendingRewards()`](#nopendingrewards) error if the user has no pending rewards (see [`getPendingRewards()`](#getpendingrewards)).
     * @dev Emits a [`Claimed()`](#claimed) event on successful claim.
     * @param recipient The address to receive the claimed rewards. If `address(0)` is passed, it will be changed to `msg.sender`.
     */
    function claim(address recipient) external;

    /**
     * @notice Allows authorized addresses to borrow tokens from the pool.
     * @dev Requires `msg.sender` to have the `keccak256('BORROWER_ROLE')` role within the pool contract,
     *      otherwise, it throws an `"AccessControl: account msg.sender is missing role keccak256('BORROWER_ROLE')"` error.
     * @dev Throws a [`BorrowAmountExceedsBalance()`](#borrowamountexceedsbalance) error if the pool's token balance is less than the requested amount.
     * @dev Emits a [`Borrowed()`](#borrowed) event on successful borrow.
     * @param amount The amount of tokens to borrow.
     */
    function borrow(uint256 amount) external;

    /**
     * @notice Allows authorized addresses to deposit tokens into the liquidity pool, pay off as many debts as possible,
     * distribute rewards for the current epoch, and start a new epoch.
     * @dev Requires `msg.sender` to have the `keccak256('REPAYER_ROLE')` role within the pool contract;
     *      otherwise, it throws an `"AccessControl: account msg.sender is missing role keccak256('REPAYER_ROLE')"` error.
     * @dev Emits a [`Returned()`](#returned) event upon successful return.
     * @param amount Amount of tokens used for debt payment.
     * @param rewards Amount of tokens to distribute as rewards for the current epoch.
     */
    function returnAndDistribute(uint256 amount, uint256 rewards) external;

    /**
     * @notice Returns the token address associated with the pool.
     * @return Token address.
     */
    function token() external view returns (address);

    /**
     * @notice Returns the current threshold on the total deposit value. The pool's token balance cannot exceed this threshold.
     * @return Current threshold value.
     */
    function thresholdOnTotalDeposit() external view returns (uint256);

    /**
     * @notice Returns amount of tokens currently deposited into the pool.
     * A portion of that may be allocated as leverage onto margin accounts.
     * @return Amount of deposited tokens to the pool.
     */
    function totalDeposited() external view returns (uint256);

    /**
     * @notice Calculates the pending rewards that a user can claim.
     * @param user address for which to calculate rewards.
     * @return rewards amount of tokens that the user can claim right now.
     */
    function getPendingRewards(address user) external view returns (uint256 rewards);

    /**
     * @notice Retrieves the amount of tokens owned by a user in the pool.
     * @param user address.
     * @return balance amount of tokens.
     */
    function getPositionInfo(address user) external view returns (uint256 balance);
}
"
    },
    "contracts/libraries/Address.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.22;

library Address {
    address internal constant ETH = 0xEeeeeEeeeEeEeeEeEeEeeEEEeeeeEeeeeeeeEEeE;

    function set(bytes32 _slot, address _value) internal {
        assembly {
            sstore(_slot, _value)
        }
    }

    function get(bytes32 _slot) internal view returns (address result_) {
        assembly {
            result_ := sload(_slot)
        }
    }

    function isEth(address _token) internal pure returns (bool) {
        return _token == ETH || _token == address(0);
    }

    function sort(address _a, address _b) internal pure returns (address, address) {
        return _a < _b ? (_a, _b) : (_b, _a);
    }

    function sort(address[4] memory _array) internal pure returns (address[4] memory _sorted) {
        // Sorting network for the array of length 4
        (_sorted[0], _sorted[1]) = sort(_array[0], _array[1]);
        (_sorted[2], _sorted[3]) = sort(_array[2], _array[3]);

        (_sorted[0], _sorted[2]) = sort(_sorted[0], _sorted[2]);
        (_sorted[1], _sorted[3]) = sort(_sorted[1], _sorted[3]);
        (_sorted[1], _sorted[2]) = sort(_sorted[1], _sorted[2]);
    }
}
"
    },
    "contracts/libraries/DiamondLibrary.sol": {
