    "contracts/liquidityManagement/insuranceFund/fund/Fund.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.22;

import {Initializable} from "@openzeppelin/contracts/proxy/utils/Initializable.sol";
import {IERC20, SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import {AddressMustNotBeZero} from "contracts/interfaces/base/CommonErrors.sol";
import {IFund, IPool} from "contracts/interfaces/liquidityManagement/insuranceFund/IFund.sol";
import {
    IFundInitializer
} from "contracts/interfaces/liquidityManagement/insuranceFund/IFundInitializer.sol";

import {AccessControlDS} from "contracts/base/auth/AccessControlDS.sol";

import {FundDebt, MoneyAccountant} from "../libraries/MoneyAccountant.sol";

contract Fund is IFund, IFundInitializer, AccessControlDS, Initializable {
    using SafeERC20 for IERC20;

    struct Storage {
        IPool pool;
        IERC20 token;
    }

    bytes32 private constant STORAGE_SLOT = keccak256("Fund storage slot V1");

    address private immutable forwarder;

    bytes32 public constant DEBT_ADMIN_ROLE = keccak256("DEBT_ADMIN_ROLE");
    bytes32 public constant FORWARDER_ROLE = keccak256("FORWARDER_ROLE");

    modifier onlyInitialized() {
        if (Initializable._getInitializedVersion() < 1) revert IFundInitializer.NotInitialized();
        _;
    }

    constructor(address _forwarder) {
        if (_forwarder == address(0)) revert AddressMustNotBeZero();
        forwarder = _forwarder;
    }

    function initialize(
        address _liquidityPool,
        address debtAdmin
    ) external override reinitializer(2) {
        _grantRole(DEFAULT_ADMIN_ROLE, tx.origin);
        _grantRole(DEBT_ADMIN_ROLE, debtAdmin);
        _grantRole(FORWARDER_ROLE, forwarder);

        _storage().pool = IPool(_liquidityPool);
        _storage().token = IERC20(pool().token());

        if (token().allowance(address(this), _liquidityPool) == 0) {
            token().safeIncreaseAllowance(_liquidityPool, type(uint256).max);
        }
    }

    /// @inheritdoc IFund
    function supply(
        uint256 amount,
        address beneficiary
    ) external override onlyInitialized onlyRole(FORWARDER_ROLE) {
        token().safeTransferFrom(msg.sender, address(this), amount);
        MoneyAccountant.replenish(amount, beneficiary);

        emit Supplied(amount);
    }

    /// @inheritdoc IFund
    function forward(
        uint256 amount,
        uint256 reward
    ) external override onlyInitialized onlyRole(FORWARDER_ROLE) {
        (amount, reward) = MoneyAccountant.spendMoney(amount, reward);
        _forward(amount, reward);
    }

    /// @inheritdoc IFund
    function authorize(bytes32 id) external override onlyInitialized onlyRole(DEBT_ADMIN_ROLE) {
        FundDebt memory debt = MoneyAccountant.getDebt(id);

        if (debt.amount == 0 && debt.reward == 0) {
            revert DebtDoesNotExists(id);
        }
        if (MoneyAccountant.balance() < debt.amount + debt.reward) {
            revert DebtAmountExceedsBalance(id);
        }

        _forward(debt.amount, debt.reward);
        MoneyAccountant.deleteDebt(id);
        emit DebtPaid(id);
    }

    /// @inheritdoc IFund
    function withdrawAll() external {
        uint256 _balance = MoneyAccountant.balanceOf(msg.sender);
        if (_balance == 0) revert NothingToWithdraw();
        MoneyAccountant.deleteBalanceOf(msg.sender);
        token().safeTransfer(msg.sender, _balance);
    }

    /// @inheritdoc IFund
    function pool() public view returns (IPool) {
        return _storage().pool;
    }

    /// @inheritdoc IFund
    function token() public view returns (IERC20) {
        return _storage().token;
    }

    /// @inheritdoc IFund
    function balance() external view returns (uint) {
        return MoneyAccountant.balance();
    }

    /// @inheritdoc IFund
    function balanceOf(address account) external view returns (uint) {
        return MoneyAccountant.balanceOf(account);
    }

    /// @inheritdoc IFund
    function getDebt(bytes32 id) external view override returns (FundDebt memory) {
        return MoneyAccountant.getDebt(id);
    }

    function _forward(uint256 amount, uint256 reward) private {
        if (amount == 0 && reward == 0) return;

        pool().returnAndDistribute(amount, reward);
        emit Forwarded(amount);
    }

    function _storage() private pure returns (Storage storage s_) {
        bytes32 storageSlot = STORAGE_SLOT;
        assembly {
            s_.slot := storageSlot
        }
    }
}
"
    },
    "contracts/liquidityManagement/insuranceFund/libraries/DebtLibrary.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.22;

type DebtID is bytes32;

import {FundDebt} from "contracts/interfaces/liquidityManagement/insuranceFund/Debt.sol";

library DebtLibrary {
    error CannotCastToDebtStructDueToOverflow(uint256 from);

    function createDebt(
        uint256 amount,
        uint256 reward,
        uint256 salt
    ) internal pure returns (DebtID id, FundDebt memory self) {
        id = DebtID.wrap(keccak256(abi.encode(salt, amount, reward)));
        if (amount > type(uint128).max) {
            revert CannotCastToDebtStructDueToOverflow(amount);
        }
        if (reward > type(uint128).max) {
            revert CannotCastToDebtStructDueToOverflow(reward);
        }
        self = FundDebt(uint128(amount), uint128(reward));
    }
}
"
    },
    "contracts/liquidityManagement/insuranceFund/libraries/MoneyAccountant.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.22;

import {Math} from "@openzeppelin/contracts/utils/math/Math.sol";
import {DebtID, DebtLibrary, FundDebt} from "./DebtLibrary.sol";

library MoneyAccountant {
    struct Storage {
        uint256 nonce;
        uint256 balance;
        address beneficiary;
        mapping(DebtID => FundDebt) debts;
        mapping(address => uint) balanceOf;
    }

    bytes32 private constant STORAGE_SLOT = keccak256("MoneyAccountant storage slot V1");

    event NewDebt(DebtID);

    error BeneficiaryIsAlreadySet();

    function replenish(uint256 amount, address beneficiary) internal {
        accounting().balance += amount;
        if (beneficiary != address(0)) {
            if (accounting().beneficiary == address(0)) accounting().beneficiary = beneficiary;
            else revert BeneficiaryIsAlreadySet();
        }
    }

    function spendMoney(
        uint256 amount,
        uint256 reward
    ) internal returns (uint256 allowedAmount, uint256 allowedReward) {
        bool haveSurplus;
        (allowedAmount, allowedReward, haveSurplus) = calculateAcceptableExpenses(amount, reward);

        uint256 allowedTotal = allowedAmount + allowedReward;
        if (haveSurplus && accounting().beneficiary != address(0)) {
            uint256 surplus = accounting().balance - allowedTotal;
            accounting().balance = 0;
            accounting().balanceOf[accounting().beneficiary] += surplus;
        } else {
            accounting().balance -= allowedTotal;
            createDebtIfNecessary(amount - allowedAmount, reward - allowedReward);
        }
        accounting().beneficiary = address(0);

        return (allowedAmount, allowedReward);
    }

    function deleteDebt(bytes32 id) internal {
        delete accounting().debts[DebtID.wrap(id)];
    }

    function deleteBalanceOf(address account) internal {
        delete accounting().balanceOf[account];
    }

    function balance() internal view returns (uint256) {
        return accounting().balance;
    }

    function balanceOf(address account) internal view returns (uint256) {
        return accounting().balanceOf[account];
    }

    function getDebt(bytes32 id) internal view returns (FundDebt memory) {
        return accounting().debts[DebtID.wrap(id)];
    }

    function calculateAcceptableExpenses(
        uint256 wantAmount,
        uint256 wantReward
    ) internal view returns (uint256 allowedAmount, uint256 allowedReward, bool haveSurplus) {
        (bool ok, uint256 wantTotal) = Math.tryAdd(wantAmount, wantReward);
        if (!ok) wantTotal = type(uint256).max;

        if (accounting().balance >= wantTotal) return (wantAmount, wantReward, true);

        unchecked {
            /// @dev NOTE: if wantTotal == 0 then condition above is true
            allowedAmount = Math.mulDiv(accounting().balance, wantAmount, wantTotal);
            /// @dev NOTE: if surplus == 0 => allowedReward == 0
            allowedReward = accounting().balance - allowedAmount;
        }
    }

    function createDebtIfNecessary(uint256 differenceAmount, uint256 differenceReward) private {
        if (differenceAmount != 0 || differenceReward != 0) {
            (DebtID id, FundDebt memory debt) = DebtLibrary.createDebt(
                differenceAmount,
                differenceReward,
                accounting().nonce++
            );
            accounting().debts[id] = debt;
            emit NewDebt(id);
        }
    }

    function accounting() private pure returns (Storage storage s) {
        bytes32 slot = STORAGE_SLOT;
        assembly {
            s.slot := slot
        }
    }
}
