                mstore(0x00, 0xd1a57ed6) // `TransferToNonERC721ReceiverImplementer()`.
                revert(0x1c, 0x04)
            }
        }
    }

    function paused() public view override returns (bool) {
        return DN404Base(baseERC20()).paused();
    }
}
