    "contracts/MarketLauncher.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import '@openzeppelin/contracts/token/ERC20/IERC20.sol';
import '@uniswap/v2-core/contracts/interfaces/IUniswapV2Factory.sol';
import '@uniswap/v2-core/contracts/interfaces/IUniswapV2Pair.sol';
import '@uniswap/v2-periphery/contracts/interfaces/IUniswapV2Router02.sol';

contract MarketLauncher {
    IUniswapV2Router02 public immutable uniswapRouter;
    IUniswapV2Factory public immutable uniswapFactory;
    address public immutable wethAddress;

    constructor(address _uniswapRouter, address _wethAddress) {
        uniswapRouter = IUniswapV2Router02(_uniswapRouter);
        uniswapFactory = IUniswapV2Factory(uniswapRouter.factory());
        wethAddress = _wethAddress;
    }

    function createEthERC20PairAndSwap(
        address _tokenAddress,
        uint256 _tokenAmount,
        uint256 _ethAmount,
        uint256 _additionalEthAmount
    ) external payable {
        require(
            msg.value >= _ethAmount + _additionalEthAmount,
            'Wrong Eth Amount'
        );

        address pairAddress = uniswapFactory.getPair(
            _tokenAddress,
            wethAddress
        );

        require(pairAddress == address(0), 'Pair exists');

        pairAddress = uniswapFactory.createPair(_tokenAddress, wethAddress);

        require(pairAddress != address(0), 'Pair creation failed');

        IERC20 token = IERC20(_tokenAddress);
        require(
            token.transferFrom(msg.sender, address(this), _tokenAmount),
            'Token transfer failed'
        );

        token.approve(address(uniswapRouter), _tokenAmount);

        uniswapRouter.addLiquidityETH{value: _ethAmount}(
            _tokenAddress,
            _tokenAmount,
            0, // Min token amount
            0, // Min ETH amount
            msg.sender,
            block.timestamp
        );

        uint256 remainingToken = token.balanceOf(address(this));
        require(
            token.transfer(msg.sender, remainingToken),
            'Remaining token transfer failed'
        );

        if (_additionalEthAmount > 0) {
            address[] memory path = new address[](2);
            path[0] = wethAddress;
            path[1] = _tokenAddress;
            uniswapRouter.swapExactETHForTokensSupportingFeeOnTransferTokens{
                value: _additionalEthAmount
            }(0, path, msg.sender, block.timestamp);
        }
    }

    receive() external payable {}
}
