    "src/RewardPool.sol": {
      "content": "// SPDX-License-Identifier: Apache License 2.0
pragma solidity ^0.8.22;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { SignatureChecker } from "@openzeppelin/contracts/utils/cryptography/SignatureChecker.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";

/**
 * @title RewardPool
 * @dev A contract for creating reward _pools and claiming rewards from the pool
 * The creator of the pool can create a pool with a specific amount of tokens and an end time.
 * The users can claim tokens from the pool by providing a valid signature.
 */
contract RewardPool is OwnableUpgradeable, UUPSUpgradeable {
    event PoolCreated(bytes32 pid, address creator, uint256 amount, uint256 endTime);
    event Claimed(bytes32 pid, address user, uint256 amount);

    mapping(bytes32 => Pool) private _pools;
    // userClaimed is a mapping of user address to the amount of tokens claimed by the user
    mapping(address => mapping(bytes32 => uint256)) public userClaimed;
    address public signer;

    struct Pool {
        address creator;
        uint64 endTime;
        address token;
        uint96 totalAmount;
        uint96 claimed;
        uint96 claimLimit; // 0 means no limit, otherwise the limit of each user
        bool withdrawn;
    }

    // The initialize function will be used to set up the initial state of the contract.
    function initialize(address signer_) public initializer {
        __Ownable_init(msg.sender);
        __UUPSUpgradeable_init();
        signer = signer_;
    }

    function _authorizeUpgrade(address newImplementation) internal override onlyOwner { }

    function setSigner(address signer_) external onlyOwner {
        signer = signer_; // ignore the event
    }

    function getPool(bytes32 pid) external view returns (Pool memory) {
        require(_pools[pid].endTime > 0, "pool not found");
        return _pools[pid];
    }

    /**
     * @dev  Create a new pool
     */
    function createPool(bytes32 pid, IERC20 token, uint256 amount, uint256 claimLimit, uint256 endTime, bytes calldata signature) external {
        // Verify the signature
        bytes32 signatureHash = keccak256(abi.encode(block.chainid, address(this), pid, msg.sender, token, amount, claimLimit, endTime));
        require(SignatureChecker.isValidSignatureNow(signer, signatureHash, signature), "invalid signature");

        require(_pools[pid].endTime == 0, "pool already exists");
        require(endTime > block.timestamp + 1 hours, "invalid end time");
        require(amount > 0, "invalid amount");
        SafeERC20.safeTransferFrom(token, msg.sender, address(this), amount);

        _pools[pid] = Pool({
            creator: msg.sender,
            endTime: uint64(endTime),
            token: address(token),
            claimLimit: uint96(claimLimit),
            withdrawn: false,
            claimed: 0,
            totalAmount: uint96(amount)
        });
        emit PoolCreated(pid, msg.sender, amount, endTime);
    }

    /**
     * @dev  Claim tokens from the pool
     */
    function claim(bytes32 pid, uint256 amount, bytes calldata signature) external {
        // Verify the signature
        bytes32 signatureHash = keccak256(abi.encode(block.chainid, address(this), pid, msg.sender, amount));
        require(SignatureChecker.isValidSignatureNow(signer, signatureHash, signature), "invalid signature");

        // Check status of the pool
        Pool memory pool = _pools[pid];
        require(pool.endTime > block.timestamp, "time expired");
        require(pool.claimed + amount <= pool.totalAmount, "reward pool exhausted");
        require(userClaimed[msg.sender][pid] == 0, "already claimed");
        require(pool.claimLimit == 0 || amount <= pool.claimLimit, "exceed claim limit");

        // Update the pool and userClaimed
        unchecked {
            _pools[pid].claimed += uint96(amount);
            userClaimed[msg.sender][pid] += amount;
        }

        // Transfer the tokens
        SafeERC20.safeTransfer(IERC20(pool.token), msg.sender, amount);

        emit Claimed(pid, msg.sender, amount);
    }

    function withdraw(bytes32 pid) external {
        Pool memory pool = _pools[pid];
        require(pool.endTime <= block.timestamp, "not ended");
        require(pool.creator == msg.sender, "not creator");
        require(pool.claimed < pool.totalAmount, "all claimed");
        require(!pool.withdrawn, "already withdrawn");
        _pools[pid].withdrawn = true;
        SafeERC20.safeTransfer(IERC20(pool.token), msg.sender, pool.totalAmount - pool.claimed);
    }
}
"
