    "contracts/BaseRewards.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./interfaces/IBaseRewards.sol";
import "@openzeppelin/contracts/utils/math/SafeCast.sol";

abstract contract BaseRewards is IBaseRewards {
    using SafeCast for uint128;
    using SafeCast for uint256;
    using SafeCast for int256;

    uint128 public constant PRIZE_AMPLIFICATION_RATIO = type(uint128).max;

    uint256 public totalTokensStaked;
    uint256 public summedRewardPerShare;
    uint256 public cumulativeDistributedRewards;

    mapping(address => int256) public userPointsModification;
    mapping(address => uint256) public userPayoutRewards;
    mapping(address => uint256) public userDepositedStakes;

    function rewardsAvailableForRedemption(address account)
        public
        view
        override
        returns (uint256)
    {
        return rewardsAccumulationOf(account) - userPayoutRewards[account];
    }

    function rewardsAcquiredBy(address account)
        public
        view
        override
        returns (uint256)
    {
        return userPayoutRewards[account];
    }

    function rewardsAccumulationOf(address account)
        public
        view
        override
        returns (uint256)
    {
        return
            ((summedRewardPerShare * userDepositedStakes[account]).toInt256() +
                userPointsModification[account]).toUint256() /
            PRIZE_AMPLIFICATION_RATIO;
    }

    function rewardsDistribution(uint256 amountForDistribution) internal {
        if (totalTokensStaked == 0) {
            revert zeroShareSupply();
        }
        if (amountForDistribution > 0) {
            summedRewardPerShare =
                summedRewardPerShare +
                ((amountForDistribution * PRIZE_AMPLIFICATION_RATIO) /
                    totalTokensStaked);
        }
    }

    function rewardClaimPreparation(address rewardClaimBeneficiary)
        internal
        returns (uint256)
    {
        uint256 redeemableShare = rewardsAvailableForRedemption(
            rewardClaimBeneficiary
        );
        if (redeemableShare > 0) {
            userPayoutRewards[rewardClaimBeneficiary] =
                userPayoutRewards[rewardClaimBeneficiary] +
                redeemableShare;
        }
        return redeemableShare;
    }

    function rewardPointsAdjustment(address account, int256 adjustedShares)
        internal
    {
        userPointsModification[account] =
            userPointsModification[account] +
            (adjustedShares * int256(summedRewardPerShare));
    }
}
"
    },
    "contracts/interfaces/IBaseRewards.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

interface IBaseRewards {
    function rewardsAvailableForRedemption(address _account)
        external
        view
        returns (uint256);

    function rewardsAcquiredBy(address _account)
        external
        view
        returns (uint256);

    function rewardsAccumulationOf(address _account)
        external
        view
        returns (uint256);

    error zeroShareSupply();
}
"
    },
    "contracts/interfaces/IStaking.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

interface IStaking {
    function stake(uint256 amount) external;
    function unstake(uint256 id) external;

    error invalidId();
    error earlyWithdrawal();
    error zeroValue();
    error invalidFeeRate();
    error zeroAddress();
}
"
