    "contracts/NezhaStaking.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./BaseRewards.sol";
import "./interfaces/IStaking.sol";

contract NezhaStaking is IStaking, Ownable, ReentrancyGuard, BaseRewards {
    using SafeERC20 for IERC20;

    IERC20 public nezha;
    IERC20 public wTAO;
    uint256 constant percentageBaseFactor = 100;
    address public withdrawFeeRecipient;
    uint256 public withdrawFeeRatio;
    uint256 public lockTimeframe = 21 days;

    struct UserStakeInfo {
        uint256 amount;
        uint128 unstakeTime;
    }
    mapping(address => UserStakeInfo[]) private stakeInfo;

    constructor() Ownable(msg.sender) {
        withdrawFeeRecipient = msg.sender;
        withdrawFeeRatio = 5;
    }

    function stake(uint256 amount) external nonReentrant {
        if (amount == 0) {
            revert zeroValue();
        }
        nezha.safeTransferFrom(msg.sender, address(this), amount);
        stakeInfo[msg.sender].push(
            UserStakeInfo({
                amount: amount,
                unstakeTime: uint128(block.timestamp + lockTimeframe)
            })
        );
        userDepositedStakes[msg.sender] += amount;
        rewardPointsAdjustment(msg.sender, -int256(amount));
        totalTokensStaked += amount;
    }

    function unstake(uint256 id) external nonReentrant {
        if (id > stakeInfo[msg.sender].length) {
            revert invalidId();
        }
        uint256 unStakeAmount = stakeInfo[msg.sender][id].amount;
        userDepositedStakes[msg.sender] -= unStakeAmount;
        rewardPointsAdjustment(msg.sender, int256(unStakeAmount));
        totalTokensStaked -= unStakeAmount;

        if (block.timestamp < stakeInfo[msg.sender][id].unstakeTime) {
            uint256 fee = (unStakeAmount * withdrawFeeRatio) / percentageBaseFactor;
            nezha.transfer(withdrawFeeRecipient, fee);
            unStakeAmount -= fee;
        }
        nezha.safeTransfer(msg.sender, unStakeAmount);

        if (id < stakeInfo[msg.sender].length - 1) {
            stakeInfo[msg.sender][id] = stakeInfo[msg.sender][stakeInfo[msg.sender].length - 1];
        }
        stakeInfo[msg.sender].pop();
    }

    function setLockTimePeriod(uint256 duration) external onlyOwner {
        if (duration == 0) {
            revert zeroValue();
        }
        lockTimeframe = duration;
    }

    function setFeeReceiver(address newFeeReceiver) external onlyOwner {
        if (newFeeReceiver == address(0)) {
            revert zeroAddress();
        }
        withdrawFeeRecipient = newFeeReceiver;
    }

    function setWithdrawFeeRate(uint8 newWithdrawFeeRate) external onlyOwner {
        if (newWithdrawFeeRate > 10) {
            revert invalidFeeRate();
        }
        withdrawFeeRatio = newWithdrawFeeRate;
    }

    function allocateRewards(uint256 rewardPool) external  {
        if (rewardPool == 0) {
            revert zeroValue();
        }
        wTAO.safeTransferFrom(msg.sender, address(this), rewardPool);
        rewardsDistribution(rewardPool);
        cumulativeDistributedRewards += rewardPool;
    }

    function claimRewards() external {
        uint256 userReward = rewardClaimPreparation(msg.sender);

        if (userReward > 1) {
            wTAO.safeTransfer(msg.sender, userReward);
        }
    }

    function setTokens(address _nezha, address _wTAO) external onlyOwner {
        nezha = IERC20(_nezha);
        wTAO = IERC20(_wTAO);
    }

    function getUserStakes(address user)
        external
        view
        returns (UserStakeInfo[] memory)
    {
        return stakeInfo[user];
    }
}
"
