      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.24;

// Uncomment this line to use console.log
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract SWAPCONTRACT is Ownable {
    address public feeRecipient;
    address nativeToken = 0xEeeeeEeeeEeEeeEeEeEeeEEEeeeeEeeeeeeeEEeE;

    event FeeSent(uint256 indexed fee);
    event AmountSent(uint256 indexed amount);
    event feeRecipientSet(address indexed feeRecipient);

    constructor(address _owner, address payable _feeRecipient) Ownable(_owner) {
        feeRecipient = _feeRecipient;
    }

    function swap(
        IERC20 tokenIn,
        IERC20 tokenOut,
        address agregator,
        bytes calldata approvalData,
        bytes calldata swapData,
        uint256 amount
    ) external {
        // approve
        externalCall(address(tokenIn), approvalData);

        //swap
        externalCall(address(agregator), swapData);
        uint256 fee;
        if (address(tokenOut) == nativeToken) {
            //get balance
            uint256 balance = address(this).balance;

            if (balance > amount) {
                payable(msg.sender).transfer(amount);
                payable(feeRecipient).transfer(balance - amount);
                fee = balance - amount;
            } else if (balance == amount) {
                payable(msg.sender).transfer(amount);
            } else {
                // This should not reach
                revert("SWAPCONTRACT: Not enough swaped tokenOut amount");
            }
        } else {
            //get balance
            uint256 balance = tokenOut.balanceOf(address(this));

            if (balance > amount) {
                tokenOut.transfer(msg.sender, amount);
                tokenOut.transfer(feeRecipient, balance - amount);
                fee = balance - amount;
            } else if (balance == amount) {
                tokenOut.transfer(msg.sender, amount);
            } else {
                // This should not reach
                revert("SWAPCONTRACT: Not enough swaped tokenOut amount");
            }
        }

        emit FeeSent(fee);
        emit AmountSent(amount);
    }

    function externalCall(address target, bytes memory data) internal {
        (bool success, ) = payable(target).call(data);
        require(success, "SWAPCONTRACT: the external call failed");
    }

    function setFeeRecipient(address payable _feeRecipient) external onlyOwner {
        feeRecipient = _feeRecipient;
        emit feeRecipientSet(_feeRecipient);
    }

    receive() external payable {}
}
