    "contracts/interfaces/IEigenpieConfig.sol": {
      "content": "// SPDX-License-Identifier: GPL-3.0-or-later
pragma solidity 0.8.21;

interface IEigenpieConfig {
    // Errors
    error ValueAlreadyInUse();
