// File: contracts/MeltedDudz.sol



pragma solidity ^0.8.19;

interface Ddlz {

    function ownerOf(uint256 tokenId) external view returns (address owner);

    function tokensOfOwner(address owner) external view returns (uint256[] memory);
}

pragma solidity ^0.8.19;

interface Sparq {

    function ownerOf(uint256 tokenId) external view returns (address owner);

    function burnBatteryForAddress(uint256 tokenId) external;

    function batteryTypeIdOfTokenId(uint256 tokenId) external view returns (uint256);

}

pragma solidity ^0.8.19;







/*
