contract MeltedDudz is ERC721, ERC2981, Ownable, ReentrancyGuard {
    using Strings for uint256;

    Ddlz public immutable ddlz;
    Sparq public immutable sparq;

    bool public publicSaleActive;
    bool public myntxActive;
    bool public isDynamicCostActive = true;
    bool public revealed = false;

    mapping(uint256 => uint256) public magmaMeltationIdsByDudelz;
    mapping(uint256 => bool) public hasDudelzTokenIdUsedForMelting;

    string public baseTokenUri;
    string public notRevealedUri;

    uint8 public constant num_battery_types = 2;
    uint16 public currentMagmaMeltationId = 16665;
    uint16 public constant max_magma_meltation_id = 16673;
    uint256 public constant magma_battery_type = 3;
    uint256 public constant num_magma_melted_dudz = 9;
    uint256 public constant max_public_mint = 100;
    uint256 public constant max_publicsale_melteddudz = 5555;
    uint256 public constant meltation_offset = 5554;
    uint256 public staticCost;
    uint256 public numMeltedDudzMinted;
    uint256 private _totalSupply;

    event BatchMetadataUpdate(uint256 _fromTokenId, uint256 _toTokenId);

    modifier whenPublicSaleActive() {
        require(publicSaleActive, "Melted Dudz :: Public sale is not active");
        _;
    }

    modifier callerIsUser() {
        require(tx.origin == msg.sender, "Melted Dudz :: Cannot be called by a contract");
        _;
    }
 
    constructor(string memory name, string memory symbol, address ddlzAddress, address sparqAddress) ERC721(name, symbol) {
        ddlz = Ddlz(ddlzAddress);
        sparq = Sparq(sparqAddress);
    }

    function dynamicCost(uint256 _supply) internal pure returns (uint256 _ps_minted_price) {
        if (_supply < 300) {
            return 0.01 ether;
        }

        if (_supply < 800) {
            return 0.015 ether;
        }

        if (_supply < 1300) {
            return 0.02 ether;
        }

        if (_supply < 1800) {
            return 0.025 ether;
        }

        if (_supply < 2300) {
            return 0.03 ether;
        }

        if (_supply < 2800) {
            return 0.035 ether;
        }

        if (_supply < 3300) {
            return 0.04 ether;
        }

        if (_supply < 3800) {
            return 0.045 ether;
        }

        if (_supply < 4300) {
            return 0.05 ether;
        }

        if (_supply < 4800) {
            return 0.055 ether;
        }

        if (_supply <= max_publicsale_melteddudz) {
            return 0.06 ether;
        }
    }

    function getCost() public view returns (uint256) {
        if (isDynamicCostActive == true) {
           return dynamicCost(numMeltedDudzMinted);
        }
        return staticCost;
    }

    function mintMeltedDudz(uint256 numMeltedDudz) external payable whenPublicSaleActive nonReentrant callerIsUser {
        require(numMeltedDudzMinted + numMeltedDudz <= max_publicsale_melteddudz, "Melted Dudz :: Minting would exceed max supply");
        require(numMeltedDudz > 0, "Melted Dudz :: Must mint at least one Melted Dudz");
        require(numMeltedDudz <= max_public_mint, "Melted Dudz :: Requested number exceeds maximum");
        uint256 costToMint;

        if (isDynamicCostActive == true) {
           costToMint = dynamicCost(numMeltedDudzMinted) * numMeltedDudz;
        } else {
           costToMint = staticCost * numMeltedDudz;
        }
        require(msg.value >= costToMint, "Melted Dudz :: Ether value sent is not correct");

        for (uint256 i = 0; i < numMeltedDudz; ++i) {
            _safeMint(msg.sender, numMeltedDudzMinted);
            _totalSupply++;
            numMeltedDudzMinted++;
        }

        if (msg.value > costToMint) {
            Address.sendValue(payable(msg.sender), msg.value - costToMint);
        }
    }

    function meltDudelzWithMYNTX(uint256 sparqId, uint256 dudzId) external nonReentrant callerIsUser {
        require(myntxActive, "Melted Dudz :: MYNTX is not active");
        require(ddlz.ownerOf(dudzId) == msg.sender, "Melted Dudz :: Must own the Dudelz you're attempting to melt");
        require(sparq.ownerOf(sparqId) == msg.sender, "Melted Dudz :: You don't own the Sparq tokenId you have entered");
        require(hasDudelzTokenIdUsedForMelting[dudzId] == false, "Melted Dudz :: Dudelz id has been used for melting");
        uint256 batteryTypeId = sparq.batteryTypeIdOfTokenId(sparqId);

        uint256 meltationId;

        if (batteryTypeId == magma_battery_type) {
            require(
                currentMagmaMeltationId <= max_magma_meltation_id,
                "Melted Dudz :: Would exceed supply of Magma Melted Dudz"
            );
            require(
                magmaMeltationIdsByDudelz[dudzId] == 0,
                "Melted Dudz :: Dudelz already melted with Magma Battery"
            );

            meltationId = currentMagmaMeltationId;
            magmaMeltationIdsByDudelz[dudzId] = meltationId;
            currentMagmaMeltationId++;
        } else {
            meltationId = getMeltationId(batteryTypeId, dudzId);
            require(!_exists(meltationId), "Melted Dudz :: Dudelz already melted with this type of battery");
        }

        sparq.burnBatteryForAddress(sparqId);
        hasDudelzTokenIdUsedForMelting[dudzId] = true;
        _safeMint(msg.sender, meltationId);
        _totalSupply++;
    }

    function getMeltationIdForDudelzAndBatteryCombination(uint256 dudzId, uint256 sparqId) external view returns (uint256) {
        uint256 batteryTypeId = sparq.batteryTypeIdOfTokenId(sparqId);
        uint256 meltationId;
        if (batteryTypeId == magma_battery_type) {
            meltationId = magmaMeltationIdsByDudelz[dudzId];
            require(meltationId > 0, "Melted Dudz :: Invalid Magma Melted Dudz Id");
        } else {
        meltationId = getMeltationId(batteryTypeId, dudzId);
        }

        require(_exists(meltationId), "Melted Dudz :: Query for nonexistent Melted Dudz");

        return meltationId;
    }

    function hasDudelzBeenMeltedWithType(uint256 sparqId, uint256 dudzId) external view returns (bool) {
        uint256 batteryTypeId = sparq.batteryTypeIdOfTokenId(sparqId);
        if (batteryTypeId == magma_battery_type) {
            return magmaMeltationIdsByDudelz[dudzId] > 0;
        }
        uint256 meltationId = getMeltationId(batteryTypeId, dudzId);
        return _exists(meltationId);
    }

    function getMeltationId(uint256 batteryType, uint256 dudzId) internal pure returns (uint256) {
         require(
            batteryType != magma_battery_type,
            "Melted Dudz :: Super Melted ID can't be calculated"
        );
        return (dudzId * num_battery_types) + batteryType + meltation_offset;
    }

    function isMinted(uint256 tokenId) external view returns (bool) {
        require(
            tokenId < max_magma_meltation_id,
            "Melted Dudz :: tokenId outside collection bounds"
        );
        return _exists(tokenId);
    }

    function totalDudelzMelted() external view returns (uint256) {
        return totalSupply() - numMeltedDudzMinted;
    }

    function _baseURI() internal view virtual override returns (string memory) {
        return baseTokenUri;
    }

    //return uri for certain token
    function tokenURI(uint256 tokenId) public view virtual override returns (string memory) {
        require(_exists(tokenId), "ERC721Metadata: URI query for nonexistent token");

        if (revealed == false) {
           return notRevealedUri;
        }

        uint256 trueId = tokenId + 1;

        string memory baseURI = _baseURI();
        return bytes(baseURI).length > 0 ? string(abi.encodePacked(baseURI, trueId.toString(), ".json")) : "";
    }

    function teamMint(address _receiver, uint256 _mintAmount) external onlyOwner {
        require(_mintAmount > 0, "Mint amount must be greater than zero");
        require(numMeltedDudzMinted + _mintAmount <= max_publicsale_melteddudz, "Melted Dudz :: Cannot mint beyond max supply");
        for (uint256 i = 0; i < _mintAmount; ++i) {
            _safeMint(_receiver, numMeltedDudzMinted);
            _totalSupply++;
            numMeltedDudzMinted++;
        }
    }

    function setTokenUri(string memory _baseTokenUri) external onlyOwner{
        baseTokenUri = _baseTokenUri;
        emit BatchMetadataUpdate(0, 16673);
    }

    function setNotRevealedURI(string memory _notRevealedURI) external onlyOwner {
        notRevealedUri = _notRevealedURI;
        emit BatchMetadataUpdate(0, 16673);
    }

    function setStaticCost(uint256 _staticCost) external onlyOwner {
        staticCost = _staticCost;
    }

    function toggleIsDynamicCostActive() external onlyOwner {
        isDynamicCostActive = !isDynamicCostActive;
    }

    function togglePublicSaleActive() external onlyOwner {
        publicSaleActive = !publicSaleActive;
    }

    function toggleMYNTXActive() external onlyOwner {
        myntxActive = !myntxActive;
    }

    function toggleReveal() external onlyOwner {
        revealed = !revealed;
    }

    function totalSupply() public view returns (uint256) {
        return _totalSupply;
    }

    function toBeUsedDudelzIdsForMelting(address user) public view returns (uint256[] memory) {
        uint256[] memory ownedDudelzIdsByUser = ddlz.tokensOfOwner(user);
        require(ownedDudelzIdsByUser.length > 0, "User doesn't own any dudelz");
        uint256[] memory tokenIdsRemainingToBeUsed = new uint256[](ownedDudelzIdsByUser.length);
        uint256 counter = 0;
        for (uint256 i = 0; i < ownedDudelzIdsByUser.length; ++i) {
            if (hasDudelzTokenIdUsedForMelting[ownedDudelzIdsByUser[i]] == false) {
               tokenIdsRemainingToBeUsed[counter] = ownedDudelzIdsByUser[i];
               counter++;
            }
        }
        assembly{mstore(tokenIdsRemainingToBeUsed, counter)}
        return tokenIdsRemainingToBeUsed;
    }

    function supportsInterface(bytes4 interfaceId) public view virtual override (ERC721, ERC2981) returns (bool) {
        return interfaceId == 0x49064906 || // ERC165 interface ID for ERC4906
        super.supportsInterface(interfaceId);
    }

    function setDefaultRoyalty(address receiver, uint96 feeNumerator) external onlyOwner {
        _setDefaultRoyalty(receiver, feeNumerator);
    }

    function deleteDefaultRoyalty() external onlyOwner {
        _deleteDefaultRoyalty();
    }

    function setTokenRoyalty(uint256 tokenId, address receiver, uint96 feeNumerator) external onlyOwner {
        _setTokenRoyalty(tokenId, receiver, feeNumerator);
    }

    function resetTokenRoyalty(uint256 tokenId) external onlyOwner {
        _resetTokenRoyalty(tokenId);
    }

    function withdraw() public onlyOwner {
        uint256 balance = address(this).balance;
        // This will pay CM 2% of the initial sale.
        // =============================================================================
        Address.sendValue(payable(0xac84A8e795a642DD7F4550E873FB03Ec905C7172), (balance * 2) / 100);
        // =============================================================================

        // This will pay Dev Team 2% of the initial sale.
        // =============================================================================
        Address.sendValue(payable(0xdBE7272E85503a348fC0bC20885dF71e392bd7AD), (balance * 2) / 100);
        // =============================================================================

        // This will pay Lead Developer 15% of the initial sale.
        // =============================================================================
        Address.sendValue(payable(0x4c16b1A11B51E218673e9dF375AEC72DC63742bD), (balance * 15) / 100);
        // =============================================================================

        // This will pay Founder 35% of the initial sale.
        // =============================================================================
        Address.sendValue(payable(0x7C3e22190d11C6b34a6b6490f134BBc69da8f6Cc), (balance * 35) / 100);
        // =============================================================================

        // This will transfer the remaining contract balance to the owner.
        // Do not remove this otherwise you will not be able to withdraw the funds.
        // =============================================================================
        Address.sendValue(payable(msg.sender), address(this).balance);
        // =============================================================================
    }
}
