pragma solidity ^0.8.20;





contract Presale is ReentrancyGuard {
    using SafeMath for uint256;
    address public owner;
    IERC20  public  token;
    uint256 public rate;    // If rate = 100, then tokensPerETH is 100
    uint256 public tokensPerUSDT;
    bool public saleState; //sale state
    IERC20 USDT = IERC20(0xdAC17F958D2ee523a2206206994597C13D831ec7); // USDT address on Ethereum
    
    constructor() {
        owner = msg.sender;
    }

     modifier onlyOwner() {
        require(msg.sender == owner, "Only the owner can call this function.");
        _;
    }

    function setInitial(address _tokenAddress, uint256 _rate, uint256 _tokensPerUSDT) external onlyOwner() {
        token = IERC20(_tokenAddress);
        rate = _rate; 
        tokensPerUSDT = _tokensPerUSDT;
    }

    function updateRate(uint256 newRate, uint256 newTokensPerUSDT) external onlyOwner() {
        rate = newRate;
        tokensPerUSDT = newTokensPerUSDT;
    }

    function startPreSale() external onlyOwner() {
        saleState = true;
    }

    function endPreSale() external onlyOwner() {
        saleState = false;
    }

    receive() external payable {}

    function buyWithETH() external payable {
        require(saleState, "NOT_PRESALE_STATE");
        uint256 bnbAmountToBuy = msg.value;
          uint256 tokenAmount = bnbAmountToBuy.mul(rate);
        require(token.balanceOf(address(this)) >= tokenAmount, "INSUFFICIENT_BALANCE_IN_CONTRACT");
        (bool sent) = token.transfer(msg.sender, tokenAmount);
        require(sent, "FAILED_TO_TRANSFER_TOKENS_TO_BUYER");
        
    }

    function buyWithUSDT(uint256 _USDTAmount) external {
        require(saleState, "NOT_PRESALE_STATE");
        uint256 tokenAmount = _USDTAmount.mul(tokensPerUSDT * (10**18)).div(10**6);
        USDT.transferFrom(msg.sender, address(this), _USDTAmount);
        require(token.balanceOf(address(this)) >= tokenAmount, "INSUFFICIENT_BALANCE_IN_CONTRACT");
        (bool sent) = token.transfer(msg.sender, tokenAmount);
        require(sent, "FAILED_TO_TRANSFER_TOKENS_TO_BUYER");
    }

    function withdrawToken(address withdrawer) external onlyOwner() {
        uint256 tokenBalance = token.balanceOf(address(this));
        require(tokenBalance > 0, "No tokens to withdraw");
        bool success = token.transfer(withdrawer, tokenBalance);
        require(success, "Token transfer failed");
    }

    function withdrawBalance(address withdrawer) external  onlyOwner(){
        uint256 contractBalance = address(this).balance;
        require(contractBalance > 0, "No ETH balance to withdraw");
        (bool success, ) = payable(withdrawer).call{value: contractBalance}("");
        require(success, "ETH withdrawal failed");
    }

