        view
        returns (
            address elRewardsVault,
            address oracleReportSanityChecker,
            address stakingRouter,
            address treasury,
            address withdrawalQueue,
            address withdrawalVault
        );

    function oracleReportComponentsForLido()
        external
        view
        returns (
