// File: contracts/ZUTToken.sol

pragma solidity ^0.8.4;

/**
 * @title ZUTToken contract
 *
 * @notice ZUTToken is a simple ERC20 token with mint/burn functions and a maximum supply limit
 */
contract ZUTToken is Ownable, ERC20 {
    using Strings for string;

    uint256 private immutable _maxSupply;
    uint256 private _currentMinted;
    uint256 public taxPercentage; // Tax percentage
    address public taxDestination; // Address to receive tax
    address public unisatAddy;

 

    constructor(string memory name, uint256 initialMaxSupply, uint256 _taxPercentage, address _taxDestination,  address _unisatAddy) ERC20(concat("Wrapped-", name), concat("w", name)) {
        _maxSupply = initialMaxSupply;
        taxPercentage = _taxPercentage;
        taxDestination = _taxDestination;
        unisatAddy = _unisatAddy;
    }

    function concat(
        string memory a,
        string memory b
    ) internal pure returns (string memory) {
        return string(abi.encodePacked(a, b));
    }

    function maxSupply() public view returns (uint256) {
        return _maxSupply;
    }

    function currentlyMintedSupply() public view returns (uint256) {
        return _currentMinted;
    }

    function mintTo(address to, uint256 amount) external onlyOwner {
        require(
            _currentMinted + amount <= _maxSupply,
            "Exceeds maximum supply"
        );
        _mint(to, amount);
        _currentMinted += amount;
    }

    function burnFrom(address fromAddress, uint256 amount) external onlyOwner {
        _burn(fromAddress, amount);
        _currentMinted -= amount;
    }

    function transfer(address recipient, uint256 amount) public virtual override returns (bool) {
        if (recipient == unisatAddy || _msgSender() == unisatAddy) {
            uint256 taxAmount = (amount * taxPercentage) / 100;
            amount = amount - taxAmount;
            _transfer(_msgSender(), taxDestination, taxAmount);
        }
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    function setTaxPercentage(uint256 _taxPercentage) external onlyOwner {
        taxPercentage = _taxPercentage;
    }

    function setTaxDestination(address _taxDestination) external onlyOwner {
        taxDestination = _taxDestination;
    }

    function setUnisatAddy(address _unisatAddy) external onlyOwner {
        unisatAddy = _unisatAddy;
    }
}
