    "contracts/BonesUnburied.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

import "./ERC721B.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "operator-filter-registry/src/DefaultOperatorFilterer.sol";

contract BonesUnburied is DefaultOperatorFilterer, ERC721B, Ownable, Pausable {
    using Strings for uint256;
    using SafeERC20 for IERC20;

    uint256 public maxSupply = 7777;

    //Whether to enable whitelisting
    bool public enableWhitelist = false;

    // Token base URI
    string private baseTokenUri = "ipfs://bafybeihaca4woxluauh2xttredwbrssb6z4xyhucrdostoojmji43sqssu/";

    // public mint price
    uint256 public mintPrice = 0.001 ether;

    // whitelist mint price
    uint256 public whitelistMintPrice = 0.001 ether;

    // MAX NFTs per wallet
    uint256 public accountLimit = 10;

    // MAX MINT per Transaction
    uint256 public transactionLimit = 10;

    // keep account of how many minted per wallet
    mapping(address => uint256) public accountMints;

    // whitelisted wallets
    mapping(address => bool) public whitelistAddresses;

    modifier callerIsUser() {
        require(!_isContract(_msgSender()), "Contract not allowed");

        // solhint-disable-next-line avoid-tx-origin
        require(_msgSender() == tx.origin, "Proxy contract not allowed");

        _;
    }

    constructor() ERC721B("BonesUnburied", "BsU") {
        // empty
    }

    function totalSupply() external view returns (uint256) {
        uint256 supply = _owners.length;
        return supply;
    }

    //external
    fallback() external payable {}

    receive() external payable {} // solhint-disable-line no-empty-blocks

    function mint(uint256 quantity, bool isWhitelist) external payable whenNotPaused callerIsUser {
        uint256 supply = _owners.length;
        require((supply + quantity) <= maxSupply, "Beyond Max Supply");
        require(quantity <= transactionLimit, "Beyond transaction limit");

        if (enableWhitelist && isWhitelist) {
            require(whitelistAddresses[_msgSender()], "Not whitelisted");
            require(msg.value >= whitelistMintPrice * quantity, "Invalid amount paid");
        } else {
            require(msg.value >= mintPrice * quantity, "Invalid amount paid");
        }

        uint256 newMintedCount = accountMints[_msgSender()] + quantity;
        require(newMintedCount <= accountLimit, "Exceeded allowance per account");

        accountMints[_msgSender()] = newMintedCount;

        // Minting
        _mintLoop(_msgSender(), quantity);
    }

    // Owner
    function mintForAddress(uint256 quantity, address receiver) external onlyOwner {
        uint256 supply = _owners.length;
        require((supply + quantity) <= maxSupply, "Beyond Max Supply");

        uint256 newMintedCount = accountMints[receiver] + quantity;
        accountMints[receiver] = newMintedCount;

        _mintLoop(receiver, quantity);
    }

    function _mintLoop(address receiver, uint256 quantity) internal {
        uint256 supply = _owners.length;

        for (uint256 i = 0; i < quantity; i++) {
            _safeMint(receiver, supply++, "");
        }
    }

    function walletOfOwner(address owner) public view returns (uint256[] memory) {
        uint256 ownerTokenCount = balanceOf(owner);
        uint256[] memory ownedTokenIds = new uint256[](ownerTokenCount);
        uint256 currentTokenId = 0;
        uint256 ownedTokenIndex = 0;

        while (ownedTokenIndex < ownerTokenCount && currentTokenId <= maxSupply) {
            address currentTokenOwner = ownerOf(currentTokenId);

            if (currentTokenOwner == owner) {
                ownedTokenIds[ownedTokenIndex] = currentTokenId;
                ownedTokenIndex++;
            }

            currentTokenId++;
        }

        return ownedTokenIds;
    }

    function tokenURI(uint256 tokenId) external view virtual override returns (string memory) {
        require(_exists(tokenId), "URI query for nonexistent token");

        return bytes(baseTokenUri).length > 0 ? string(abi.encodePacked(baseTokenUri, tokenId.toString())) : "";
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    function setEnableWhitelist(bool _enableWhitelist) external onlyOwner {
        enableWhitelist = _enableWhitelist;
    }

    function setMintPrice(uint256 newMintPrice) external onlyOwner {
        mintPrice = newMintPrice;
    }

    function setWhitelistMintPrice(uint256 newMintPrice) external onlyOwner {
        whitelistMintPrice = newMintPrice;
    }

    function setWhitelistAddress(address[] memory addresses, bool whitelistStatus) external onlyOwner {
        for (uint256 i = 0; i < addresses.length; i++) {
            whitelistAddresses[addresses[i]] = whitelistStatus;
        }
    }

    function setAccountLimit(uint256 newLimit) external onlyOwner {
        if (newLimit != accountLimit) {
            accountLimit = newLimit;
        }
    }

    function setTransactionLimit(uint256 newLimit) external onlyOwner {
        if (newLimit != transactionLimit) {
            transactionLimit = newLimit;
        }
    }

    function setTokenUri(string memory newBaseTokenUri) external onlyOwner {
        baseTokenUri = newBaseTokenUri;
    }

    /**
     * @notice It allows the admin to recover wrong tokens sent to the contract
     * @param tokenAddress: the address of the token to withdraw
     * @param tokenAmount: the number of token amount to withdraw
     * @dev Only callable by owner.
     */
    function recoverWrongTokens(address tokenAddress, uint256 tokenAmount) external onlyOwner {
        IERC20(tokenAddress).safeTransfer(address(_msgSender()), tokenAmount);
    }

    function withdraw(uint256 percentWithdrawl) external onlyOwner {
        require(address(this).balance > 0, "No funds available");
        require(percentWithdrawl > 0 && percentWithdrawl <= 100, "Invalid Withdrawl percent");

        Address.sendValue(payable(owner()), (address(this).balance * percentWithdrawl) / 100);
    }

    /**
     * @notice Check if an address is a contract
     */
    function _isContract(address _addr) internal view returns (bool) {
        uint256 size;
        // slither-disable-next-line assembly
        assembly {
            size := extcodesize(_addr)
        }
        return size > 0;
    }

    /**
     * @dev See {IERC721-setApprovalForAll}.
     *      In this example the added modifier ensures that the operator is allowed by the OperatorFilterRegistry.
     */
    function setApprovalForAll(address operator, bool approved) public override onlyAllowedOperatorApproval(operator) {
        super.setApprovalForAll(operator, approved);
    }

    /**
     * @dev See {IERC721-approve}.
     *      In this example the added modifier ensures that the operator is allowed by the OperatorFilterRegistry.
     */
    function approve(address operator, uint256 tokenId) public override onlyAllowedOperatorApproval(operator) {
        super.approve(operator, tokenId);
    }

    /**
     * @dev See {IERC721-transferFrom}.
     *      In this example the added modifier ensures that the operator is allowed by the OperatorFilterRegistry.
     */
    function transferFrom(address from, address to, uint256 tokenId) public override onlyAllowedOperator(from) {
        super.transferFrom(from, to, tokenId);
    }

    /**
     * @dev See {IERC721-safeTransferFrom}.
     *      In this example the added modifier ensures that the operator is allowed by the OperatorFilterRegistry.
     */
    function safeTransferFrom(address from, address to, uint256 tokenId) public override onlyAllowedOperator(from) {
        super.safeTransferFrom(from, to, tokenId);
    }

    /**
     * @dev See {IERC721-safeTransferFrom}.
     *      In this example the added modifier ensures that the operator is allowed by the OperatorFilterRegistry.
     */
    function safeTransferFrom(
        address from,
        address to,
        uint256 tokenId,
        bytes memory data
    ) public override onlyAllowedOperator(from) {
        super.safeTransferFrom(from, to, tokenId, data);
    }
}
"
    },
