pragma solidity ^0.8.20;

contract TIME is ERC20, Ownable {
    using SafeMath for uint256;

    bool public noWhale = true;
    uint256 private _tTotal = 1000000000000 * 10 ** decimals(); // 1 trillion
    uint256 public max = 500000000 * 10 ** decimals();
    uint256 public initialMaxPercentage = 50; // Initial max buy amount 0.05% of total supply
    uint256 public increaseMultiplier = 200; // 1.2x increase multiplier (120%)
    uint256 public lastMaxUpdateTime;

    constructor(string memory name, string memory symbol) payable ERC20(name, symbol) Ownable(msg.sender) {
        super._update(address(0), msg.sender, _tTotal);
        lastMaxUpdateTime = block.timestamp;
    }

    function _update(address from, address to, uint256 amount) internal override virtual {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        require(amount > 0, "Transfer amount must be greater than zero");

        if (tx.origin != owner()) {
            // Anti-whale check
            if (noWhale == true) {
                // Update the max amount dynamically
                _updateMaxAmount();

                require(amount <= max, "Max amount of tokens in transaction exceeded");
            }
        }

        super._update(from, to, amount);
    }

    // Internal function to update the max amount dynamically every 15 minutes
    function _updateMaxAmount() internal {
        if (block.timestamp > lastMaxUpdateTime + 5 minutes) {
            uint256 intervalsElapsed = (block.timestamp - lastMaxUpdateTime) / 5 minutes;
            for (uint256 i = 0; i < intervalsElapsed; i++) {
                max += (max * increaseMultiplier) / 100;
            }
            lastMaxUpdateTime = block.timestamp;
        }
    }

    // Toggle the anti-whale mechanism
    function removeLimits() public onlyOwner {
        noWhale = !noWhale;
    }

    // Manually set the max limit (in units of tokens with decimals considered)
    function manualSetLimit(uint256 _max) public onlyOwner {
        max = _max * 10 ** decimals();
    }

    receive() external payable {}
}
