      "content": "// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.15;

import {IAddressScreen} from "./interfaces/IAddressScreen.sol";
import {ISanctionsList} from "./interfaces/ISanctionsList.sol";
import {EnumerableSet} from "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {Address} from "@openzeppelin/contracts/utils/Address.sol";

/**
 * @title AddressScreen
 * @dev Contract that manages address screening against an allow list, a deny list and an external sanctions oracle
 */
contract AddressScreen is IAddressScreen, Ownable {
  using EnumerableSet for EnumerableSet.AddressSet;
  using Address for address;

  /**
   * @dev External sanctions oracle
   */
  ISanctionsList public immutable sanctionsOracle;

  /**
   * @dev Set of addresses that are in the allow list
   */
  EnumerableSet.AddressSet internal allowList;

  /**
   * @dev Set of addresses that are in the deny list
   */
  EnumerableSet.AddressSet internal denyList;

  /**
   * @dev Emitted when a new address is added to the allow list
   */
  event AllowListAddressAdded(address indexed addr);

  /**
   * @dev Emitted when a new address is removed from allow list
   */
  event AllowListAddressRemoved(address indexed addr);

  /**
   * @dev Emitted when a new address is added to the deny list
   */
  event DenyListAddressAdded(address indexed addr);

  /**
   * @dev Emitted when a new address is removed from deny list
   */
  event DenyListAddressRemoved(address indexed addr);

  constructor(address owner_, address sanctionsOracleAddress_) {
    require(owner_ != address(0), "Owner cannot be address zero");
    require(sanctionsOracleAddress_.isContract(), "sanctionsOracleAddress is not a contract");
    sanctionsOracle = ISanctionsList(sanctionsOracleAddress_);
    _transferOwnership(owner_);
  }

  /**
   * @dev Add addresses to the allow list. Can emit one or more {AllowListAddressAdded}.
   * @param addrs addresses
   */
  function addAddressesToAllowList(address[] calldata addrs) external onlyOwner {
    for (uint256 i = 0; i < addrs.length; ) {
      addAddressToAllowList(addrs[i]);
      unchecked {
        i++;
      }
    }
  }

  /**
   * @dev Remove addresses from the allow list. Can emit one or more {AllowListAddressRemoved}.
   * @param addrs addresses
   */
  function removeAddressesFromAllowList(address[] calldata addrs) external onlyOwner {
    for (uint256 i = 0; i < addrs.length; ) {
      removeAddressFromAllowList(addrs[i]);
      unchecked {
        i++;
      }
    }
  }

  /**
   * @dev Add addresses to the deny list. Can emit one or more {DenyListAddressAdded}.
   * @param addrs addresses
   */
  function addAddressesToDenyList(address[] calldata addrs) external onlyOwner {
    for (uint256 i = 0; i < addrs.length; ) {
      addAddressToDenyList(addrs[i]);
      unchecked {
        i++;
      }
    }
  }

  /**
   * @dev Remove addresses from the deny list. Can emit one or more {DenyListAddressRemoved}.
   * @param addrs addresses
   */
  function removeAddressesFromDenyList(address[] calldata addrs) external onlyOwner {
    for (uint256 i = 0; i < addrs.length; ) {
      removeAddressFromDenyList(addrs[i]);
      unchecked {
        i++;
      }
    }
  }

  /**
   * @dev Return true if the address is allow listed, false otherwise
   * @param addr address
   */
  function isAllowListed(address addr) external view returns (bool) {
    return allowList.contains(addr);
  }

  /**
   * @dev Return true if the address is deny listed (including sanctioned), false otherwise
   * @param addr address
   */
  function isDenyListed(address addr) external view returns (bool) {
    return (denyList.contains(addr) || sanctionsOracle.isSanctioned(addr));
  }

