contract BattleRevenewShare is ReentrancyGuard, Ownable {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;
    using Counters for Counters.Counter;

    Counters.Counter private _stakerCount;

    struct BattleHolder {
        uint256 index;
        bool autoStaked;
        uint256 amount;
        uint256 claimed;
        uint256 timestamp;
    }

    IERC20 private token = IERC20(0x63F314C44D164D5Be423199AFBfbe129D72d3EA6);

    uint256 private   _totalShareHolderBalance;
    uint256 private   _totalStakerRewards;
    uint256 private   _totalRewardsApplied;
    uint256 private   _totalDistribution;
    uint256 private   _minTokensAmount;
    uint256[] public  _indexes;

    mapping(address => BattleHolder) private battleHolder;
    mapping(uint256 => address) private _indexedAddress;


    event Staked(address indexed user, uint256 index, uint256 amount);
    event Unstaked(address indexed user, uint256 index, uint256 amount);

    constructor()  {
        transferOwnership(msg.sender);
    }

    modifier allowedTokenHolder() {
        require(token.balanceOf(msg.sender) > _minTokensAmount, "ERROR: Not enough tokens!");
           _;
    }

    modifier isBattleHolder() {
        require(battleHolder[msg.sender].autoStaked, "ERROR: Not a battleHolder!");
           _;
    }

    function getPoolBalance() external  view returns (uint256) {
        return token.balanceOf(address(this));
    }

    function getBattleHolder(address owner) public view returns(BattleHolder memory) {
        return battleHolder[owner];
    }

    function getIndexes() public view returns(uint256[] memory) {
        return _indexes;
    }

    function getIndexedAddress(uint256 index) public view returns(address){ 
        return _indexedAddress[index];
    }

    function getIndexesLength() public view returns(uint256) {
        return _indexes.length;
    }
    
    function getTotalRewards() public view returns(uint256) {
        return _totalStakerRewards;
    }

    function getCurrentRewards() public view returns(uint256) {
        return _totalRewardsApplied;
    }

    function getCurrenttDistribution() public view returns(uint256) {
        return _totalDistribution;
    }

    function isRegistered(address owner) public view returns(bool) {
        return battleHolder[owner].autoStaked;
    }

    function getClaimed(address owner) external view returns(uint256){
        return battleHolder[owner].claimed;
    }

    function getStakedAmount(address owner) external view returns(uint256) {
        return battleHolder[owner].amount;
    }

    function getStakersDuration(address owner) external view returns(uint) {
        uint stakerTimestamp = battleHolder[owner].timestamp;
        uint currentTime = block.timestamp;

        if (stakerTimestamp + 1 days <= currentTime) {
            return 0;
        } else {
            return stakerTimestamp + 1 days - currentTime;
        }
    }

    function getTotalStakersCount() external  view returns(uint256) {
        return _stakerCount.current();
    }
    
    function getTotalStaked() public view returns(uint256) {
        return _totalShareHolderBalance;
    }

    function getOwnersShares(address owner) public view returns(uint256) {
          return fromX(battleHolder[owner].amount, _totalShareHolderBalance, toWei(100));
    }

    function getSharesFromAmount(uint256 _amount, address owner) public view returns(uint256) {
          uint256 sharePercetage =  fromX(battleHolder[owner].amount, _totalShareHolderBalance, toWei(100));
          return  _amount * sharePercetage / toWei(100);
    }

    function getShareAmount(uint256 _amount, uint256 percetage, uint256 scale) public pure returns(uint256) {
        return _amount * percetage / scale;
    }

    function getOwenersPoolPercentage(address owner) public view returns(uint256) {
          return fromX(battleHolder[owner].amount, _totalShareHolderBalance, toWei(100));
    }

    function setMinForRewards(uint256 _amount) external onlyOwner {
        _minTokensAmount = _amount;
    }

    function claimRewards() isBattleHolder() nonReentrant external  {
        uint256 _amount = _totalDistribution;
        require(_amount > 0, "ERROR: NO REWARDS TOO DISTRBUTE!");
        require(token.balanceOf(address(this)) >= _amount, "ERROR: NO REWARDS TOO DISTRBUTE!");

        BattleHolder storage autoSakerX = battleHolder[msg.sender];
        uint256 balance = token.balanceOf(msg.sender);        
        require(autoSakerX.autoStaked == true, "ERROR: NOT AUTOSTAKED!");
        require(block.timestamp >= autoSakerX.timestamp + 1 days, "ERROR: MAX CLAIM EVERY 24h");
        
        if(autoSakerX.amount > balance) {
            _totalShareHolderBalance = _totalShareHolderBalance.sub(autoSakerX.amount);
            _totalShareHolderBalance = _totalShareHolderBalance.add(balance);
             autoSakerX.amount = balance;
        }
        
        if(autoSakerX.amount >= _minTokensAmount && autoSakerX.autoStaked) {
            uint256 sharePercetage =  fromX(autoSakerX.amount, _totalShareHolderBalance, toWei(100));
            uint256 computedAmount =  _amount * sharePercetage / toWei(100);
                if(_totalDistribution >= computedAmount) {
                    token.transfer(msg.sender, computedAmount);
                    autoSakerX.amount = balance;
                    autoSakerX.autoStaked = false;
                    autoSakerX.claimed = autoSakerX.claimed.add(computedAmount);
                    _totalDistribution = _totalDistribution.sub(computedAmount);
                } else return;
        } else revert();
    }

    function applyRevenewHolders(address[] memory _owners) external onlyOwner() {

        for(uint256 i = 0; i < _owners.length; i++) {
            BattleHolder storage _autoStaker = battleHolder[_owners[i]];           
            uint256 _amount = token.balanceOf(_owners[i]);

            if(_amount >= _minTokensAmount) {
                uint256 current = _stakerCount.current();

                if(_autoStaker.index == 0) {
                        _stakerCount.increment();
                        current = _stakerCount.current(); 
                        _autoStaker.index = current; 
                        _indexes.push(current); 
                        _indexedAddress[current] = _owners[i]; 

                } else {
                    _autoStaker.index = _autoStaker.index;
                }

                _autoStaker.autoStaked = true;
                _autoStaker.amount = _amount;
                _autoStaker.timestamp = block.timestamp;
                _autoStaker.claimed = _autoStaker.claimed;
                _totalShareHolderBalance = _totalShareHolderBalance.add(_amount);

            } else {
                _autoStaker.autoStaked = false;
                _autoStaker.amount = 0;
                _autoStaker.timestamp = block.timestamp;
                _autoStaker.claimed = _autoStaker.claimed;
            }
        }
    }

    function importTokens(uint256 amount) external nonReentrant onlyOwner {
        uint256 _amount = token.allowance(msg.sender, address(this));
        require(_amount >= amount, "ERROR: NO ALLOWANCE");
        require(token.balanceOf(msg.sender) >= amount, "ERROR: NO ALLOWANCE");

        token.transferFrom(msg.sender, address(this), amount);
        _totalStakerRewards = _totalStakerRewards.add(amount);
        _totalRewardsApplied = _totalDistribution.add(amount);
        _totalDistribution = _totalDistribution.add(amount);
    }

    function fromX(uint256 a, uint256 b, uint256 scale) internal  pure returns(uint256) {
        return (scale * a) / b;
    }

    function toWei(uint value) internal pure returns (uint) {
       return value*(10**18);
    }

    function percentagecal(uint256 a, uint256 b, uint256 scale) private pure returns(uint256) {
        return (scale - a) / b;
    }

    function clearCompundBalance() external onlyOwner {
        _totalShareHolderBalance = 0;
    }
    
    function withdrawStuckTokens(address _address) payable external nonReentrant onlyOwner {
        IERC20(_address).transfer(owner(),  IERC20(_address).balanceOf(address(this)));
        _totalDistribution = 0;
    }

    function withdraw() payable external nonReentrant onlyOwner {
        uint256 balance = address(this).balance;
        payable(address(owner())).transfer(balance);
    }

    
    receive() external payable {
    }

}
