    "contracts/StakeERC721.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC721/utils/ERC721Holder.sol";

contract StakeERC721 is ERC721Holder, Ownable(msg.sender), AccessControl{
    event StakeEvent (address tokenOwner, address tokenAddress, uint tokenID, uint stakeDate, uint period, uint targetEndDate);
    event WithdrawEvent (address tokenOwner, address tokenAddress, uint tokenID, uint withdrawDate);
    event ChangeEndDateEvent(address tokenOwner, address tokenAddress, uint tokenID, uint targetEndDate);
    struct StakeRecord {
        uint period; // in days
        address tokenAddress;
        uint tokenID;
        uint stakeStartDate;
        address owner;
        uint targetWithdrawDate;
        uint withdrawDate;
        bool isExist;
    }
    mapping (address => StakeRecord) public userStakeRecord;
    address public tokenAddress;
    mapping(uint => bool) public periodMap; // in days
    bool public adminEnable;

    constructor (address _tokenAddress) {
      tokenAddress = _tokenAddress;
      periodMap[30] = true;
      periodMap[90] = true;
      periodMap[180] = true;
      periodMap[360] = true;
      adminEnable = false;
    }

    function stakeERC721 (uint _period, uint _tokenID) external {
        require(tokenAddress != address(0), "ERC721 contract address it not set");
        require(periodMap[_period], "This period is not allowed");
        require(!userStakeRecord[msg.sender].isExist, "You have already staked ERC721");

        IERC721 tokenContract = IERC721(tokenAddress);

        require(tokenContract.ownerOf(_tokenID) == msg.sender, "This token is not owned by you");
        require(tokenContract.getApproved(_tokenID) == address(this), "The owner hasn't approved to use token");

        userStakeRecord[msg.sender] = StakeRecord({
            period: _period,
            tokenAddress: tokenAddress,
            tokenID: _tokenID,
            stakeStartDate: block.timestamp,
            owner: msg.sender,
            targetWithdrawDate: block.timestamp + (_period * 86400),
            withdrawDate: 0,
            isExist: true
        });

        tokenContract.safeTransferFrom(msg.sender, address(this), _tokenID);

        emit StakeEvent(msg.sender, tokenAddress, _tokenID, block.timestamp, _period, block.timestamp + (_period * 86400));
    }

    function withdrawStakeRecord() external {
        require(userStakeRecord[msg.sender].isExist, "You haven't stake any ERC721 token");
        require(userStakeRecord[msg.sender].targetWithdrawDate <= block.timestamp, "You cannot get back the ERC721 token yet");

        IERC721 tokenContract = IERC721(userStakeRecord[msg.sender].tokenAddress);
        userStakeRecord[msg.sender].isExist = false;
        tokenContract.safeTransferFrom(address(this), msg.sender, userStakeRecord[msg.sender].tokenID);
