pragma solidity ^0.8.17;

import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Royalty.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";
import "@ensdomains/ens-contracts/contracts/resolvers/Resolver.sol";
import "@ensdomains/ens-contracts/contracts/registry/ENS.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";

interface NameWrapper {
    function ownerOf(uint256 id) external view returns (address);
}

contract AGIGirlfriendv0 is ERC721URIStorage, ERC721Royalty, Ownable, ReentrancyGuard, Pausable {
    using Counters for Counters.Counter;
    using EnumerableSet for EnumerableSet.AddressSet;
    using Strings for uint256;
    using Address for address;
    using MerkleProof for bytes32[];
    using ECDSA for bytes32;

    Counters.Counter private _tokenIdCounter;
    IERC20 public agiToken;
    ENS public ens;
    NameWrapper public nameWrapper;
    bytes32 public rootNode;
    string private _baseTokenURI;
    string private _contractURI;
    uint256 public basePrice = 888 * 10**18;
    uint256 public maxMintAmount = 8;
    uint256 public maxDiscountCap = 88;
    bytes32 public merkleRoot;
    mapping(uint256 => string) private _tokenURIs;

    uint256 public maxLinkedWallets = 12;
    mapping(address => address[]) public linkedWallets;

    event WalletLinkCreated(address indexed primary, address indexed secondary);
    event WalletLinkRemoved(address indexed primary, address indexed secondary);

    struct DiscountRule {
        EnumerableSet.AddressSet qualifyingNFTs;
        uint256 discountPercentage;
        bool isCompound;
    }

    mapping(uint256 => DiscountRule) private discountRules;
    uint256 public ruleCount;

    event MetadataUpdated(uint256 indexed tokenId, string uri);
    event ContractURIUpdated(string newContractURI);
    event AGITokenUpdated(address newAGITokenAddress);
    event OwnershipVerified(address claimant, string subdomain);
    event ResolverChecked(bytes32 indexed node, address resolver);
    event RecoveryInitiated(string action);
    event ENSUpdated(address indexed newENS);
    event RootNodeUpdated(bytes32 indexed newRootNode);
    event ResolverUpdated(bytes32 indexed node, address newResolver);
    event DiscountNFTUpdated(address indexed newDiscountNFT);
    event BasePriceUpdated(uint256 newBasePrice);
    event RoyaltyInfoUpdated(address recipient, uint96 feeBasisPoints);
    event DiscountRuleAdded(uint256 indexed ruleId, uint256 discountPercentage, bool isCompound);
    event DiscountRuleUpdated(uint256 indexed ruleId, uint256 discountPercentage);
    event NFTAddedToRule(uint256 indexed ruleId, address indexed nftAddress);
    event NFTRemovedFromRule(uint256 indexed ruleId, address indexed nftAddress);
    event NameWrapperAddressUpdated(NameWrapper oldAddress, NameWrapper newAddress);
    event MerkleRootUpdated(bytes32 newMerkleRoot);

    struct Listing {
        uint256 price;
        address seller;
        uint256 expirationTime;
    }

    mapping(uint256 => Listing) private listings;
    Counters.Counter private _listingIdTracker;

    event Listed(uint256 indexed tokenId, uint256 price, address indexed seller, uint256 expirationTime);
    event Delisted(uint256 indexed tokenId);
    event Purchased(uint256 indexed tokenId, uint256 price, address indexed buyer, address indexed seller);

    constructor(ENS _ens, NameWrapper _nameWrapper, bytes32 _rootNode, string memory baseTokenURI, address _agiTokenAddress) 
        ERC721("AGI Girlfriend v0", "AGIGirlfriendv0") 
    {
        ens = _ens;
        nameWrapper = _nameWrapper;
        rootNode = _rootNode;
        _baseTokenURI = baseTokenURI;
        agiToken = IERC20(_agiTokenAddress);
        _setDefaultRoyalty(owner(), 1000);
    }

    // Signature verification and wallet linking methods

    function getMessageToSign(address primary, address secondary) public view returns (bytes32) {
        bytes32 domainSeparator = keccak256(
            abi.encode(
                keccak256("EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)"),
                keccak256(bytes("AGIGirlfriendv0")),
                keccak256(bytes("1")),
                block.chainid,
                address(this)
            )
        );

        bytes32 structHash = keccak256(
            abi.encode(
                keccak256("LinkRequest(address primary,address secondary)"),
                primary,
                secondary
            )
        );

        return keccak256(abi.encodePacked("\x19\x01", domainSeparator, structHash));
    }

    function linkWallet(address primary, address secondary, bytes memory signature) public {
        require(primary != address(0) && secondary != address(0), "Invalid address");
        require(linkedWallets[primary].length < maxLinkedWallets, "Too many linked wallets");
        bytes32 message = getMessageToSign(primary, secondary);
        bytes32 digest = ECDSA.toEthSignedMessageHash(message);
        require(digest.recover(signature) == secondary, "Invalid signature");

        linkedWallets[primary].push(secondary);
        emit WalletLinkCreated(primary, secondary);
    }

    function unlinkWallet(address primary, address secondary) public {
        require(msg.sender == primary, "Only primary can unlink");
        require(secondary != address(0), "Invalid address");

        int256 index = findWalletIndex(primary, secondary);
        require(index >= 0, "Wallet not linked");

        removeWalletByIndex(primary, uint256(index));
        emit WalletLinkRemoved(primary, secondary);
    }

    function findWalletIndex(address primary, address secondary) internal view returns (int256) {
        address[] memory links = linkedWallets[primary];
        for (uint256 i = 0; i < links.length; i++) {
            if (links[i] == secondary) {
                return int256(i);
            }
        }
        return -1;
    }

    function removeWalletByIndex(address primary, uint256 index) internal {
        address[] storage links = linkedWallets[primary];
        links[index] = links[links.length - 1];
        links.pop();
    }

    function setNameWrapperAddress(NameWrapper _nameWrapper) public onlyOwner {
        require(address(_nameWrapper) != address(0), "Invalid NameWrapper address");
        emit NameWrapperAddressUpdated(nameWrapper, _nameWrapper);
        nameWrapper = _nameWrapper;
    }

    function setMerkleRoot(bytes32 _merkleRoot) public onlyOwner {
        merkleRoot = _merkleRoot;
        emit MerkleRootUpdated(_merkleRoot);
    }

    function updateENSAddress(address newENS) public onlyOwner {
        require(newENS != address(0), "ENS address cannot be the zero address");
        require(Address.isContract(newENS), "Provided address must be a deployed contract");

        ens = ENS(newENS);
        emit ENSUpdated(newENS);
    }

    function setMaxDiscountCap(uint256 newCap) public onlyOwner {
        require(newCap <= 100, "Cap must be 100% or less");
        maxDiscountCap = newCap;
    }

    function addDiscountRule(uint256 discountPercentage, bool isCompound) public onlyOwner {
        ruleCount += 1;
        DiscountRule storage rule = discountRules[ruleCount];
        rule.discountPercentage = discountPercentage;
        rule.isCompound = isCompound;
        emit DiscountRuleAdded(ruleCount, discountPercentage, isCompound);
    }

    function addNFTToRule(uint256 ruleId, address nftAddress) public onlyOwner {
        require(nftAddress != address(0) && nftAddress.isContract(), "Invalid NFT address");
        require(ruleId > 0 && ruleId <= ruleCount, "Invalid rule ID");
        discountRules[ruleId].qualifyingNFTs.add(nftAddress);
        emit NFTAddedToRule(ruleId, nftAddress);
    }

    function removeNFTFromRule(uint256 ruleId, address nftAddress) public onlyOwner {
        require(ruleId > 0 && ruleId <= ruleCount, "Invalid rule ID");
        discountRules[ruleId].qualifyingNFTs.remove(nftAddress);
        emit NFTRemovedFromRule(ruleId, nftAddress);
    }

    function updateDiscountPercentage(uint256 ruleId, uint256 newPercentage) public onlyOwner {
        require(ruleId > 0 && ruleId <= ruleCount, "Invalid rule ID");
        discountRules[ruleId].discountPercentage = newPercentage;
        emit DiscountRuleUpdated(ruleId, newPercentage);
    }

    function getDiscountPercentage(uint256 ruleId) public view returns (uint256) {
        require(ruleId <= ruleCount, "Invalid rule ID");
        return discountRules[ruleId].discountPercentage;
    }

    function getQualifyingNFTs(uint256 ruleId) public view returns (address[] memory) {
        require(ruleId <= ruleCount, "Invalid rule ID");
        EnumerableSet.AddressSet storage set = discountRules[ruleId].qualifyingNFTs;
        address[] memory addresses = new address[](set.length());
        for (uint256 i = 0; i < set.length(); i++) {
            addresses[i] = set.at(i);
        }
        return addresses;
    }

    function batchUpdateTokenURIs(uint256 startTokenId, uint256 endTokenId, string memory newBaseURI) public onlyOwner {
        require(startTokenId > 0 && endTokenId >= startTokenId, "Invalid token ID range");
        require(endTokenId <= _tokenIdCounter.current(), "End token ID exceeds current maximum");

        for (uint256 i = startTokenId; i <= endTokenId; i++) {
            _setTokenURI(i, newBaseURI);
        }

        emit MetadataUpdated(0, newBaseURI); // Using 0 to indicate a bulk update
    }

    function _verifyOwnership(address claimant, string memory subdomain, bytes32[] calldata proof) internal returns (bool) {
        bytes32 leaf = keccak256(abi.encodePacked(claimant));
        if (proof.verify(merkleRoot, leaf)) {
            emit OwnershipVerified(claimant, subdomain);
            return true;
        }

        bytes32 subnode = keccak256(abi.encodePacked(rootNode, keccak256(bytes(subdomain))));
        try nameWrapper.ownerOf(uint256(subnode)) returns (address actualOwner) {
            if (actualOwner == claimant) {
                emit OwnershipVerified(claimant, subdomain);
                return true;
            }
        } catch Error(string memory reason) {
            emit RecoveryInitiated(reason);
        } catch {
            emit RecoveryInitiated("NameWrapper call failed without a specified reason.");
        }

        address resolverAddress = ens.resolver(subnode);
        if (resolverAddress != address(0)) {
            Resolver resolver = Resolver(resolverAddress);
            try resolver.addr(subnode) returns (address payable resolvedAddress) {
                if (resolvedAddress == claimant) {
                    emit OwnershipVerified(claimant, subdomain);
                    return true;
                }
            } catch {
                emit RecoveryInitiated("Resolver call failed without a specified reason.");
            }
        } else {
            emit RecoveryInitiated("Resolver address not found for node.");
        }

        return false;
    }

    function mint(string memory subdomain, uint256 amount, bytes32[] calldata proof) external whenNotPaused nonReentrant {
        require(amount > 0 && amount <= maxMintAmount, "Invalid mint amount");
        require(_verifyOwnership(msg.sender, subdomain, proof), "Unauthorized subdomain or not whitelisted");

        uint256 price = calculatePrice(msg.sender);
        require(agiToken.transferFrom(msg.sender, address(this), price * amount), "Payment failed");

        for (uint256 i = 0; i < amount; i++) {
            _tokenIdCounter.increment();
            uint256 tokenId = _tokenIdCounter.current();
            _safeMint(msg.sender, tokenId);
            string memory uri = bytes(_tokenURIs[tokenId]).length > 0 ? _tokenURIs[tokenId] : _baseTokenURI;
            _setTokenURI(tokenId, uri);
        }
    }

    function endsWith(string memory fullString, string memory ending) private pure returns (bool) {
        bytes memory fullStrBytes = bytes(fullString);
        bytes memory endingBytes = bytes(ending);
        if (endingBytes.length > fullStrBytes.length) {
            return false;
        }
        for (uint i = fullStrBytes.length - endingBytes.length; i < fullStrBytes.length; i++) {
            if (fullStrBytes[i] != endingBytes[i - fullStrBytes.length + endingBytes.length]) {
                return false;
            }
        }
        return true;
    }

    function countDots(string memory str) private pure returns (uint256) {
        bytes memory strBytes = bytes(str);
        uint256 count = 0;
        for (uint256 i = 0; i < strBytes.length; i++) {
            if (strBytes[i] == '.') {
                count++;
            }
        }
        return count;
    }

    function substring(string memory str, uint startIndex, uint endIndex) private pure returns (string memory) {
        bytes memory strBytes = bytes(str);
        bytes memory result = new bytes(endIndex - startIndex + 1);
        for (uint i = startIndex; i <= endIndex; i++) {
            result[i - startIndex] = strBytes[i];
        }
        return string(result);
    }

    function setAGITokenAddress(address _newAGIToken) public onlyOwner {
        require(_newAGIToken != address(0), "Invalid AGI token address");
        agiToken = IERC20(_newAGIToken);
        emit AGITokenUpdated(_newAGIToken);
    }

    function setBaseTokenURI(string memory uri) public onlyOwner {
        _baseTokenURI = uri;
        emit MetadataUpdated(0, uri); // Use 0 as tokenId for a base URI update
    }

    function setContractURI(string memory newContractURI) public onlyOwner {
        _contractURI = newContractURI;
        emit ContractURIUpdated(newContractURI);
    }

    function contractURI() public view returns (string memory) {
        return _contractURI;
    }

    function setTokenURI(uint256 tokenId, string memory _tokenURI) public onlyOwner {
        require(_exists(tokenId), "ERC721Metadata: URI set of nonexistent token");
        _tokenURIs[tokenId] = _tokenURI;
        emit MetadataUpdated(tokenId, _tokenURI);
    }

    function tokenURI(uint256 tokenId) public view override(ERC721, ERC721URIStorage) returns (string memory) {
        string memory _tokenURI = _tokenURIs[tokenId];
        return bytes(_tokenURI).length > 0 ? _tokenURI : super.tokenURI(tokenId);
    }

    function updateTokenURI(uint256 tokenId, string memory newTokenURI) public onlyOwner {
        require(_exists(tokenId), "ERC721Metadata: URI set of nonexistent token");
        _tokenURIs[tokenId] = newTokenURI;
        emit MetadataUpdated(tokenId, newTokenURI);
    }

    function calculatePrice(address claimant) public view returns (uint256) {
        uint256 finalPrice = basePrice;
        uint256 highestNonCompoundDiscount = 0;
        uint256 totalCompoundDiscount = 0;

        for (uint256 i = 1; i <= ruleCount; i++) {
            if (isEligibleForDiscount(claimant, i)) {
                DiscountRule storage rule = discountRules[i];
                if (rule.isCompound) {
                    totalCompoundDiscount += rule.discountPercentage;
                    if (totalCompoundDiscount > maxDiscountCap) {
                        totalCompoundDiscount = maxDiscountCap;
                    }
                } else {
                    if (rule.discountPercentage > highestNonCompoundDiscount) {
                        highestNonCompoundDiscount = rule.discountPercentage;
                    }
                }
            }
        }

        uint256 appliedDiscount = totalCompoundDiscount > highestNonCompoundDiscount ? totalCompoundDiscount : highestNonCompoundDiscount;
        if (appliedDiscount > maxDiscountCap) {
            appliedDiscount = maxDiscountCap;
        }

        finalPrice -= finalPrice * appliedDiscount / 100;
        return finalPrice;
    }

    function isEligibleForDiscount(address claimant, uint256 ruleId) public view returns (bool) {
        DiscountRule storage rule = discountRules[ruleId];

        if (checkNFTBalances(claimant, rule)) {
            return true;
        }

        address[] memory secondaryWallets = linkedWallets[claimant];
        for (uint256 i = 0; i < secondaryWallets.length; i++) {
            if (checkNFTBalances(secondaryWallets[i], rule)) {
                return true;
            }
        }
        return false;
    }

    function checkNFTBalances(address wallet, DiscountRule storage rule) internal view returns (bool) {
        for (uint256 i = 0; i < rule.qualifyingNFTs.length(); i++) {
            if (IERC721(rule.qualifyingNFTs.at(i)).balanceOf(wallet) > 0) {
                return true;
            }
        }
        return false;
    }

    function updateRoyaltyInfo(address recipient, uint96 feeBasisPoints) public onlyOwner {
        _setDefaultRoyalty(recipient, feeBasisPoints);
        emit RoyaltyInfoUpdated(recipient, feeBasisPoints);
    }

    function updateBasePrice(uint256 _newBasePrice) public onlyOwner {
        basePrice = _newBasePrice;
        emit BasePriceUpdated(_newBasePrice);
    }

    function updateRootNode(bytes32 _rootNode) public onlyOwner {
        rootNode = _rootNode;
        emit RootNodeUpdated(_rootNode);
    }

    function updateResolverForNode(bytes32 node, address newResolver) public onlyOwner {
        require(newResolver.isContract(), "New resolver must be a contract");
        Resolver resolver = Resolver(newResolver);
        require(resolver.supportsInterface(type(IAddrResolver).interfaceId), "New resolver does not support required interface");
        ens.setResolver(node, newResolver);
        emit ResolverUpdated(node, newResolver);
    }

    function withdraw() public onlyOwner {
        uint256 balance = agiToken.balanceOf(address(this));
        require(agiToken.transfer(msg.sender, balance), "Withdrawal failed");
    }

    function pause() public onlyOwner {
        _pause();
    }

    function unpause() public onlyOwner {
        _unpause();
    }

    function setMaxMintAmount(uint256 _max) public onlyOwner {
        maxMintAmount = _max;
    }

    // Marketplace functionalities
    function listItem(uint256 tokenId, uint256 price, uint256 duration) external nonReentrant whenNotPaused {
        require(ownerOf(tokenId) == msg.sender, "Caller is not the owner");
        require(price > 0, "Price must be greater than zero");
        require(getApproved(tokenId) == address(this) || isApprovedForAll(msg.sender, address(this)), "Marketplace is not approved");

        uint256 expirationTime = block.timestamp + duration;
        listings[tokenId] = Listing({
            price: price,
            seller: msg.sender,
            expirationTime: expirationTime
        });

        emit Listed(tokenId, price, msg.sender, expirationTime);
    }

    function delistItem(uint256 tokenId) external nonReentrant whenNotPaused {
        require(listings[tokenId].seller == msg.sender, "Caller is not the seller");

        delete listings[tokenId];

        emit Delisted(tokenId);
    }

    function buyItem(uint256 tokenId) external nonReentrant whenNotPaused {
        Listing memory listing = listings[tokenId];
        require(listing.price > 0, "Item not listed for sale");
        require(block.timestamp <= listing.expirationTime, "Listing has expired");

        address seller = listing.seller;
        delete listings[tokenId];

        require(agiToken.transferFrom(msg.sender, seller, listing.price), "Payment failed");

        safeTransferFrom(seller, msg.sender, tokenId);

        emit Purchased(tokenId, listing.price, msg.sender, seller);
    }

    function isListedForSale(uint256 tokenId) external view returns (bool) {
        return listings[tokenId].price > 0 && block.timestamp <= listings[tokenId].expirationTime;
    }

    function getPrice(uint256 tokenId) external view returns (uint256) {
        return listings[tokenId].price;
    }

    function getExpirationTime(uint256 tokenId) external view returns (uint256) {
        return listings[tokenId].expirationTime;
    }

    function totalSupply() public view returns (uint256) {
        return _tokenIdCounter.current();
    }

    // Override functions to address multiple inheritance issues
    function _burn(uint256 tokenId) internal override(ERC721URIStorage, ERC721Royalty) {
