    "contracts/TukuruERC1155.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.20;

import "@openzeppelin/contracts-upgradeable/token/ERC1155/ERC1155Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/common/ERC2981Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";
import './IERC5192.sol';

contract TukuruERC1155 is ERC1155Upgradeable, AccessControlUpgradeable, IERC5192, ERC2981Upgradeable, OwnableUpgradeable {
    using Strings for uint256;

    struct WithdrawSetting {
        address receiver;
        uint256 ratio;
    }

    // Role
    bytes32 public constant ADMIN = "ADMIN";
    bytes32 public constant MINTER = "MINTER";
    bytes32 public constant PIEMENT = "PIEMENT";

    // Metadata
    string public name;
    string public symbol;
    string public baseURI;
    string public baseExtension;

    // Mint
    mapping(uint256 => IERC20) public currencies;
    mapping(uint256 => uint256) public mintCosts;
    mapping(uint256 => uint256) public maxSupply;
    mapping(uint256 => uint256) public totalSupply;
    bool public isLocked;

    // AL Mint
    mapping(uint256 => bool) public isAllowlistSale;
    mapping(uint256 => uint256) public mintLimit;
    mapping(uint256 => mapping(address => uint256)) public mintedAmount;
    mapping(uint256 => bytes32) merkleRoot;

    // Withdraw
    uint256 public usageFee = 0.1 ether;
    WithdrawSetting[] public withdrawSettings;
    uint256 public systemRoyalty;
    address public royaltyReceiver;
    address public royaltyReceiverCurrency;

    // Modifier
    modifier withinMaxSupply(uint256 _tokenId, uint256 _amount) {
        require(totalSupply[_tokenId] + _amount <= maxSupply[_tokenId], 'Over Max Supply');
        _;
    }
    modifier enoughEth(uint256 _tokenId, uint256 _amount) {
        if (address(currencies[_tokenId]) == address(0)) {
            require(msg.value >= _amount * mintCosts[_tokenId], 'Not Enough Eth');
        } else {
            require(currencies[_tokenId].balanceOf(msg.sender) >= _amount * mintCosts[_tokenId], 'Not Enough Currency');
        }
        _;
    }
    modifier withinMintLimit(uint256 _tokenId, uint256 _amount) {
        require(mintLimit[_tokenId] == 0 || mintLimit[_tokenId] >= mintedAmount[_tokenId][msg.sender] + _amount, 'Over Mint Limit');
        _;
    }
    modifier isValidProof(address _address, uint256 _tokenId, uint256 _allowlistedAmount, bytes32[] calldata _merkleProof) {
        bytes32 node = keccak256(abi.encodePacked(_address, _allowlistedAmount));
        require(MerkleProof.verifyCalldata(_merkleProof, merkleRoot[_tokenId], node), "Invalid proof");
        _;
    }

    constructor() {
        _disableInitializers();
    }

    function initialize(
        address _owner,
        string memory _name,
        string memory _symbol,
        bool _isLocked,
        uint96 _royaltyFee,
        address _withdrawAddress,
        uint256 _systemRoyalty,
        address _royaltyReceiver,
        address _royaltyReceiverCurrency,
        address _piementMinter,
        string memory _prefBaseURI
    )  external initializer {
        __ERC1155_init("");
        __AccessControl_init();
        __ERC2981_init();
        __Ownable_init(_owner);

        // Role
        _grantRole(DEFAULT_ADMIN_ROLE, _owner);
        _grantRole(ADMIN, _owner);

        // Feature
        name = _name;
        symbol = _symbol;
        isLocked = _isLocked;

        // Withdraw
        withdrawSettings = [WithdrawSetting(_withdrawAddress, 100)];

        // Payment
        _setDefaultRoyalty(_withdrawAddress, _royaltyFee);
        systemRoyalty = _systemRoyalty;
        royaltyReceiver = _royaltyReceiver;
        royaltyReceiverCurrency = _royaltyReceiverCurrency;
        _grantRole(PIEMENT, _piementMinter);

        // Metadata
        baseURI = string(abi.encodePacked(_prefBaseURI, "/", Strings.toHexString(address(this)), "/"));
        baseExtension = ".json";
    }
    function updateToNoSystemRoyalty() external payable {
        require(systemRoyalty > 0, "No System Royalty");
        require(msg.value >= usageFee, "Not Enough Eth");
        systemRoyalty = 0;
    }

    // Airdrop
    function airdrop(uint256[] calldata _tokenIds, address[] calldata _addresses,  uint256[] calldata _amounts) external onlyRole(ADMIN) {
        require(_tokenIds.length == _addresses.length && _addresses.length == _amounts.length, "Invalid Length");
        for (uint256 i = 0; i < _addresses.length; i++) {
            _mint(_addresses[i], _tokenIds[i], _amounts[i], "");
            totalSupply[_tokenIds[i]] += _amounts[i];
        }
    }

    // Public Mint
    function mint(address _address, uint256 _tokenId, uint256 _amount) external payable {
        _publicMintCommon(_address, _tokenId, _amount, msg.sender);
    }
    function piementMint(address _address, uint256 _tokenId, uint256 _amount) external payable onlyRole(PIEMENT) {
        _publicMintCommon(_address, _tokenId, _amount, _address);
    }
    function _publicMintCommon(address _address, uint256 _tokenId, uint256 _amount, address _senderAddress) internal
        withinMaxSupply(_tokenId, _amount)
        withinMintLimit(_tokenId, _amount)
        enoughEth(_tokenId, _amount)
    {
        require(!isAllowlistSale[_tokenId], "Now AL Sale");
        _mintCommon(_address, _tokenId, _amount);
        mintedAmount[_tokenId][_senderAddress] += _amount;
    }

    // AL Mint
    function allowlistMint(address _address, uint256 _tokenId, uint256 _amount, uint256 _allowlistedAmount, bytes32[] calldata _merkleProof) external payable {
        _allowlistMintCommon(_address, _tokenId, _amount, _allowlistedAmount, _merkleProof, msg.sender);
    }
    function piementAllowlistMint(address _address, uint256 _tokenId, uint256 _amount, uint256 _allowlistedAmount, bytes32[] calldata _merkleProof) external payable onlyRole(PIEMENT) {
        _allowlistMintCommon(_address, _tokenId, _amount, _allowlistedAmount, _merkleProof, _address);
    }
    function _allowlistMintCommon(address _address, uint256 _tokenId, uint256 _amount, uint256 _allowlistedAmount, bytes32[] calldata _merkleProof, address _senderAddress) private
        withinMaxSupply(_tokenId, _amount)
        enoughEth(_tokenId, _amount)
        isValidProof(_senderAddress, _tokenId, _allowlistedAmount, _merkleProof)
    {
        require(isAllowlistSale[_tokenId], "Now Not AL Sale");
        require(mintedAmount[_tokenId][_senderAddress] + _amount <= _allowlistedAmount, "Over Allowlist Amount");
        _mintCommon(_address, _tokenId, _amount);
        mintedAmount[_tokenId][_senderAddress] += _amount;
    }

    // Others
    function externalMint(address _address, uint256 _tokenId, uint256 _amount) external onlyRole(MINTER) {
        _mintCommon(_address, _tokenId, _amount);
    }
    function _mintCommon(address _address, uint256 _tokenId, uint256 _amount) private {
        if (currencies[_tokenId] != IERC20(address(0))) {
            currencies[_tokenId].transferFrom(msg.sender, address(this), _amount * mintCosts[_tokenId]);
        }
        _mint(_address, _tokenId, _amount, "");
        totalSupply[_tokenId] += _amount;
        if (isLocked) {
            emit Locked(_tokenId);
        }
    }

    // Withdraw
    function withdraw(uint256 _withdrawAmount) public onlyRole(ADMIN) {
        uint256 _balance = address(this).balance;
        require(_balance >= _withdrawAmount && _balance > 0, "Not Enough Balance");

        uint256 _remainAmount = 0;
        if (_withdrawAmount == 0) {
            _remainAmount = _balance;
        } else {
            _remainAmount = _withdrawAmount;
        }
        bool success;

        if (systemRoyalty > 0) {
            uint256 _royaltyAmount = _remainAmount * systemRoyalty / 100;
            (success, ) = payable(royaltyReceiver).call{value: _royaltyAmount}("");
            require(success);
            _remainAmount -= _royaltyAmount;
        }
        uint256 _totalDistribution = _remainAmount;

        uint256 _totalRatio = 0;
        for (uint256 i = 0; i < withdrawSettings.length; i++) {
            _totalRatio += withdrawSettings[i].ratio;
        }
        for (uint256 i = 0; i < withdrawSettings.length; i++) {
            WithdrawSetting memory _withdrawSetting = withdrawSettings[i];
            if (i == withdrawSettings.length - 1) {
                (success, ) = payable(_withdrawSetting.receiver).call{value: _remainAmount}("");
                require(success);
            } else {
                uint256 payAmount = _totalDistribution * _withdrawSetting.ratio / _totalRatio;
                (success, ) = payable(_withdrawSetting.receiver).call{value: payAmount}("");
                require(success);
                _remainAmount -= payAmount;
            }
        }
    }
    function withdrawCurrency(address _currencyAddress, uint256 _withdrawAmount) public onlyRole(ADMIN) {
        IERC20 _currency = IERC20(_currencyAddress);
        uint256 _balance = _currency.balanceOf(address(this));
        require(_balance >= _withdrawAmount && _balance > 0, "Not Enough Balance");

        uint256 _remainAmount = 0;
        if (_withdrawAmount == 0) {
            _remainAmount = _balance;
        } else {
            _remainAmount = _withdrawAmount;
        }

        if (systemRoyalty > 0) {
            uint256 _royaltyAmount = _remainAmount * systemRoyalty / 100;
            _currency.transfer(royaltyReceiverCurrency, _royaltyAmount);
            _remainAmount -= _royaltyAmount;
        }
        uint256 _totalDistribution = _remainAmount;

        uint256 _totalRatio = 0;
        for (uint256 i = 0; i < withdrawSettings.length; i++) {
            _totalRatio += withdrawSettings[i].ratio;
        }

        for (uint256 i = 0; i < withdrawSettings.length; i++) {
            WithdrawSetting memory _withdrawSetting = withdrawSettings[i];
            if (i == withdrawSettings.length - 1) {
                _currency.transfer(_withdrawSetting.receiver, _remainAmount);
            } else {
                uint256 payAmount = _totalDistribution * _withdrawSetting.ratio / _totalRatio;
                _currency.transfer(_withdrawSetting.receiver, payAmount);
                _remainAmount -= payAmount;
            }
        }
    }


    // Getter
    function uri(uint256 _tokenId) public view virtual override returns (string memory) {
        return string(abi.encodePacked(baseURI, _tokenId.toString(), baseExtension));
    }
    function getBalance() public view returns (uint256) {
        return address(this).balance;
    }

    // Setter
    function setWithdrawSettings(WithdrawSetting[] memory _value) public onlyRole(ADMIN) {
        withdrawSettings = _value;
    }
    function setMetadataBase(string memory _baseURI, string memory _baseExtension) external onlyRole(ADMIN) {
        baseURI = _baseURI;
        baseExtension = _baseExtension;
    }
    function setIsLocked(bool _isLocked) external onlyRole(ADMIN) {
        isLocked = _isLocked;
    }
    function setSalesInfo(uint256 _tokenId, uint256 _mintCost, uint256 _maxSupply, uint256 _mintLimit, address _currency) external onlyRole(ADMIN) {
        mintCosts[_tokenId] = _mintCost;
        maxSupply[_tokenId] = _maxSupply;
        mintLimit[_tokenId] = _mintLimit;
        isAllowlistSale[_tokenId] = false;
        currencies[_tokenId] = IERC20(_currency);
    }
    function setAllowlistSalesInfo(uint256 _tokenId, uint256 _mintCost, uint256 _maxSupply, bytes32 _merkleRoot, address _currency) external onlyRole(ADMIN) {
        mintCosts[_tokenId] = _mintCost;
        maxSupply[_tokenId] = _maxSupply;
        isAllowlistSale[_tokenId] = true;
        merkleRoot[_tokenId] = _merkleRoot;
        currencies[_tokenId] = IERC20(_currency);
    }

    // interface
    function supportsInterface(bytes4 interfaceId) public view virtual override(ERC1155Upgradeable, AccessControlUpgradeable, ERC2981Upgradeable) returns (bool) {
        return
            ERC1155Upgradeable.supportsInterface(interfaceId) ||
            AccessControlUpgradeable.supportsInterface(interfaceId) ||
            ERC2981Upgradeable.supportsInterface(interfaceId) ||
            interfaceId == type(IERC5192).interfaceId ||
            super.supportsInterface(interfaceId);
    }

    // Locked
    function locked(uint256) override public view returns (bool){
        return isLocked;
    }
    function emitLockState(uint256 _tokenId) external onlyRole(ADMIN) {
        if (isLocked) {
            emit Locked(_tokenId);
        } else {
            emit Unlocked(_tokenId);
        }
    }
    function setApprovalForAll(address _operator, bool _approved) public virtual override {
        require (!_approved || !isLocked, "Locked");
        super.setApprovalForAll(_operator, _approved);
    }
    function _update(address _from, address _to, uint256[] memory _ids, uint256[] memory _values) internal virtual override {
        require (!isLocked || _from  == address(0) || _to == address(0), "Locked");
        super._update(_from, _to, _ids, _values);
    }
}"
