pragma solidity ^0.8.13;
import "openzeppelin/access/Ownable.sol";
import {WETH} from "solady/tokens/WETH.sol";
import {curveSwap} from "../basePool/curveSwapV2.sol";
import {IERC20} from "openzeppelin/token/ERC20/IERC20.sol";

//    V.2
//   ____________________________________
//   |::::::/-/-/-/-/-/-/-/-/-/-/-::::::|
//   |:(1)*······SMOL.DOLLAR·······*(1):|
//   |:''·········/¯¯¯¯¯¯¯\··'''''''·'':|
//   |':{G}·······| ʕ•ᴥ•ʔ |···········:'|
//   |:'···''''''·| { . } |····O.N.E.·':|
//   |:(1)········\_______/·········(1):|
//   |//-------ONE.SMOL.DOLLAR--------//|
//   ------------------------------------
//

// This is a reduced version of V.1 - now able to handle WETH as well as exchange Eth -> STETH  - all Seigniorage related Code is out for now

contract smolFedV2 is Ownable, curveSwap {
    address public BasePool;
    WETH public weth;
    IERC20 public STETH;

    constructor(
        address _token0,
        address curvePool,
        address _WETH,
        address pool
    ) curveSwap(_token0, curvePool) {
        weth = WETH(payable(_WETH));
        STETH = IERC20(_token0);
        BasePool = pool;
    }

    function unwrappAllWeth() public onlyOwner {
        uint256 b = weth.balanceOf(address(this));
        weth.withdraw(b);
    }

    function exchangeETH(uint256 amount) public onlyOwner {
        EthToStETHSlippage(amount);
    }

    function sendSTETH() public onlyOwner {
        uint256 b = STETH.balanceOf(address(this));
        STETH.transfer(BasePool, b);
    }

    function exchangeAndSend() public onlyOwner {
        //check and unwrapp weth
        if (weth.balanceOf(address(this)) > 0) {
            unwrappAllWeth();
        }
        // get all ETH
        uint256 allETH = payable(address(this)).balance;
        // change ETH -> STETH
        exchangeETH(allETH);
        // send STETH to basepool
        sendSTETH();
    }

    // function to set new Approval Limit for Pool
    function setApprovalPool(uint256 maxAmount) external onlyOwner {
        _setApproval(maxAmount);
    }

    // function to migrate to new pool address
    function setNewPool(address pool) external onlyOwner {
        _setNewPool(pool);
    }

    function setWeth(address _WETH) external onlyOwner {
        weth = WETH(payable(_WETH));
    }

    function setSTeth(address _STETH) external onlyOwner {
        STETH = IERC20(_STETH);
    }

    function setBasePool(address pool) external onlyOwner {
        BasePool = pool;
    }
}
