    "contracts/WW3SHIBASale.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.18;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";

import "@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol";

import "./IWW3SHIBASale.sol";
import "hardhat/console.sol";


contract WW3SHIBASale is Initializable, PausableUpgradeable, AccessControlUpgradeable, IWW3SHIBASale {
    using SafeERC20Upgradeable for IERC20Upgradeable;

    IERC20Upgradeable public WW3SHIBAToken; // Token for sale
    IERC20Upgradeable public usdt; // USDT token for WW3SHIBA token purchasing

    struct Stage {
        uint256 tokensToSale; // Amount of tokens to sale
        uint256 tokensSold; // Sold tokens amount
        uint256 priceInUSD; // Price in USD with 8 decimals
        uint256 bonusPercent; // Bonus percent
    }

    Stage[] public stages; // Array with stages and their info

    address public treasury; // Address to receive funds
    uint256 public currentStage; // Current stage
    uint256 public saleDeadline; // Deadline for WW3SHIBA token sale
    uint256 public userRewardPercent; // Reward percent for referrers-user

    uint256 public totalTokensToBeDistributed; // Total amount of tokens to be distributed (sum of initial WW3SHIBA tokens amount from all stages)
    uint256 public totalTokensSold; // Total amount of tokens sold
    uint256 public totalTokensClaimed; // Total amount of tokens claimed by users
    uint256 public totalRewardsClaimed; // Total amount of WW3SHIBA claimed by referrers
    uint256 public totalRewards; // Total amount of WW3SHIBA received by referrers

    uint256 public constant PERCENT_DENOMINATOR = 10_000; // so, 10% -> 1000, 5% -> 500

    mapping(address => uint256) public referralRewards; // Amount of WW3SHIBA received by referrers
    mapping(address => uint256) public claimableTokens; // Amount of WW3SHIBA tokens claimable by users

    AggregatorV3Interface public priceFeedUsdt; // Chainlink price feed for USDT
    AggregatorV3Interface public priceFeedEth; // Chainlink price feed for ETH

    event ClaimDateExtended(uint256 newClaimDate);
    event DeadlineExtended(uint256 newDeadline);
    event WW3SHIBATokenChanged(address newWW3SHIBAToken);
    event PriceFeedEthChanged(address newPriceFeedEth);
    event PriceFeedUsdtChanged(address newPriceFeedUsdt);
    event RewardsDistributed(uint256 distributedRewards, address referrer);
    event ReferrerRewardsPaid(address referrer, uint256 amount);
    event RewardPercentChanged(uint256 userRewardPercent);
    event RemainderWithdrawn(address treasury, uint256 amountWithdrawn);
    event StageAdded(uint256 tokens, uint256 priceInUSD);
    event TokensBought(address buyer, uint256 amount);
    event TokensClaimed(address claimer, uint256 amount);
    event TreasuryChanged(address newTreasury);
    event UsdtChanged(address newUsdt);

    error AllStagesCompleted();
    error CallerIsNotAdmin();
    error CallerIsNotAffiliateAdmin();
    error ClaimDateNotReached();
    error ClaimEnded();
    error ContractPaused();
    error DeadlineNotReached();
    error NewDeadlineIsInPast();
    error NotEnoughTokensInLastStage();
    error NotEnoughTokensInNextStage();
    error NotEnoughTokensInStageToDistribute();
    error PercentEqualOrGreaterThanDenominator();
    error ReferrerIsNotRegistered();
    error ReferrerIsSender();
    error TokenSaleEnded();
    error TransferFailed();
    error ZeroAddress();
    error ZeroAmount();

    modifier nonZeroAddress(address addr) {
        if (addr == address(0)) revert ZeroAddress();
        _;
    }

    modifier nonZeroAmount(uint256 amount) {
        if (amount == 0) revert ZeroAmount();
        _;
    }

    modifier whenActive() {
        if (block.timestamp >= saleDeadline) revert TokenSaleEnded();
        if (currentStage >= stages.length) revert AllStagesCompleted();
        // if amount in last stage to sale is less than 1e9, then revert
        if (stages[stages.length - 1].tokensToSale - stages[stages.length - 1].tokensSold < 1e9) revert AllStagesCompleted();
        if (paused()) revert ContractPaused();
        _;
    }

    function initialize(
        IERC20Upgradeable _token,
        IERC20Upgradeable _usdt,
        address _priceFeedEth,
        address _priceFeedUsdt,
        address _treasury
    ) public initializer {
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);

        setSaleDeadline(block.timestamp + 4 * 30 days);

        setTreasury(_treasury);
        setWW3SHIBAToken(_token);
        setUsdt(_usdt);
        setRewardPercent(1000); //10%
        setPriceFeedEth(_priceFeedEth);
        setPriceFeedUsdt(_priceFeedUsdt);

        _addStages();
    }

    receive() external payable {}

    ///@notice Pause contract
    ///@dev Only admin can pause contract
    function pause() public onlyRole(DEFAULT_ADMIN_ROLE) {
        _pause();
    }

    ///@notice Unpause contract
    ///@dev Only admin can unpause contract
    function unpause() public onlyRole(DEFAULT_ADMIN_ROLE) {
        _unpause();
    }

    /// @notice Buy WW3SHIBA tokens with ETH
    /// @param referrer Referrer address
    /// @dev There is no purchase itself. Users collect their tokens in the claimTokens() function
    /// @dev Perform calculation of how many tokens the user is entitled to via _buyTokens() function
    /// @dev Transfer ETH to the treasury
    /// @dev Reward to the referrer is kept on the contract
    function buyTokensWithEth(address referrer) public payable whenActive nonZeroAmount(msg.value) {
        _distributeRewardReferral(referrer, msg.value, true);
        _buyTokens(msg.value, true);
        (bool success, ) = payable(treasury).call{value: msg.value}("");
        if (!success) revert TransferFailed();
    }

    /// @notice Buy WW3SHIBA tokens with USDT
    /// @param usdtAmount Amount of USDT - 6 decimals
    /// @param referrer Referrer address
    /// @dev There is no purchase itself. Users collect their tokens in the claimTokens() function
    /// @dev Perform calculation of how many tokens the user is entitled to via _buyTokens() function
    /// @dev Transfer USDT to the treasury
    /// @dev Reward to the referrer is kept on the contract
    function buyTokensWithUsdt(uint256 usdtAmount, address referrer) public whenActive nonZeroAmount(usdtAmount) {
        _distributeRewardReferral(referrer, usdtAmount, false);
        usdt.safeTransferFrom(msg.sender, treasury, usdtAmount);
        _buyTokens(usdtAmount, false);
    }

    ///@notice Buy WW3SHIBA tokens for user
    ///@param user User address
    ///@param amountWW3SHIBA Amount of WW3SHIBA tokens in WEI
    ///@dev Only admin can buy tokens for user
    function buyFor(address user, address referrer, uint256 amountWW3SHIBA) external whenActive onlyRole(DEFAULT_ADMIN_ROLE) {
        uint256 _currentStage = currentStage;
        uint256 remainingTokens = stages[_currentStage].tokensToSale - stages[_currentStage].tokensSold;
        if (referrer == user) revert ReferrerIsSender();
        if (referrer != address(0)) {
            uint256 amountForReferrer = (amountWW3SHIBA * userRewardPercent) / PERCENT_DENOMINATOR;
            totalRewards += amountForReferrer;
            if (remainingTokens >= amountForReferrer) {
                Stage storage stage = stages[_currentStage];
                stage.tokensSold = stage.tokensToSale;
                totalTokensSold += amountForReferrer;
                referralRewards[referrer] += amountForReferrer;
                if (stage.tokensSold == stage.tokensToSale) {
                    currentStage++;
                }
            } else {
                uint256 excessTokens = amountForReferrer;
                for(uint256 i = _currentStage; i < stages.length; i++) {
                    Stage storage stage = stages[i];
                    if(excessTokens > stage.tokensToSale - stage.tokensSold) {
                        excessTokens -= stage.tokensToSale - stage.tokensSold;
                        stage.tokensSold = stage.tokensToSale;
                        totalTokensSold += stage.tokensToSale - stage.tokensSold;
                        referralRewards[referrer] += stage.tokensToSale - stage.tokensSold;
                        if (stage.tokensSold == stage.tokensToSale) {
                            currentStage++;
                        }
                    } else {
                        stage.tokensSold += excessTokens;
                        totalTokensSold += excessTokens;
                        referralRewards[referrer] += excessTokens;
                        if (stage.tokensSold == stage.tokensToSale) {
                            currentStage++;
                        }
                        break;
                    }
                }
            }
        }
        if (remainingTokens >= amountWW3SHIBA) {
            Stage storage stage = stages[currentStage];
            stage.tokensSold = stage.tokensToSale;
            totalTokensSold += amountWW3SHIBA;
            claimableTokens[user] += amountWW3SHIBA;
            if (stage.tokensSold == stage.tokensToSale) {
                currentStage++;
            }
        } else {
            uint256 excessTokens = amountWW3SHIBA;
            for(uint256 i = currentStage; i < stages.length; i++) {
                Stage storage stage = stages[i];
                if(excessTokens > stage.tokensToSale - stage.tokensSold) {
                    excessTokens -= stage.tokensToSale - stage.tokensSold;
                    stage.tokensSold = stage.tokensToSale;
                    totalTokensSold += stage.tokensToSale - stage.tokensSold;
                    claimableTokens[user] += stage.tokensToSale - stage.tokensSold;
                    if (stage.tokensSold == stage.tokensToSale) {
                        currentStage++;
                    }
                } else {
                    stage.tokensSold += excessTokens;
                    totalTokensSold += excessTokens;
                    claimableTokens[user] += excessTokens;
                    if (stage.tokensSold == stage.tokensToSale) {
                        currentStage++;
                    }
                    break;
                }
            }
        }
        emit TokensBought(user, amountWW3SHIBA);
    }

    ///@notice Claim WW3SHIBA tokens by user
    ///@dev Users can claim their tokens after the claim date
    ///@dev Tokens are transferred to the user's wallet
    ///@dev Calculation of user's WW3SHIBA token amount is performed in the _buyTokens() function
    function claimTokens() external {
        if (block.timestamp < saleDeadline) {
            revert ClaimDateNotReached();
        }
        else if (block.timestamp > saleDeadline + 30 days) {
            revert ClaimEnded();
        }
        uint256 amount = claimableTokens[msg.sender];
        if(amount == 0) {
            revert ZeroAmount();
        }
        claimableTokens[msg.sender] = 0;
        totalTokensClaimed += amount;
        WW3SHIBAToken.safeTransfer(msg.sender, amount);
        emit TokensClaimed(msg.sender, amount);
    }

    ///@notice Claim reward in WW3SHIBA tokens by referrer
    ///@dev Referrers can claim their reward without restrictions
    ///@dev Reward is transferred to the referrer's wallet
    function claimReferralReward() external {
        if (block.timestamp < saleDeadline) {
            revert ClaimDateNotReached();
        }
        else if (block.timestamp > saleDeadline + 30 days) {
            revert ClaimEnded();
        }
        uint256 amount = referralRewards[msg.sender];
        if (amount != 0) {
            referralRewards[msg.sender] = 0;
            totalRewardsClaimed += amount;
            WW3SHIBAToken.safeTransfer(msg.sender, amount);
        }
        emit ReferrerRewardsPaid(msg.sender, amount);
    }

    ///@notice Claim all tokens and rewards by user
    function claimAll() external {
        if (block.timestamp < saleDeadline) {
            revert ClaimDateNotReached();
        }
        else if (block.timestamp > saleDeadline + 30 days) {
            revert ClaimEnded();
        }
        uint256 amount = claimableTokens[msg.sender];
        if(amount != 0) {
            claimableTokens[msg.sender] = 0;
            totalTokensClaimed += amount;
            WW3SHIBAToken.safeTransfer(msg.sender, amount);
        }
        emit TokensClaimed(msg.sender, amount);

        uint256 amountReferrer = referralRewards[msg.sender];
        if (amountReferrer != 0) {
            referralRewards[msg.sender] = 0;
            totalRewardsClaimed += amountReferrer;
            WW3SHIBAToken.safeTransfer(msg.sender, amountReferrer);
        }
        emit ReferrerRewardsPaid(msg.sender, amountReferrer);
    }

    ///@notice Withdraw remainder of WW3SHIBA tokens
    ///@dev Only admin can withdraw remainder of WW3SHIBA tokens
    ///@dev Remainder of WW3SHIBA tokens after token sale is transferred to the treasury
    ///@dev Possible to withdraw only after token sale is ended
    function withdrawRemainder(uint256 amount) external onlyRole(DEFAULT_ADMIN_ROLE) {
        if (block.timestamp < saleDeadline + 30 days || currentStage < stages.length) {
            revert DeadlineNotReached();
        }
        WW3SHIBAToken.safeTransfer(treasury, amount);
        emit RemainderWithdrawn(treasury, amount);
    }

    /// @notice Calculate WW3SHIBA token amount entitled to user
    /// @param amount Amount of ETH or USDT - 18 or 6 decimals
    /// @param isEth True if amount is in ETH, false if amount is in USDT
    /*
    If user wants to buy more tokens than there are left in the current stage,
    the missing amount is taken from the next stage at the price specified in the new stage.
    Example: a user wants to buy 100 tokens from stage 0 at the price of 0.01, but there are only 50 tokens left in stage 0.
    The shortage, i.e. 100 - 50 = 50, will be sold to the user at the new price, 0.0125, which is specified in stage 1.
    */
    function _buyTokens(uint256 amount, bool isEth) internal {
        (uint256 currentStageTokens, uint256[] memory tokensNextStage) = _calculateTokenAmountFromCurrencyAmount(amount, isEth);
        _distributeTokensInCurrentStage(currentStageTokens, msg.sender);
        for(uint256 i = 0; i < tokensNextStage.length; i++) {
            _distributeTokensInCurrentStage(tokensNextStage[i], msg.sender);
        }
    }

    ///@notice Calculate rate in currency
    function _getRateInCurrency(uint256 stageId, bool isEth) internal view returns (uint256) {
        Stage storage stage = stages[stageId];
        // price in 18 decimals
        uint256 currentPrice = (isEth ? _getEthPrice() : _getUsdtPrice()) * 1e10;
        uint256 priceInUSDDecimals = 1e8;
        uint256 rateInCurrency = (priceInUSDDecimals * currentPrice) / stage.priceInUSD; // calculate the number of tokens in 1 unit of the selected currency based on its current price (USDT or ETH)
        return rateInCurrency + (rateInCurrency * stage.bonusPercent) / PERCENT_DENOMINATOR; // subtract bonus percent from the rate
    }

    ///@notice Distribute tokens in current stage
    function _distributeTokensInCurrentStage(uint256 tokens, address user) internal {
        Stage storage stage = stages[currentStage];
        if (stage.tokensToSale - stage.tokensSold < tokens) revert NotEnoughTokensInStageToDistribute();
        stage.tokensSold += tokens;
        totalTokensSold += tokens;
        claimableTokens[user] += tokens;

        if (stage.tokensSold == stage.tokensToSale) {
            currentStage++;
        }
        emit TokensBought(user, tokens);
    }

    /// @notice Calculate reward for referrer
    /// @param referrer Referrer address
    /// @param amount Amount of ETH or USDT - 18 or 6 decimals
    /// @param isEth True if amount is in ETH, false if amount is in USDT
    /// @dev Reward is calculated based on the amount of ETH or USDT sent by the user
    /// @dev Blogger referrers receive 10% of the amount sent by the user and user referrers receive 5%
    function _distributeRewardReferral(address referrer, uint256 amount, bool isEth) internal {
        if (referrer == msg.sender) revert ReferrerIsSender();
        if (referrer != address(0)) {
            uint256 rewardPercent = userRewardPercent;
            (uint256 currentStageReward, uint256[] memory rewardsNextStage) = _calculateTokenAmountFromCurrencyAmount((amount * rewardPercent) / PERCENT_DENOMINATOR, isEth);
            Stage storage stage = stages[currentStage];
            if (stage.tokensToSale - stage.tokensSold < currentStageReward) revert NotEnoughTokensInStageToDistribute();
            stage.tokensSold += currentStageReward;
            totalTokensSold += currentStageReward;
            if (stage.tokensSold == stage.tokensToSale) {
                currentStage++;
            }
            uint256 reward = currentStageReward;
            for(uint256 i = 0; i < rewardsNextStage.length; i++) {
                Stage storage nextStage = stages[currentStage + i];
                if (nextStage.tokensToSale - nextStage.tokensSold < rewardsNextStage[i]) revert NotEnoughTokensInNextStage();
                nextStage.tokensSold += rewardsNextStage[i];
                totalTokensSold += rewardsNextStage[i];
                if (stage.tokensSold == stage.tokensToSale) {
                    currentStage++;
                }
                reward += rewardsNextStage[i];
            }
            totalRewards += reward;
            referralRewards[referrer] += reward;
            emit RewardsDistributed(reward, referrer);
        }

    }

    ///@notice Add all stages with tokens and price in USD
    function _addStages() internal {
        _addStage(150_000_000, 10e4, 0);    // 150 million tokens for 0.0010 USDT (no bonus)
        _addStage(200_000_000, 13e4, 500);  // 200 million tokens for 0.0013 USDT (5% bonus)
        _addStage(225_000_000, 16e4, 800);  // 225 million tokens for 0.0016 USDT (8% bonus)
        _addStage(225_000_000, 19e4, 1000); // 225 million tokens for 0.0019 USDT (10% bonus)
        _addStage(250_000_000, 23e4, 1200); // 250 million tokens for 0.0023 USDT (12% bonus)
        _addStage(250_000_000, 29e4, 1400); // 250 million tokens for 0.0029 USDT (14% bonus)
        _addStage(250_000_000, 37e4, 1600); // 250 million tokens for 0.0037 USDT (16% bonus)
        _addStage(200_000_000, 44e4, 1800); // 200 million tokens for 0.0044 USDT (18% bonus)
        _addStage(200_000_000, 49e4, 2000); // 200 million tokens for 0.0049 USDT (20% bonus)
        _addStage(200_000_000, 56e4, 2000); // 200 million tokens for 0.0056 USDT (20% bonus)
    }                                       // total 2 billion 150 million tokens

    ///@notice Add stage with tokens and price in USD
    function _addStage(uint256 tokensToSale, uint256 priceInUSD, uint256 bonusPercent) internal {
        uint256 tokensToSaleWithDecimals = tokensToSale * 10 ** 18;
        stages.push(Stage(tokensToSaleWithDecimals, 0, priceInUSD, bonusPercent));
        totalTokensToBeDistributed += tokensToSaleWithDecimals;
        emit StageAdded(tokensToSale, priceInUSD);
    }

    ///@notice Set new treasury address
    ///@param _treasury New treasury address
    ///@dev Only admin can set new treasury address
    function setTreasury(address _treasury) public onlyRole(DEFAULT_ADMIN_ROLE) nonZeroAddress(_treasury) {
        treasury = _treasury;
        emit TreasuryChanged(_treasury);
    }

    ///@notice Set new WW3SHIBA token address
    ///@param _token New WW3SHIBA token address
    ///@dev Only admin can set new WW3SHIBA token address
    function setWW3SHIBAToken(
        IERC20Upgradeable _token
    ) public onlyRole(DEFAULT_ADMIN_ROLE) nonZeroAddress(address(_token)) {
        WW3SHIBAToken = _token;
        emit WW3SHIBATokenChanged(address(_token));
    }

    ///@notice Set new USDT address
    ///@param _usdt New USDT address
    ///@dev Only admin can set new USDT address
    function setUsdt(IERC20Upgradeable _usdt) public onlyRole(DEFAULT_ADMIN_ROLE) nonZeroAddress(address(_usdt)) {
        usdt = _usdt;
        emit UsdtChanged(address(_usdt));
    }

    ///@notice Set new price feed for ETH
    ///@param _priceFeedEth New price feed for ETH
    ///@dev Only admin can set new price feed for ETH
    function setPriceFeedEth(address _priceFeedEth) public onlyRole(DEFAULT_ADMIN_ROLE) nonZeroAddress(_priceFeedEth) {
        priceFeedEth = AggregatorV3Interface(_priceFeedEth);
        emit PriceFeedEthChanged(_priceFeedEth);
    }

    ///@notice Set new price feed for USDT
    ///@param _priceFeedUsdt New price feed for USDT
    ///@dev Only admin can set new price feed for USDT
    function setPriceFeedUsdt(
        address _priceFeedUsdt
    ) public onlyRole(DEFAULT_ADMIN_ROLE) nonZeroAddress(_priceFeedUsdt) {
        priceFeedUsdt = AggregatorV3Interface(_priceFeedUsdt);
        emit PriceFeedUsdtChanged(_priceFeedUsdt);
    }

    ///@notice Set new reward percent for referrers
    ///@param _userRewardPercent New reward percent for referrers-user
    ///@dev Only admin can set new reward percent for referrers
    function setRewardPercent(
        uint256 _userRewardPercent
    ) public onlyRole(DEFAULT_ADMIN_ROLE) {
        if (_userRewardPercent >= PERCENT_DENOMINATOR) {
            revert PercentEqualOrGreaterThanDenominator();
        }
        userRewardPercent = _userRewardPercent;
        emit RewardPercentChanged(_userRewardPercent);
    }

    ///@notice Set new sale deadline
    ///@param newDeadline New sale deadline
    ///@dev Only admin can set new sale deadline
    function setSaleDeadline(uint256 newDeadline) public onlyRole(DEFAULT_ADMIN_ROLE) {
        if (newDeadline < block.timestamp) {
            revert NewDeadlineIsInPast();
        }
        saleDeadline = newDeadline;
        emit DeadlineExtended(newDeadline);
    }

    ///@notice Return stages quantity
    function getStagesLength() external view returns (uint256) {
        return stages.length;
    }

    ///@notice Return current stage info
    ///@return tokensToSale Amount of tokens to sale
    ///@return tokensSold Sold tokens amount
    ///@return priceInUSD Price in USD with 8 decimals
    ///@return bonusPercent Bonus percent
    ///@return _currentStage Current stage
    ///@return nextStagePriceInUsd Price in USD with 8 decimals for next stage
    function getCurrentStageInfo()
        external
        view
        returns (
            uint256 tokensToSale,
            uint256 tokensSold,
            uint256 priceInUSD,
            uint256 bonusPercent,
            uint256 _currentStage,
            uint256 nextStagePriceInUsd
        )
    {
        uint256 stageId = currentStage;
        nextStagePriceInUsd = stageId == stages.length - 1 ? 0 : stages[stageId + 1].priceInUSD;
        return (
            stages[stageId].tokensToSale,
            stages[stageId].tokensSold,
            stages[stageId].priceInUSD,
            stages[stageId].bonusPercent,
            currentStage,
            nextStagePriceInUsd
        );
    }

    ///@notice Return info for all stages
    function getAllStagesInfo() external view returns (Stage[] memory) {
        return stages;
    }

    ///@notice Return rewards amount in USDT and ETH for specified referrer
    function getRewardsForReferrer(address referrer) external view returns (uint256) {
        return (referralRewards[referrer]);
    }

    ///@notice Return user's balances in WW3SHIBA token, USDT and ETH
    function getUserBalances(
        address user
    ) external view returns (uint256 WW3SHIBABalance, uint256 usdtBalance, uint256 ethBalance) {
        WW3SHIBABalance = WW3SHIBAToken.balanceOf(user);
        usdtBalance = usdt.balanceOf(user);
        ethBalance = address(user).balance;
    }

    ///@notice Calculate and return token amount from provided currency amount
    ///@param amount Amount of ETH or USDT - 18 or 6 decimals
    function getTokenAmountFromCurrencyAmount(uint256 amount, bool isEth) public view returns (uint256 tokens) {
        (uint256 currentStageTokens, uint256[] memory nextStageTokens) = _calculateTokenAmountFromCurrencyAmount(amount, isEth);
        tokens = currentStageTokens;
        for(uint256 i = 0; i < nextStageTokens.length; i++) {
            tokens += nextStageTokens[i];
        }
    }

    function _calculateTokenAmountFromCurrencyAmount(
        uint256 amount,
        bool isEth
    ) internal view returns (uint256 currentStageTokens, uint256[] memory tokensAmountNextStage) {
        Stage storage stage = stages[currentStage];
        uint256 _currentStage = currentStage;
        uint256 rateInCurrency = _getRateInCurrency(_currentStage, isEth); // calculate the number of tokens in 1 unit of the selected currency based on its current price (USDT or ETH)
        uint256 tokens = (amount * rateInCurrency) / (isEth ? 1e18 : 1e6); // calculate the number of tokens based on the passed amount of currency

        if (stage.tokensToSale - stage.tokensSold > tokens) {
            return (tokens, new uint256[](0));
        }
        if(_currentStage == stages.length - 1) {
            revert NotEnoughTokensInLastStage();
        }

        uint256 remainingTokens = stage.tokensToSale - stage.tokensSold;
        uint256 excessAmount = amount - (remainingTokens * (isEth ? 1e18 : 1e6)) / rateInCurrency;
        uint256 ex = excessAmount;
        uint256 stagesToUse;
        _currentStage++;
        for(uint256 i = _currentStage; i < stages.length; i++) {
            uint256 rate = _getRateInCurrency(i, isEth);
            if(ex >= stages[i].tokensToSale * (isEth ? 1e18 : 1e6) / rate) {
                ex -= stages[i].tokensToSale * (isEth ? 1e18 : 1e6) / rate;
                stagesToUse++;
            } else {
                stagesToUse++;
                break;
            }
        }
        tokensAmountNextStage = new uint256[](stagesToUse);
        for(uint256 i = 0; i < stagesToUse; i++) {
            uint256 nextRateInCurrency = _getRateInCurrency(_currentStage + i, isEth);
            uint256 nextAmount = (excessAmount * nextRateInCurrency) / (isEth ? 1e18 : 1e6);
            uint256 availableTokensInCurrentStage = stages[_currentStage + i].tokensToSale - stages[_currentStage + i].tokensSold;
            if(nextAmount > availableTokensInCurrentStage) {
                tokensAmountNextStage[i] = availableTokensInCurrentStage;
                excessAmount -= availableTokensInCurrentStage * (isEth ? 1e18 : 1e6) / nextRateInCurrency;
                if(i == stagesToUse - 1 && excessAmount > 1 ether) {
                    revert NotEnoughTokensInLastStage();
                }
            } else {
                tokensAmountNextStage[i] = nextAmount;
                break;
            }
        }
        return (remainingTokens, tokensAmountNextStage);
    }

    ///@notice Calculate and return currency amount from provided token amount
    ///@param tokens Amount of WW3SHIBA tokens
    ///@param isEth True if amount to return is in ETH, false if amount to return is in USDT
    function getCurrencyAmountFromTokenAmount(uint256 tokens, bool isEth) external view returns (uint256 amount) {
        Stage storage stage = stages[currentStage];
        uint256 rateInCurrency = _getRateInCurrency(currentStage, isEth); // calculate the number of tokens in 1 unit of the selected currency based on its current price (USDT or ETH)
        if (stage.tokensToSale - stage.tokensSold >= tokens) {
            amount = (tokens * (isEth ? 1e18 : 1e6)) / rateInCurrency; // calculate the amount of currency based on the passed number of tokens
            return amount;
        }

        uint256 _currentStage = currentStage;
        uint256 remainingTokens = stage.tokensToSale - stage.tokensSold;
        uint256 excessTokens = tokens - remainingTokens;
        uint256 ex = excessTokens;
        uint256 stagesToUse;
        for(uint256 i = _currentStage; i < stages.length; i++) {
            if(ex >= stages[i].tokensToSale) {
                ex -= stages[i].tokensToSale;
                stagesToUse++;
            } else {
                stagesToUse++;
                break;
            }
        }

        _currentStage++;
        amount = (remainingTokens * (isEth ? 1e18 : 1e6)) / rateInCurrency;
        for(uint256 i = 0; i < stagesToUse; i++) {
            uint256 nextRateInCurrency = _getRateInCurrency(_currentStage + i, isEth);
            uint256 availableTokensInCurrentStage = stages[_currentStage + i].tokensToSale - stages[_currentStage + i].tokensSold;
            if(excessTokens > availableTokensInCurrentStage) {
                amount += (availableTokensInCurrentStage * nextRateInCurrency) / (isEth ? 1e18 : 1e6);
                excessTokens -= availableTokensInCurrentStage;
                if(i == stagesToUse - 1 && excessTokens > 1 ether) {
                    revert NotEnoughTokensInLastStage();
                }
            } else {
                amount += (excessTokens * (isEth ? 1e18 : 1e6)) / nextRateInCurrency;
                break;
            }
        }
    }

    ///@notice Return ETH price from Chainlink
    function _getEthPrice() internal view returns (uint256) {
        (, int256 price, , , ) = priceFeedEth.latestRoundData();
        return uint256(price);
    }

    ///@notice Return USDT price from Chainlink
    function _getUsdtPrice() internal view returns (uint256) {
        (, int256 price, , , ) = priceFeedUsdt.latestRoundData();
        return uint256(price);
    }
}
