           	  uint256[] memory id = _id;
        require (teamReserve == false,"team already reserved"); //cannot mint more than once
        require(msg.sender == tx.origin, "Contract call");
        require(totalSupply() + reserveSupply <= MAX_SUPPLY,"Exceeded total supply");
                      userToTokensMinted[to] = reserveSupply;
                      teamReserve = true;
                      uint8 _index = 0;
            //loop through multiple random token mint
         for(uint256 i = 1; i <= reserveSupply; i++){
            	uri = string.concat(baseTokenUri, Strings.toString(id[_index]), ".json");
          		mintAction(uri);
	        	    _index++; 
         }
         emit reserveMinted(reserveSupply,to);
    }

      
     
     function whitelistMint(uint256 amount,bytes32 _key,uint256[] calldata _id) external payable {
       uint256 whitelistPrice = whitelistFee * amount;
         require(msg.value == whitelistPrice,"Incorrect mint fee");
        require(msg.sender == tx.origin, "Contract call");
                  userToTokensMinted[msg.sender] += amount;
        require(totalSupply() + amount <= MAX_SUPPLY,"Low supply");
        require(amount <= maxUserMintLimit && userToTokensMinted[msg.sender] <= maxUserMintLimit, "User limit reached");
        bytes32 key =keccak256(abi.encode(_key));
          uint256[] memory id = _id;
         uint8 _index = 0;
     for(uint256 i = 1; i <= amount; i++){
       uri = string.concat(baseTokenUri, Strings.toString(id[_index]), ".json");
        if(publicSale == false && key == V_hash){
		      	mintAction(uri);
		        _index++; 
	    	}
        else if (publicSale) {
			          mintAction(uri);
		          	_index++; 
        }
        else{
          revert unauthorizedAccess();
        }

      } 
                 emit whitelistMinted(amount,whitelistPrice);

    }


