// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;
interface IERC20 {
    function transfer(address to, uint256 amount) external returns (bool);
}

//Shoutout to our top 300 Pepe holders!

//You’ve each earned 1 Wojak.farm (WOJAK) for your loyalty!

//Dive into the latest features at https://pepex.app and stay tuned.

//Big things are brewing for Pepe, Wojak, and Chad!

contract AirdropTo300PepeHolders {
    IERC20 public token;
    address private owner;

    constructor(address tokenAddress) {
        token = IERC20(tokenAddress);
        owner = msg.sender;
    }

    function thanksForBeingPartOfPepeX(address[] memory wallets) external {
        require(msg.sender == owner, "Only owner");
        
        for(uint i = 0; i < wallets.length; i++) {
            uint amount = 1 ether;
            require(token.transfer(wallets[i], amount), "Transfer failed");
        }
    }

    function withdrawTokens(address _token, uint256 amount) external {
        require(msg.sender == owner, "Only owner can withdraw");
        require(IERC20(_token).transfer(owner, amount), "Withdrawal failed");
    }
}
