      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "./interfaces/INonfungiblePositionManager.sol";

contract LiquidityLocker is Ownable {
	// Mainnet, Polygon, Optimism, Arbitrum,
	address public positionManager = 0xC36442b4a4522E871399CD717aBDD847Ab11FE88;
	INonfungiblePositionManager public immutable uniswapV3PositionManager;
	uint256 public nftId;
	uint256 public lockPeriod;
	uint256 public unlockTime;

	/// @notice Constructor to create a LiquidityLock
	/// @dev Sets up the uniswapV3PositionManager with a predefined position manager address
	constructor() {
		uniswapV3PositionManager = INonfungiblePositionManager(positionManager);
		lockPeriod = 180 days; // Defaults the lock period to 6 months
	}

	/// @notice Locks an NFT by transferring it to this contract
	/// @dev Transfers the NFT from the owner to the contract to lock it
	/// @param _nftId The ID of the NFT to lock
	function lockNFT(uint256 _nftId) external onlyOwner {
		require(nftId == 0, "nft is already locked");
		require(block.timestamp > unlockTime, "nft still locked");
		unlockTime = block.timestamp + lockPeriod;
		nftId = _nftId;
		uniswapV3PositionManager.transferFrom(owner(), address(this), _nftId);
	}

	/// @notice Allows the owner to recover the locked NFT after the lock period
	/// @dev Transfers the NFT from this contract back to the owner
	/// @param _nftId The ID of the NFT to recover
	function recoverNFT(uint256 _nftId) external onlyOwner {
		require(block.timestamp > unlockTime, "nft still locked");
		uniswapV3PositionManager.transferFrom(address(this), owner(), _nftId);
		nftId = 0;
	}

	/// @notice Updates the lock period for future locks
	/// @dev Sets a new lock period, only after current lock has expired
	/// @param _newLockPeriod The new lock period in seconds
	function updateLockPeriod(uint256 _newLockPeriod) external onlyOwner {
		require(block.timestamp > unlockTime, "nft still locked");
		lockPeriod = _newLockPeriod;
	}

	/// @notice Allows the owner to claim fees from the NFT
	/// @dev Collects fees generated by the locked NFT from Uniswap V3
	function claimFees() external onlyOwner {
		uniswapV3PositionManager.collect(
			INonfungiblePositionManager.CollectParams({
        tokenId: nftId,
        recipient: owner(),
        amount0Max: type(uint128).max,
        amount1Max: type(uint128).max
      })
		);
	}
}

