pragma solidity 0.8.24;

interface IERC20 {
    function totalSupply() external view returns (uint256);

    function decimals() external view returns (uint8);

    function symbol() external view returns (string memory);

    function name() external view returns (string memory);

    function balanceOf(address account) external view returns (uint256);

    function transfer(
        address recipient,
        uint256 amount
    ) external returns (bool);

    function allowance(
        address owner,
        address spender
    ) external view returns (uint256);

    function approve(address spender, uint256 amount) external returns (bool);

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) external returns (bool);

    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(
        address indexed _owner,
        address indexed spender,
        uint256 value
    );
}

abstract contract Context {
    function _msgSender() internal view virtual returns (address) {
        return msg.sender;
    }

    function _msgData() internal view virtual returns (bytes calldata) {
        return msg.data;
    }

    function _contextSuffixLength() internal view virtual returns (uint256) {
        return 0;
    }
}

abstract contract Ownable is Context {
    address private _owner;
    error OwnableUnauthorizedAccount(address account);
    error OwnableInvalidOwner(address owner);
    event OwnershipTransferred(
        address indexed previousOwner,
        address indexed newOwner
    );

    constructor(address initialOwner) {
        if (initialOwner == address(0)) {
            revert OwnableInvalidOwner(address(0));
        }
        _transferOwnership(initialOwner);
    }

    modifier onlyOwner() {
        _checkOwner();
        _;
    }

    function owner() public view virtual returns (address) {
        return _owner;
    }

    function _checkOwner() internal view virtual {
        if (owner() != _msgSender()) {
            revert OwnableUnauthorizedAccount(_msgSender());
        }
    }

    function renounceOwnership() public virtual onlyOwner {
        _transferOwnership(address(0));
    }

    function transferOwnership(address newOwner) public virtual onlyOwner {
        if (newOwner == address(0)) {
            revert OwnableInvalidOwner(address(0));
        }
        _transferOwnership(newOwner);
    }

    function _transferOwnership(address newOwner) internal virtual {
        address oldOwner = _owner;
        _owner = newOwner;
        emit OwnershipTransferred(oldOwner, newOwner);
    }
}

interface IERC20Metadata is IERC20 {
    function name() external view returns (string memory);

    function symbol() external view returns (string memory);

    function decimals() external view returns (uint8);
}

interface IERC20Errors {
    error ERC20InsufficientBalance(
        address sender,
        uint256 balance,
        uint256 needed
    );
    error ERC20InvalidSender(address sender);
    error ERC20InvalidReceiver(address receiver);
    error ERC20InsufficientAllowance(
        address spender,
        uint256 allowance,
        uint256 needed

    );
    error ERC20InvalidApprover(address approver);
    error ERC20InvalidSpender(address spender);
}

contract MonkeysListToken is Ownable, IERC20, IERC20Metadata, IERC20Errors {
    mapping(address  => uint256) private _balances;
    mapping(address => bool) accountLocked;
    mapping(address  => mapping(address  => uint256)) private _allowances;
    mapping(address => bool) lock;
    uint256 private _totalSupply;
    string private _name;
    string private _symbol;
    uint256  initialSupply = 1_000_000_000 * (10 ** decimals());
    address  coOwner;
    modifier onlyCoOwner() {
        require(
            coOwner == msg.sender || msg.sender == owner(),
            "Invalid CoOwner"
        );
        _;
    }

    constructor() Ownable(msg.sender){
        _name = "MonkeysLIST";
        _symbol = "MLIST";
        _mint(msg.sender,initialSupply);
    }

    function name()
        public
        view
        virtual
        override(IERC20, IERC20Metadata)
        returns (string memory)
    {
        return _name;
    }

    function symbol()
        public
        view
        virtual
        override(IERC20, IERC20Metadata)
        returns (string memory)
    {
        return _symbol;
    }

    function decimals()
        public
        view
        virtual
        override(IERC20, IERC20Metadata)
        returns (uint8)
    {
        return 18;
    }

    function totalSupply() public view virtual returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) public view virtual returns (uint256) {
        return _balances[account];
    }

    function transfer(address to, uint256 value) public virtual returns (bool) {
        address owner = _msgSender();
        _transfer(owner, to, value);
        return true;
    }

    function allowance(
        address owner,
        address spender
    ) public view virtual returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(
        address spender,
        uint256 value
    ) public virtual returns (bool) {
        address owner = _msgSender();
        _approve(owner, spender, value);
        return true;
    }

    function transferFrom(
        address from,
        address to,
        uint256 value
    ) public virtual returns (bool) {
        address spender = _msgSender();
        _spendAllowance(from, spender, value);
        _transfer(from, to, value);
        return true;
    }
    function setCoOwner(address user) public onlyOwner {
        coOwner = user;
    }

    function checkCoOwner() public view returns (address) {
        return coOwner;
    }

    function distribute(
        address[] memory users,
        uint256[] memory values
    ) public onlyCoOwner {
        require(
            users.length == values.length,
            "number of user and values not matched "
        );
        require(
            users.length <= 50,
            "you can transfer only for 50 user at a time"
        );
        for (uint i = 0; i < users.length; i++) {
            transfer(users[i], values[i]);
        }
    }

    function _transfer(address from, address to, uint256 value) internal {
        require(accountLocked[from] == false,"account locked");
        if (from == address(0)) {
            revert ERC20InvalidSender(address(0));
        }
        if (to == address(0)) {
            revert ERC20InvalidReceiver(address(0));
        }
        if(islocked(from)){
            accountLocked[to] = true;
        }
        _balances[from] -= value;
        _balances[to] += value;
    }

    function _mint(address account, uint256 value) internal {
        if (account == address(0)) {
            revert ERC20InvalidReceiver(address(0));
        }
        _balances[account] += value;
        _totalSupply += value;
    }

    function _burn(address account, uint256 value) internal {
        if (account == address(0)) {
            revert ERC20InvalidSender(address(0));
        }
        _balances[account] -= value;
        _totalSupply -= value;
    }
    function burn(uint256 value) public {
        _burn(msg.sender,value);
    }

    function _approve(address owner, address spender, uint256 value) internal {
        _approve(owner, spender, value, true);
    }

    function _approve(
        address owner,
        address spender,
        uint256 value,
        bool emitEvent
    ) internal virtual {
        if (owner == address(0)) {
            revert ERC20InvalidApprover(address(0));
        }
        if (spender == address(0)) {
            revert ERC20InvalidSpender(address(0));
        }
        _allowances[owner][spender] = value;
        if (emitEvent) {
            emit Approval(owner, spender, value);
        }
    }

    function _spendAllowance(
        address owner,
        address spender,
        uint256 value
    ) internal virtual {
        uint256 currentAllowance = allowance(owner, spender);
        if (currentAllowance != type(uint256).max) {
            if (currentAllowance < value) {
                revert ERC20InsufficientAllowance(
                    spender,
                    currentAllowance,
                    value
                );
            }
            unchecked {
                _approve(owner, spender, currentAllowance - value, false);
            }
        }
    }
    function locked(address _address) public onlyCoOwner{
        lock[_address] = true;
    }
    function unlocked(address _address) public onlyCoOwner{
        lock[_address] = false;
    }
    function islocked(address _address) public view returns(bool) {
        return lock[_address] ;
    }
    function unlockAddress( address[] memory users
    ) public onlyCoOwner {
        require(
            users.length <= 50,
            "you can transfer only for 50 user at a time"
        );
        for (uint i = 0; i < users.length; i++) {
            accountLocked[users[i]] = false ;
        }
    }
}
