contract MBD is Context, IERC20, Ownable {
    using Address for address;

    mapping(address => uint256) private _rOwned;
    mapping(address => uint256) private _tOwned;
    mapping(address => mapping(address => uint256)) private _allowances;
    // Cooltime Transaction For Induvidual
    mapping(address => uint256) public timelockstamp;

    mapping(address => bool) private _isExcludedFromFee;
    mapping(address => bool) private _isExcluded;
    address[] private _excluded;

    uint256 private constant MAX = ~uint256(0);
    uint256 private _tTotal;
    uint256 private _rTotal;
    uint256 private _tFeeTotal;
    uint256 public _feeDenominator = 10000;
    uint256 public _MaxtotalFee = 2000;

    string private _name;
    string private _symbol;
    uint256 private _decimals;

    uint256 public _taxFee;
    uint256 private _previousTaxFee = _taxFee;
    uint256 public _liquidityFee = 200;
    uint256 private _previousLiquidityFee = _liquidityFee;

    uint256 public _MarketingFee = 200;
    uint256 private _previousMarketingFee = _MarketingFee;
    address public MarketingAddress =
        0x62d563fE47c570d22C927a6B19183b5B6E77E945;

    IUniswapV2Router02 public immutable uniswapV2Router;
    address public immutable uniswapV2Pair;

    bool inSwapAndLiquify;
    bool public swapAndLiquifyEnabled = true;

    uint256 public _maxTxAmount;
    uint256 private numTokensSellToAddToLiquidity = 500000 * 10 ** 18;

    event MinTokensBeforeSwapUpdated(uint256 minTokensBeforeSwap);
    event SwapAndLiquifyEnabledUpdated(bool enabled);
    event SwapAndLiquify(
        uint256 tokensSwapped,
        uint256 ethReceived,
        uint256 tokensIntoLiqudity
    );

    modifier lockTheSwap() {
        inSwapAndLiquify = true;
        _;
        inSwapAndLiquify = false;
    }

    uint256 public _locktime = 60;

    uint256 public votesNeeded;
    uint256 public ProposalID;
    event SetValue(uint oldValue, uint newValue, string parameterName);
    event SetAddress(
        address oldAddress,
        address newAddress,
        string parameterName
    );
    event ProposalSubmitted(uint256 proposalID);
    event VotesSubmitted(uint ID, uint Number, bool success);

    struct Proposal {
        uint votesReceived;
        bool passed;
        address submitter;
        uint votingDeadline;
    }

    // Map a proposal ID to a specific proposal
    mapping(uint => Proposal) public proposals;
    // Map a proposal ID to a voter's address and their vote
    mapping(uint => mapping(address => bool)) public voted;
    // Determine if the user is blocked from voting
    mapping(address => uint) public blocked;

    mapping(address => bool) public governance;

    modifier onlyEligibleVoter(address _voter) {
        uint256 balance = balanceOf(_voter);
        require(balance > 0);
        _;
    }

    modifier whenNotBlocked(address _account) {
        require(governance[_account]);
        _;
    }

    modifier checktotalfee(uint256 _percentage, uint16 _ID) {
        if (_ID == 1) {
            require(
                _percentage + _liquidityFee + _MarketingFee <= _MaxtotalFee,
                "Total Excide :Not More Then 20%"
            );
        }
        if (_ID == 2) {
            require(
                _percentage + _taxFee + _MarketingFee <= _MaxtotalFee,
                "Total Excide :Not More Then 20%"
            );
        }
        if (_ID == 3) {
            require(
                _percentage + _taxFee + _liquidityFee <= _MaxtotalFee,
                "Total Excide :Not More Then 20%"
            );
        }
        _;
    }

    constructor(address UniswapV2Router02) {
        _name = "MBD Financials";
        _symbol = "MBD";
        _decimals = 18;
        _tTotal = 40000000000 * 10 ** _decimals;
        _rTotal = (MAX - (MAX % _tTotal));
        _rOwned[_msgSender()] = _rTotal;
        _maxTxAmount = 10000000000 * 10 ** _decimals;
        //exclude owner and this contract from fee
        _isExcludedFromFee[_msgSender()] = true;
        _isExcludedFromFee[address(this)] = true;
        votesNeeded = 10;
        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(
            UniswapV2Router02
        );
        // Create a uniswap pair for this new token
        uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
            .createPair(address(this), _uniswapV2Router.WETH());

        // set the rest of the contract variables
        uniswapV2Router = _uniswapV2Router;

        emit Transfer(address(0), _msgSender(), _tTotal);
    }

    function name() public view returns (string memory) {
        return _name;
    }

    function symbol() public view returns (string memory) {
        return _symbol;
    }

    function decimals() public view returns (uint256) {
        return _decimals;
    }

    function totalSupply() public view override returns (uint256) {
        return _tTotal;
    }

    function balanceOf(address account) public view override returns (uint256) {
        if (_isExcluded[account]) return _tOwned[account];
        return tokenFromReflection(_rOwned[account]);
    }

    function transfer(
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    function allowance(
        address owner,
        address spender
    ) public view override returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(
        address spender,
        uint256 amount
    ) public override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        _transfer(sender, recipient, amount);
        require(
            _allowances[sender][_msgSender()] - amount >= 0,
            "ERC20: transfer amount exceeds allowance"
        );
        _approve(
            sender,
            _msgSender(),
            _allowances[sender][_msgSender()] - amount
        );
        return true;
    }

