    "contracts/bridging/hybrid/MainNetworkBridgeGateway.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {ReentrancyGuard} from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import {AccessControlEnumerable} from "@openzeppelin/contracts/access/extensions/AccessControlEnumerable.sol";

import {Client} from "@chainlink/contracts-ccip/src/v0.8/ccip/libraries/Client.sol";

import {CCIPClient} from "../../ccip/CCIPClient.sol";
import {ServiceFeeable} from "../../service_feeable/ServiceFeeable.sol";

contract MainNetworkBridgeGatewayHybrid is AccessControlEnumerable, ServiceFeeable, CCIPClient, ReentrancyGuard {

    bytes32 public constant BRIDGE_AUTOMATOR = keccak256("BRIDGE_AUTOMATOR");

    bool ccipBridgingEnabled;
    bool customBridgingEnabled;

    IERC20 public token;

    event BridgeCustom(address indexed sender, address indexed recipient, uint64 indexed chainId, uint256 amount);
    event UnbridgeCustom(address indexed recipient, uint64 indexed chainId, uint256 amount);
    event BridgeCcip(address indexed sender, address indexed recipient, uint64 indexed chainId, uint256 amount);
    event UnbridgeCcip(address indexed recipient, uint64 indexed chainId, uint256 amount);

    error CcipBridgingDisabled();
    error CustomBridgingDisabled();

    modifier ccipEnabled() {
        if(!ccipBridgingEnabled) {
            revert CcipBridgingDisabled();
        }
        _;
    }

    modifier customEnabled() {
        if(!customBridgingEnabled) {
            revert CustomBridgingDisabled();
        }
        _;
    }

    constructor(
        address _tokenAddress,
        address _ccipRouter,
        address _serviceFeeRecipient,
        uint256 _serviceFee,
        uint256 _minServiceAmount
    ) CCIPClient(_ccipRouter, address(0)) 
      ServiceFeeable(_serviceFeeRecipient, _serviceFee, _minServiceAmount) {

        token = IERC20(_tokenAddress);
        ccipBridgingEnabled = false;
        customBridgingEnabled = false;

        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }

    // #### ADMINISTRATION ####
    function supportsInterface(bytes4 interfaceId) public pure virtual override(AccessControlEnumerable, CCIPClient) returns (bool) {
        return CCIPClient.supportsInterface(interfaceId);
    }

    function setCcipBridgingEnabled(bool status) external  onlyRole(DEFAULT_ADMIN_ROLE) {
        ccipBridgingEnabled = status;
    }

    function setCustomBridgingEnabled(bool status) external  onlyRole(DEFAULT_ADMIN_ROLE) {
        customBridgingEnabled = status;
    }

    // #### CUSTOM BRIDGING ####

    function bridgeTokenCustom(
        address recipient,
        uint256 amount,
        uint64 targetNetwork
    )
        external
        payable
        onlySupportedNetwork(targetNetwork)
        customEnabled
        gteMinAmount(amount)
        collectsServiceFee
        nonReentrant
    {
        token.transferFrom(msg.sender, address(this), amount);
        emit BridgeCustom(msg.sender, recipient, targetNetwork, amount);
    }

    function unbridgeTokenCustom(
        uint64 sourceNetwork,
        address recipient,
        uint256 amount
    ) external onlyRole(BRIDGE_AUTOMATOR) onlySupportedNetwork(sourceNetwork) {
        token.transfer(recipient, amount);
        emit UnbridgeCustom(recipient, sourceNetwork, amount);
    }

    // #### CCIP BRIDGING ####

    /**
    * request CCIP messaging fee for bridging
    */
    function estimateCcipFee(uint64 targetNetwork) external view onlySupportedNetwork(targetNetwork) returns (uint256) {
        bytes memory dummyMessage = abi.encode(address(0), uint256(0));
        return CCIPClient.estimateMessageFee(uint64(targetNetwork), dummyMessage);
    }

    /**
     * Bridge the specified token amount to a supported target network.
     */
    function bridgeTokenCcip(
        address recipient,
        uint256 amount,
        uint64 targetNetwork
    )
        external
        payable
        onlySupportedNetwork(targetNetwork)
        ccipEnabled
        nonReentrant
    {
        token.transferFrom(msg.sender, address(this), amount);
        emit BridgeCcip(msg.sender, recipient, targetNetwork, amount);
        CCIPClient.sendCcipMessage(targetNetwork, ccipNetworkToPartnerContract[targetNetwork], 
            abi.encode(recipient, amount));
    }

    /**
     * Handle receiving ccip message with an unbridging/unwrapping request and release the locked tokens to the recipient.
     */
    function _ccipReceive(Client.Any2EVMMessage memory ccipMessage) internal override onlySupportedNetwork(ccipMessage.sourceChainSelector) {

        // check if sender is the correct partner contract of the corresponding source network
        address ccipSender = abi.decode(ccipMessage.sender, (address));
        if(ccipSender != ccipNetworkToPartnerContract[ccipMessage.sourceChainSelector]) {
            revert IncorrectPartnerContract(ccipMessage.sourceChainSelector, ccipNetworkToPartnerContract[ccipMessage.sourceChainSelector], ccipSender);
        }

        // decode message content
        (address recipient, uint256 amount) = abi.decode(ccipMessage.data, (address, uint256));

        // unbridge token
        token.transfer(recipient, amount);
        emit UnbridgeCcip(recipient, ccipMessage.sourceChainSelector, amount);
    }
}
"
    },
    "contracts/ccip/CCIPClient.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {IERC165} from "@openzeppelin/contracts/utils/introspection/IERC165.sol";
import {AccessControlEnumerable} from "@openzeppelin/contracts/access/extensions/AccessControlEnumerable.sol";
import {IAccessControlEnumerable} from "@openzeppelin/contracts/access/extensions/IAccessControlEnumerable.sol";


import {IAny2EVMMessageReceiver} from "@chainlink/contracts-ccip/src/v0.8/ccip/interfaces/IAny2EVMMessageReceiver.sol";
import {IRouterClient} from "@chainlink/contracts-ccip/src/v0.8/ccip/interfaces/IRouterClient.sol";
import {Client} from "@chainlink/contracts-ccip/src/v0.8/ccip/libraries/Client.sol";

abstract contract CCIPClient is IAny2EVMMessageReceiver, IERC165, AccessControlEnumerable {

    IRouterClient public ccipRouter;
    IERC20 public ccipFeeToken; // if fee token is address(0), fees will be payed with native coin
    uint256 public ccipMessageGasLimit;

    mapping(uint64 chainId => bool) public isSupportedCcipNetwork; // mapping of supported partner networks to which the token can be bridged
    mapping(uint64 chainId => address) public ccipNetworkToPartnerContract; // mapping of network to address of token on that network

    error CcipFeeTokenBalanceTooLow(address token, uint256 balance, uint256 requiredFee);
    error InvalidRouter(address routerAddress);
    error UnsupportedNetwork(uint64 chainId);
    error IncorrectPartnerContract(uint64 chainId, address expected, address actual);

    event CcipMessageSent(uint64 indexed targetChainSelector, bytes32 messageId);
    event CcipMessageReceived(uint64 indexed sourceChainSelector, bytes32 messageId);

    //only calls from the set router are accepted.
    modifier onlyRouter() {
        if (msg.sender != address(ccipRouter)) revert InvalidRouter(msg.sender);
        _;
    }

    modifier onlySupportedNetwork(uint64 chainId) {
        if (!isSupportedCcipNetwork[chainId]) {
            revert UnsupportedNetwork(chainId);
        }
        _;
    }

    constructor(address _router, address _feeToken) {
        ccipRouter = IRouterClient(_router);
        ccipFeeToken = IERC20(_feeToken);
        ccipMessageGasLimit = 200_000;
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }


    function supportsInterface(bytes4 interfaceId) public pure virtual override(AccessControlEnumerable, IERC165) returns (bool) {
        return interfaceId == type(IAny2EVMMessageReceiver).interfaceId 
            || interfaceId == type(IERC165).interfaceId
            || interfaceId == type(AccessControlEnumerable).interfaceId;
    }

    // ### CCIP MESSAGING ADMINISTRATION ###
    function setCcipRouter(address _router) external onlyRole(DEFAULT_ADMIN_ROLE) {
        ccipRouter = IRouterClient(_router);
    }

    function setCcipGasLimit(uint256 _gasLimit) external onlyRole(DEFAULT_ADMIN_ROLE) {
        ccipMessageGasLimit = _gasLimit;
    } 

    function setCcipFeeToken(address _feeToken) external onlyRole(DEFAULT_ADMIN_ROLE) {
        ccipFeeToken = IERC20(_feeToken);
    }

    /**
     * Specificy which networks have a counterpart of this bridge gateway
     * and thus are able to bridge the asset.
     */
    function setSupportedNetwork(
        uint64 chainId,
        bool supported
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        isSupportedCcipNetwork[chainId] = supported;
    }

    /**
     * Specificy the token address on a supported network, which is 
     * assumed to be the receiver for any ccip messaging to that 
     * network.
     */
    function setSupportedNetworkReceiverAddress(
        uint64 chainId,
        address receiverAddress
    ) external onlySupportedNetwork(chainId) onlyRole(DEFAULT_ADMIN_ROLE) {
        ccipNetworkToPartnerContract[chainId] = receiverAddress;
    }

    // ### CCIP SENDER FUNCTIONALITY ### 

    function createCcipMessage(address receiver, bytes memory message) private view returns (Client.EVM2AnyMessage memory) {

        Client.EVM2AnyMessage memory ccipMessage = Client.EVM2AnyMessage({
            receiver: abi.encode(receiver),
            data: message,
            tokenAmounts: new Client.EVMTokenAmount[](0), // empty array indicating no tokens are being sent
            extraArgs: Client._argsToBytes( 
                // Additional arguments, setting gas limit and non-strict sequencing mode
                Client.EVMExtraArgsV1({gasLimit: ccipMessageGasLimit, strict: false})
            ),
            feeToken: address(ccipFeeToken)
        });
        return ccipMessage;
    }

    function estimateMessageFee(uint64 chainSelector, bytes memory message) internal view returns (uint256) {
        Client.EVM2AnyMessage memory ccipMessage = createCcipMessage(address(0), message);
        return ccipRouter.getFee(chainSelector, ccipMessage);
    }

    function sendCcipMessage(uint64 chainSelector, address receiver, bytes memory message) internal returns (bytes32) {

        // Create an EVM2AnyMessage struct in memory with necessary information for sending a cross-chain message
        Client.EVM2AnyMessage memory ccipMessage = createCcipMessage(receiver, message);

        // query required fee
        uint256 fee = ccipRouter.getFee(chainSelector, ccipMessage);

        if(address(ccipFeeToken) == address(0)) {
            // fee payment via native coin
            if (fee > address(this).balance) {
                revert CcipFeeTokenBalanceTooLow(address(0), address(this).balance, fee);
            }
            else {
                // send message
                bytes32 messageId = ccipRouter.ccipSend{value: fee}(chainSelector, ccipMessage);
                emit CcipMessageSent(chainSelector, messageId);
                return messageId;
            }
        }
        else {
            // fee payment via erc20 token
            if (fee > ccipFeeToken.balanceOf(address(this))) {
                revert CcipFeeTokenBalanceTooLow(address(ccipFeeToken), ccipFeeToken.balanceOf(address(this)), fee);
            }
            else {
                ccipFeeToken.approve(address(ccipRouter), fee);
                // send message
                bytes32 messageId = ccipRouter.ccipSend(chainSelector, ccipMessage);
                emit CcipMessageSent(chainSelector, messageId);
                return messageId;
            }
        }
    }

    // ### CCIP RECEIVER FUNCTIONALITY ### 

    function ccipReceive(Client.Any2EVMMessage calldata message) external virtual override onlyRouter {
        emit CcipMessageReceived(message.sourceChainSelector, message.messageId);
        _ccipReceive(message);
    }

    /// @notice Override this function in your implementation.
    function _ccipReceive(Client.Any2EVMMessage memory message) internal virtual;
}
"
