    "contracts/service_feeable/ServiceFeeable.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import {AccessControlEnumerable} from "@openzeppelin/contracts/access/extensions/AccessControlEnumerable.sol";

abstract contract ServiceFeeable is AccessControlEnumerable {

    address serviceFeeRecipient; // reciepient of service fees
    uint256 public serviceFee; // the service fee amount for triggering a service
    uint256 public minServiceAmount; // a minimum amount that acts as a guard to avoid service spamming

    error MinimumServiceAmountNotReached(uint256 actual, uint256 required);
    error ServiceFeeRequired(uint256 actual, uint256 required);

    modifier collectsServiceFee() {
        if (msg.value < serviceFee) {
            revert ServiceFeeRequired(msg.value, serviceFee);
        }
        payable(serviceFeeRecipient).transfer(msg.value);
        _;
    }

    modifier gteMinAmount(uint256 amount) {
        if (amount < minServiceAmount) {
            revert MinimumServiceAmountNotReached(amount, minServiceAmount);
        }
        _;
    }

    constructor (address _serviceFeeRecipient, uint256 _serviceFee, uint256 _minServiceAmount) {
        serviceFeeRecipient = _serviceFeeRecipient;
        serviceFee = _serviceFee;
        minServiceAmount = _minServiceAmount;

        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
