      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import "@openzeppelin/contracts/interfaces/IERC20.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";

/// @title DystoVesting Contract
/// @notice Manages the vesting of tokens for various beneficiaries with different vesting schedules.
contract DystoVesting is Initializable, OwnableUpgradeable {
    /// @dev Stores vesting details for each beneficiary.
    struct Beneficiary {
        uint256 initialRelease;
        uint256 vestedAmount;
        uint256 vestingStartTime;
        uint256 vestingEndTime;
        uint256 monthlyUnlockPercentage;
        uint256 lastClaimTime;
    }

    /// @notice Emitted when a new beneficiary is initialized
    event BeneficiaryInitialized(
        address indexed beneficiary,
        uint256 initialRelease,
        uint256 vestedAmount,
        uint256 vestingStartTime,
        uint256 vestingEndTime,
        uint256 monthlyUnlockPercentage
    );

    /// @notice Stores vesting information for beneficiaries with linear vesting schedule
    mapping(address => Beneficiary) public linearVestingBeneficiaries;

    /// @notice Stores vesting information for beneficiaries with end-of-month vesting schedule
    mapping(address => Beneficiary) public eomVestingBeneficiaries;

    /// @notice Address of the token being vested
    address public dystoTokenAddress;

    /// @notice Address used to verify signatures
    address public signerAddress;

    /// @notice Timestamp of the token generation event
    uint256 public tgeTimestamp;

    /// @notice Initializes the vesting contract
    /// @dev This function sets up initial state including the token address, signer address, and TGE timestamp.
    /// @param _dystoTokenAddress The token address to be vested
    /// @param _signerAddress The address used to verify signatures
    /// @param _tgeTimestamp The timestamp of the token generation event
    function initialize(address _dystoTokenAddress, address _signerAddress, uint256 _tgeTimestamp) public initializer {
        __Ownable_init();
        dystoTokenAddress = _dystoTokenAddress;
        signerAddress = _signerAddress;
        tgeTimestamp = _tgeTimestamp;
    }

    /// @notice Initializes vesting details for a beneficiary
    /// @dev Validates the signature and sets up vesting parameters in the appropriate vesting schedule mapping
    /// @param initialRelease Tokens available for immediate release upon TGE
    /// @param vestedAmount Total amount of tokens to be vested over the period
    /// @param vestingStartTime Start time of the vesting period
    /// @param vestingEndTime End time of the vesting period
    /// @param monthlyUnlockPercentage Percentage of tokens unlocked monthly (used for EOM vesting)
    /// @param signature Signature for verification
    function initializeBeneficiary(
        uint256 initialRelease,
        uint256 vestedAmount,
        uint256 vestingStartTime,
        uint256 vestingEndTime,
        uint256 monthlyUnlockPercentage,
        bytes memory signature
    ) public {
        bytes32 hashhash = keccak256(
            abi.encodePacked(
                msg.sender,
                initialRelease,
                vestedAmount,
                vestingStartTime,
                vestingEndTime,
                monthlyUnlockPercentage
            )
        );
        bytes32 signedHash = keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", hashhash));

        require(signerAddress == ECDSA.recover(signedHash, signature), "Invalid signature");

        // Determine the mapping to use
        mapping(address => Beneficiary) storage beneficiaries = monthlyUnlockPercentage > 0
            ? eomVestingBeneficiaries
            : linearVestingBeneficiaries;

        // Check if the beneficiary is already initialized in the selected mapping
        require(
            beneficiaries[msg.sender].vestingStartTime == 0,
            "Beneficiary already initialized in the selected mapping"
        );

        Beneficiary storage beneficiary = beneficiaries[msg.sender];
        beneficiary.initialRelease = initialRelease;
        beneficiary.vestedAmount = vestedAmount;
        beneficiary.vestingStartTime = vestingStartTime;
        beneficiary.vestingEndTime = vestingEndTime;
        beneficiary.monthlyUnlockPercentage = monthlyUnlockPercentage;
        beneficiary.lastClaimTime = vestingStartTime;

        emit BeneficiaryInitialized(
            msg.sender,
            initialRelease,
            vestedAmount,
            vestingStartTime,
            vestingEndTime,
            monthlyUnlockPercentage
        );
    }

    /// @notice Checks if a beneficiary is initialized in the linear vesting schedule
    /// @param beneficiaryAddress The address of the beneficiary to check
    /// @return true if initialized, false otherwise
    function isLinearBeneficiaryInitialized(address beneficiaryAddress) public view returns (bool) {
        return linearVestingBeneficiaries[beneficiaryAddress].vestingStartTime != 0;
    }

    /// @notice Checks if a beneficiary is initialized in the EOM vesting schedule
    /// @param beneficiaryAddress The address of the beneficiary to check
    /// @return true if initialized, false otherwise
    function isEOMBeneficiaryInitialized(address beneficiaryAddress) public view returns (bool) {
        return eomVestingBeneficiaries[beneficiaryAddress].vestingStartTime != 0;
    }

    /// @notice Allows a beneficiary to claim their vested tokens
    /// @dev Calculates claimable tokens, updates state, and transfers tokens
    function claim() external {
        Beneficiary storage linearVestingBeneficiary = linearVestingBeneficiaries[msg.sender];
        Beneficiary storage eomVestingBeneficiary = eomVestingBeneficiaries[msg.sender];

        if (linearVestingBeneficiary.lastClaimTime < linearVestingBeneficiary.vestingStartTime)
            linearVestingBeneficiary.lastClaimTime = linearVestingBeneficiary.vestingStartTime;

        uint256 unlockedTokens = calculateClaimableTokens(msg.sender);

        require(
            linearVestingBeneficiary.initialRelease > 0 ||
                eomVestingBeneficiary.initialRelease > 0 ||
                unlockedTokens > 0,
            "You are not a beneficiary or you have already claimed all tokens"
        );

        linearVestingBeneficiary.lastClaimTime = block.timestamp;
        linearVestingBeneficiary.initialRelease = 0; // Mark initial release as claimed

        eomVestingBeneficiary.lastClaimTime = block.timestamp;
        eomVestingBeneficiary.initialRelease = 0; // Mark initial release as claimed

        IERC20 token = IERC20(dystoTokenAddress);
        token.transfer(msg.sender, unlockedTokens);
    }

    /// @notice Calculates the total amount of tokens a beneficiary can claim at the current time
    /// @dev Combines calculations for linear and EOM vesting schedules
    /// @param beneficiaryAddress The address of the beneficiary to calculate for
    /// @return claimable Total tokens that are claimable by the beneficiary
    function calculateClaimableTokens(address beneficiaryAddress) public view returns (uint256) {
        Beneficiary storage linearVestingBeneficiary = linearVestingBeneficiaries[beneficiaryAddress];
        Beneficiary storage eomVestingBeneficiary = eomVestingBeneficiaries[beneficiaryAddress];

        uint256 currentTime = block.timestamp;
        uint256 claimable = linearVestingBeneficiary.initialRelease + eomVestingBeneficiary.initialRelease;

        // Calculate linear vesting claimable amount
        if (
            currentTime > linearVestingBeneficiary.lastClaimTime &&
            linearVestingBeneficiary.lastClaimTime < linearVestingBeneficiary.vestingEndTime
        ) {
            uint256 timePassed = currentTime - linearVestingBeneficiary.lastClaimTime;
            if (currentTime >= linearVestingBeneficiary.vestingEndTime) {
                claimable += linearVestingBeneficiary.vestedAmount;

                uint256 oldTimePassed = (linearVestingBeneficiary.lastClaimTime -
                    linearVestingBeneficiary.vestingStartTime);
                uint256 alreadyClaimed = (linearVestingBeneficiary.vestedAmount * oldTimePassed) /
                    (linearVestingBeneficiary.vestingEndTime - linearVestingBeneficiary.vestingStartTime);
                claimable -= alreadyClaimed;
            } else {
                claimable +=
                    (linearVestingBeneficiary.vestedAmount * timePassed) /
                    (linearVestingBeneficiary.vestingEndTime - linearVestingBeneficiary.vestingStartTime);
            }
        }

        // Calculate EOM vesting claimable amount
        if (
            eomVestingBeneficiary.monthlyUnlockPercentage > 0 &&
            eomVestingBeneficiary.lastClaimTime < eomVestingBeneficiary.vestingEndTime
        ) {
            uint256 lastClaimMonth = (eomVestingBeneficiary.lastClaimTime - eomVestingBeneficiary.vestingStartTime) /
                30 days;
            uint256 currentMonth = (currentTime - eomVestingBeneficiary.vestingStartTime) / 30 days;
            uint256 unlockedMonths = currentMonth - lastClaimMonth;

            if (unlockedMonths > 0) {
                uint256 unlockableAmount = (eomVestingBeneficiary.vestedAmount *
                    eomVestingBeneficiary.monthlyUnlockPercentage *
                    unlockedMonths) / 100;
                claimable += unlockableAmount;
            }
        }

        return claimable;
    }

    /// @notice Sets the token address for the contract
    /// @param _dystoTokenAddress The new token address
    function setDystoTokenAddress(address _dystoTokenAddress) public onlyOwner {
        dystoTokenAddress = _dystoTokenAddress;
    }

    /// @notice Sets the address used to verify signatures
    /// @param _signerAddress The new signer address
    function setSignerAddress(address _signerAddress) public onlyOwner {
        signerAddress = _signerAddress;
    }

    /// @notice Updates the TGE timestamp
    /// @param _tgeTimestamp The new TGE timestamp
    function setTGE(uint256 _tgeTimestamp) public onlyOwner {
        tgeTimestamp = _tgeTimestamp;
    }

    /// @notice Allows the contract owner to deposit tokens into the contract
    /// @param amount The amount of tokens to be deposited
    function deposit(uint256 amount) public onlyOwner {
        IERC20(dystoTokenAddress).transferFrom(msg.sender, address(this), amount);
    }

    /// @notice Allows the contract owner to reclaim all tokens from the contract
    /// @dev This should be used cautiously, typically in a scenario where contract migration or termination is needed
    function reclaimTokens() external onlyOwner {
        IERC20 token = IERC20(dystoTokenAddress);
        token.transfer(msg.sender, token.balanceOf(address(this)));
    }
}
