      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.26;

import '@openzeppelin/contracts/token/ERC20/IERC20.sol';
import '@openzeppelin/contracts/access/Ownable.sol';

// This contract is used to store the reserve of the token
// Ownership should be renounced shortly after deployment
contract ReservePool is Ownable {
  IERC20 public token;
  address public spender;

  constructor() Ownable(msg.sender) {}

  function setToken(address _token) external onlyOwner {
    token = IERC20(_token);
  }

  function setSpender(address _spender) external onlyOwner {
    // Make sure the token is set
    require(address(token) != address(0), 'Token not set');

    // Revoke previous spender's approval, if necessary
    if (spender != address(0)) token.approve(spender, 0);

    // Set new spender
    spender = _spender;
    token.approve(_spender, type(uint256).max);
  }

  function transfer(address to, uint256 amount) external onlyOwner {
    token.transfer(to, amount);
  }
}
