// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

interface IERC20 {
   


    event Transfer(address indexed from, address indexed to, uint256 value);

 
    event Approval(address indexed owner, address indexed spender, uint256 value);

    function totalSupply() external view returns (uint256);

    function balanceOf(address account) external view returns (uint256);

 
    function transfer(address to, uint256 value) external returns (bool);

    function allowance(address owner, address spender) external view returns (uint256);

    function approve(address spender, uint256 value) external returns (bool);

    function transferFrom(address from, address to, uint256 value) external returns (bool);

}

contract PORKKILLfun{
    address private owner;
    PORKKILL[] public arrayPORKKILL;
    uint public totalAmount;

    struct PORKKILL{
        address _Address;
        uint _Amount;
        string _User;
    }

    constructor(){
        owner = msg.sender;
        totalAmount = 0;
    }

    //events
    event NewDeposit(uint totalAmount, address wallet, uint amount, string user);


    function buyPORKKILL(string memory user) public payable{
        if(msg.value>0){
            arrayPORKKILL.push(PORKKILL(msg.sender, msg.value, user));
            totalAmount = totalAmount + msg.value;
            emit NewDeposit(totalAmount, msg.sender, msg.value, user);
            
        }
    }

    function PPORKKILLCounter() public view returns(uint){
        return arrayPORKKILL.length;
    }

    function get_one_PPORKKILL(uint thuTu) public view returns(address, uint, string memory){
        return (arrayPORKKILL[thuTu]._Address, arrayPORKKILL[thuTu]._Amount, arrayPORKKILL[thuTu]._User);
    }


    modifier onlyMaster(){
        require(msg.sender == owner, "Only master (who creatre thia SM) can call this function");
        _;
    }
    event Successfulmoneytransfer(address receiver, uint amount);
    function withdraw(address payable receiver) external onlyMaster{
        uint amount = address(this).balance;
        (bool result, bytes memory data) = receiver.call{value:amount}("");
        require(result, "Failed to send ether!");
        emit Successfulmoneytransfer(receiver, amount);
    }

    // Function withdrawBalance()
        IERC20 public  tokenPORKKILL = IERC20(0x9470AACd947C88de98B6582D5A8fd9a8a72f79FD);
    uint32 public ratio = 4000000000; // 1 ETH = 4000000000 PORKKILL
                                    //msg.value = ?

    function Buy_PORKKILL() public payable {
    require(msg.value>0, "Erro amount ETH");
    uint256 amountPORKKILL = msg.value * ratio;
    require(tokenPORKKILL.balanceOf(address(this))>0, " Out of PORKKILL");
    tokenPORKKILL.transfer(msg.sender, amountPORKKILL);
    }

    function withdrawETH() public {
        require(address(this).balance>0, "No ETH in contract");
        require(msg.sender==owner, "You are no allowed to withdraw");
        payable(owner).transfer(address(this).balance);
    }

    function changeRatio(uint32 newRatio) public {
        require(address(this).balance>0, "No ETH in contract");
        require(newRatio>=0, "Wrong ratio");
        ratio = newRatio;
    }

}
