      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.20;

import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";
import { ReentrancyGuard } from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import { IERC20Metadata } from "@openzeppelin/contracts/interfaces/IERC20Metadata.sol";
import { IERC721Metadata } from "@openzeppelin/contracts/interfaces/IERC721Metadata.sol";
import { IERC721Receiver } from "@openzeppelin/contracts/interfaces/IERC721Receiver.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { SignatureChecker } from "@openzeppelin/contracts/utils/cryptography/SignatureChecker.sol";
import { MessageHashUtils } from "@openzeppelin/contracts/utils/cryptography/MessageHashUtils.sol";
import { IVersionable } from "@ethsign/sign-protocol-evm/src/interfaces/IVersionable.sol";
import { CustomInitializable } from "../lib/CustomInitializable.sol";

contract NFTSimpleAuction is Ownable, ReentrancyGuard, CustomInitializable, IVersionable, IERC721Receiver {
    using SafeERC20 for IERC20Metadata;

    address public nft;
    address public paymentToken;
    address public authorizedWLSigner;
    uint256 public start;
    uint256 public end;
    uint256 public pricePerNFT;
    uint256 public wlPricePerNFT;
    uint256 public normalSaleSupply;
    uint256 public wlSaleSupply;
    uint256 public freeMintSupply;
    uint256[] internal _tokenIds;
    mapping(bytes32 codeHash => bool used) public usedCodes;

    error AuctionInactive();
    error AuctionActive();
    error ParamsSet();
    error InvalidData();
    error InvalidPayment();
    error LowLevelCallError(bytes data);
    error InvalidSignature();
    error InvalidCode();
    error InvalidPurchaseAmount();

    modifier onlyActive() {
        if (!getAuctionStatus()) revert AuctionInactive();
        _;
    }

    modifier onlyBeforeStart() {
        if (start < block.timestamp) revert AuctionActive();
        _;
    }

    modifier onlyInactive() {
        if (getAuctionStatus()) revert AuctionActive();
        _;
    }

    constructor() Ownable(address(0xdEaD)) ReentrancyGuard() { }

    function initialize() external override initializer {
        _transferOwnership(_msgSender());
    }

    function setAuctionParams(
        uint256 start_,
        uint256 end_,
        address nft_,
        address paymentToken_,
        uint256 price,
        uint256 wlPrice,
        uint256 normalSaleSupply_,
        uint256 wlSaleSupply_,
        uint256 freeMintSupply_
    )
        external
        onlyOwner
    {
        if (start > 0) revert ParamsSet();
        if (end_ <= start_) revert InvalidData();
        start = start_;
        end = end_;
        nft = nft_;
        paymentToken = paymentToken_;
        pricePerNFT = price;
        wlPricePerNFT = wlPrice;
        normalSaleSupply = normalSaleSupply_;
        wlSaleSupply = wlSaleSupply_;
        freeMintSupply = freeMintSupply_;
    }

    function setAuthorizedWLSigner(address signer) external onlyOwner {
        authorizedWLSigner = signer;
    }

    function onERC721Received(address, address, uint256 tokenId, bytes calldata) external returns (bytes4) {
        if (_msgSender() != nft) return 0;
        _tokenIds.push(tokenId);
        return this.onERC721Received.selector;
    }

    function purchase(uint256 numberOfNFTs, bytes calldata extraData) external payable onlyActive {
        uint256 amountToCharge;
        if (extraData.length > 0) {
            (uint256 approvedPurchaseAmount, bool freeMint) = _checkAndSaveWL(extraData);
            if (approvedPurchaseAmount != numberOfNFTs) revert InvalidPurchaseAmount();
            if (freeMint) {
                amountToCharge = 0;
                freeMintSupply -= numberOfNFTs;
            } else {
                amountToCharge = numberOfNFTs * wlPricePerNFT;
                wlSaleSupply -= numberOfNFTs;
            }
        } else {
            amountToCharge = numberOfNFTs * pricePerNFT;
            normalSaleSupply -= numberOfNFTs;
        }
        if (paymentToken == address(0)) {
            if (msg.value != amountToCharge) revert InvalidPayment();
        } else {
            if (msg.value > 0) revert InvalidPayment();
            if (amountToCharge > 0) {
                IERC20Metadata(paymentToken).safeTransferFrom(_msgSender(), address(this), amountToCharge);
            }
        }
        uint256 tokenIdsLastIndex = _tokenIds.length - 1;
        for (uint256 i = 0; i < numberOfNFTs; i++) {
            IERC721Metadata(nft).safeTransferFrom(address(this), _msgSender(), _tokenIds[tokenIdsLastIndex]);
            _tokenIds.pop();
            tokenIdsLastIndex--;
        }
    }

    function withdrawPaymentTokens() external onlyOwner {
        if (paymentToken == address(0)) {
            (bool success, bytes memory data) = owner().call{ value: address(this).balance }("");
            if (!success) revert LowLevelCallError(data);
        } else {
            IERC20Metadata(paymentToken).safeTransfer(owner(), IERC20Metadata(paymentToken).balanceOf(address(this)));
        }
    }

    function withdrawNFTs(uint256 startIndex, uint256 endIndex) external onlyOwner {
        for (uint256 i = startIndex; i < endIndex; i++) {
            IERC721Metadata(nft).safeTransferFrom(address(this), owner(), _tokenIds[i]);
            delete _tokenIds[i];
        }
    }

    function tokenIds() external view returns (uint256[] memory) {
        return _tokenIds;
    }

    function version() external pure override returns (string memory) {
        return "1.0.0-beta4";
    }

    function getAuctionStatus() public view returns (bool isActive) {
        return !(start > block.timestamp || end < block.timestamp || _tokenIds.length == 0 || nft == address(0));
    }

    function getMessageHash(
        uint256 approvedPurchaseAmount,
        bool freeMint,
        bytes32 code,
        address recipient
    )
        public
        view
        returns (bytes32)
    {
        return keccak256(abi.encode(block.chainid, approvedPurchaseAmount, freeMint, code, recipient));
    }

    function _checkAndSaveWL(bytes calldata extraData) internal returns (uint256, bool) {
        (uint256 approvedPurchaseAmount, bool freeMint, bytes32 code, bytes memory signature) =
            abi.decode(extraData, (uint256, bool, bytes32, bytes));
        if (usedCodes[code]) revert InvalidCode();
        if (
            !SignatureChecker.isValidSignatureNow(
                authorizedWLSigner,
                MessageHashUtils.toEthSignedMessageHash(
                    getMessageHash(approvedPurchaseAmount, freeMint, code, _msgSender())
                ),
                signature
            )
        ) revert InvalidSignature();
        usedCodes[code] = true;
        return (approvedPurchaseAmount, freeMint);
    }
}
