    string constant panic2 =
        "ms' repeatCount='indefinite' /> </polygon> <rect x='190' y='189' width='220' height='220' fill='#ffffff' /> </svg>";

    string constant crisis0 =
        "<svg width='100%' height='100%' viewBox='0 0 600 600' xmlns='http://www.w3.org/2000/svg' style='background-color: white'> <rect x='0' y='0' width='100%' height='100%' fill='#000000' opacity='1' /> <rect x='0' y='0' width='100%' height='100%' fill='#ff0000'> <animate attributeName='opacity' values='0;0;0.5;0;0;0;0.5;0;0;0.5;0;0.2;0;0;0;0;0.5;0;0;0;0;0;0;0.5;0;0;0;0.5;0;0;0;0;0.2;0;0;0;0;0.5;0;0.2;0;0' dur='";
