        "<svg width='100%' height='100%' viewBox='0 0 600 600' xmlns='http://www.w3.org/2000/svg' style='background-color: white;'> <rect x='0' y='0' width='100%' height='100%' fill='#000000' /> <rect x='250' y='150' width='100' height='100' fill='#b1b0b0'> <animate attributeName='x' values='400;110;265;220;500;90;290;400' repeatCount='indefinite' dur='";
