        string memory value = Strings.toString(_perc(percentage));

        return abi.encodePacked(despair0, value, despair1, value, despair2);
    }

    function _crisis(uint256 percentage) internal pure returns (bytes memory) {
        return
            abi.encodePacked(
                crisis0,
                Strings.toString(_perc(percentage)),
                crisis1,
                Strings.toString(_perc(percentage)),
                crisis2
            );
    }

    function _panic(uint256 percentage) internal pure returns (bytes memory) {
        uint256 increase;
        if (percentage == 0) {
            increase = 100;
        }

        uint256 perc = _perc(percentage) + increase;

        return
            abi.encodePacked(
                panic0,
                Strings.toString((perc * 1000) / 2 + 21),
                panic1,
                Strings.toString((perc * 1000) / 3 + 3),
                panic2
            );
    }

    function(uint256) pure returns (bytes memory)[] _renderers = [
        _panic,
        _crisis,
        _despair,
        _havoc
    ];

    function json(
        uint256 tokenId,
        uint256 progress,
        int256 percentageChange
    ) public view returns (bytes memory) {
        string memory percentageChangeDisplay;
        if (percentageChange < 0) {
            percentageChangeDisplay = string(
                abi.encodePacked(
                    "-",
                    Strings.toString(uint256((percentageChange * -1)))
                )
            );
        } else {
            percentageChangeDisplay = Strings.toString(
                uint256(percentageChange)
            );
        }

        bytes memory attributes = abi.encodePacked(
            '","attributes":',
            '[{"trait_type":"Timeframe","value":"',
            timeframes[tokenId - 1],
            '"},{"trait_type":"Color","value":"',
            colors[tokenId - 1],
            '"},{"trait_type":"% Change","value":"',
            percentageChangeDisplay,
            '"},{"display_type": "boost_percentage", "trait_type":"Tradeability","value":',
            Strings.toString(progress),
            "}]}"
        );

        return
            abi.encodePacked(
                "data:application/json;base64,",
                Base64.encode(
                    abi.encodePacked(
                        '{"name":"',
                        names[tokenId - 1],
                        '", "description":"',
                        descriptions[tokenId - 1],
                        '","image":"data:image/svg+xml;base64,',
                        Base64.encode(render(tokenId, progress)),
                        attributes
                    )
                )
            );
    }

    function render(
        uint256 tokenId,
        uint256 progress
    ) public view returns (bytes memory) {
        return _renderers[tokenId - 1](progress);
    }
