    */
    function footwearStats(uint256 gearId)
        public
        pure
        returns (int16[6] memory)
    {
        int16[6][17] memory GEAR = [
            [int16(0),0,0,0,    0,0],       // 51 | C | none

            // uncommon = 8 total
            // Health & Speed
            [int16(3),0,0,5,    1,0],       // 52 | U | sturdy cleats
