    "contracts/utils/SvgHelper.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity >=0.7.0 <0.9.0;

import "@openzeppelin/contracts/utils/Strings.sol";

library SvgHelper {
    function circle(
        string memory x,
        string memory y,
        string memory r,
        string memory color
    ) public pure returns (string memory) {
        return circle(x, y, r, "1", color, "", "0");
    }

    function circle(
        uint256 x,
        uint256 y,
        uint256 r,
        string memory opacity,
        string memory color,
        string memory strokeColor,
        string memory strokeWidth
    ) public pure returns (string memory) {
        return
            circle(
                Strings.toString(x),
                Strings.toString(y),
                Strings.toString(r),
                opacity,
                color,
                strokeColor,
                strokeWidth,
                ""
            );
    }

    function circle(
        string memory x,
        string memory y,
        string memory r,
        string memory opacity,
        string memory color,
        string memory strokeColor,
        string memory strokeWidth
    ) public pure returns (string memory) {
        return circle(x, y, r, opacity, color, strokeColor, strokeWidth, "");
    }

    function circle(
        string memory x,
        string memory y,
        string memory r,
        string memory opacity,
        string memory color,
        string memory strokeColor,
        string memory strokeWidth,
        string memory animation
    ) public pure returns (string memory) {
        return
            circle(
                x,
                y,
                r,
                opacity,
                color,
                strokeColor,
                strokeWidth,
                animation,
                ""
            );
    }

    function circle(
        string memory x,
        string memory y,
        string memory r,
        string memory opacity,
        string memory color,
        string memory strokeColor,
        string memory strokeWidth,
        string memory animation,
        string memory filter
    ) public pure returns (string memory) {
        return
            string.concat(
                "<circle cx='",
                x,
                "' cy='",
                y,
                "' r='",
                r,
                "' fill='",
                color,
                "' opacity='",
                opacity,
                "' stroke='",
                keccak256(abi.encodePacked(strokeColor)) != ""
                    ? strokeColor
                    : "",
                "' stroke-width='",
                keccak256(abi.encodePacked(strokeColor)) != ""
                    ? strokeWidth
                    : "",
                "'",
                keccak256(abi.encodePacked(filter)) == ""
                    ? ""
                    : string.concat(" filter='", filter, "'"),
                keccak256(abi.encodePacked((animation))) == ""
                    ? "/>"
                    : string.concat(" >", animation, "</circle>")
            );
    }

    function triangle(
        string memory x0,
        string memory y0,
        string memory x1,
        string memory y1,
        string memory x2,
        string memory y2,
        string memory color
    ) public pure returns (string memory) {
        return
            string.concat(
                "<polygon points='",
                x0,
                " ",
                y0,
                ", ",
                x1,
                " ",
                y1,
                ", ",
                x2,
                " ",
                y2,
                "'  fill='",
                color,
                "'/>"
            );
    }

    function square(
        string memory x,
        string memory y,
        string memory size,
        string memory color
    ) public pure returns (string memory) {
        return square(x, y, size, color, "");
    }

    function square(
        string memory x,
        string memory y,
        string memory size,
        string memory color,
        string memory animation
    ) public pure returns (string memory) {
        return rectangle(x, y, size, size, color, "", "", animation);
    }

    function animate(
        string memory attributeName,
        string memory values,
        string memory duration
    ) public pure returns (string memory) {
        return
            string.concat(
                "<animate attributeName='",
                attributeName,
                "' values='",
                values,
                "' dur='",
                duration,
                "' repeatCount='",
                "indefinite",
                "' />"
            );
    }

    function rectangle(
        string memory x,
        string memory y,
        string memory h,
        string memory w,
        string memory color
    ) public pure returns (string memory) {
        return rectangle(x, y, h, w, color, "", "", "");
    }

    function rectangle(
        string memory x,
        string memory y,
        string memory h,
        string memory w,
        string memory color,
        string memory stroke,
        string memory strokeWidth,
        string memory animation
    ) public pure returns (string memory) {
        return
            string.concat(
                "<rect x='",
                x,
                "' y='",
                y,
                "' height='",
                h,
                "' width='",
                w,
                "' fill='",
                color,
                "' stroke='",
                stroke,
                "' stroke-width='",
                strokeWidth,
                "'",
                keccak256(abi.encodePacked((animation))) == ""
                    ? "/>"
                    : string.concat(" >", animation, "</rect>")
            );
    }

    function getStandardAnimationGroup(
        string memory s
    ) public pure returns (string memory) {
        return
            string.concat(
                animate("r", "2.5;6.9;", s),
                animate("stroke-width", "0;.1;", s),
                animate("opacity", "1;0;", s)
            );
    }
}
"
    },
    "contracts/utils/TransferHelper.sol": {
      "content": "// SPDX-License-Identifier: GPL-2.0-or-later
pragma solidity >=0.6.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

library TransferHelper {
    /// @notice Transfers tokens from the targeted address to the given destination
    /// @notice Errors with 'STF' if transfer fails
    /// @param token The contract address of the token to be transferred
    /// @param from The originating address from which the tokens will be transferred
    /// @param to The destination address of the transfer
    /// @param value The amount to be transferred
    function safeTransferFrom(
        address token,
        address from,
        address to,
        uint256 value
    ) internal {
        (bool success, bytes memory data) = token.call(
            abi.encodeWithSelector(
                IERC20.transferFrom.selector,
                from,
                to,
                value
            )
        );
        require(
            success && (data.length == 0 || abi.decode(data, (bool))),
            "STF"
        );
    }

    /// @notice Transfers tokens from msg.sender to a recipient
    /// @dev Errors with ST if transfer fails
    /// @param token The contract address of the token which will be transferred
    /// @param to The recipient of the transfer
    /// @param value The value of the transfer
    function safeTransfer(address token, address to, uint256 value) internal {
        (bool success, bytes memory data) = token.call(
            abi.encodeWithSelector(IERC20.transfer.selector, to, value)
        );
        require(
            success && (data.length == 0 || abi.decode(data, (bool))),
            "ST"
        );
    }

    /// @notice Approves the stipulated contract to spend the given allowance in the given token
    /// @dev Errors with 'SA' if transfer fails
    /// @param token The contract address of the token to be approved
    /// @param to The target of the approval
    /// @param value The amount of the given token the target will be allowed to spend
    function safeApprove(address token, address to, uint256 value) internal {
        (bool success, bytes memory data) = token.call(
            abi.encodeWithSelector(IERC20.approve.selector, to, value)
        );
        require(
            success && (data.length == 0 || abi.decode(data, (bool))),
            "SA"
        );
    }

    /// @notice Transfers ETH to the recipient address
    /// @dev Fails with `STE`
    /// @param to The destination of the transfer
    /// @param value The value to be transferred
    function safeTransferETH(address to, uint256 value) internal {
        (bool success, ) = to.call{value: value}(new bytes(0));
        require(success, "STE");
    }
}
