    "contracts/strategies/pendleLp/lrt/PendleLpStrategyLrt.sol": {
      "content": "// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.20;

import "../PendleLpStrategyBase.sol";
import {IERC20Metadata} from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import {IVault} from "@balancer-labs/v2-interfaces/contracts/vault/IVault.sol";
import {IAsset} from "@balancer-labs/v2-interfaces/contracts/vault/IAsset.sol";
import {ErrorLib} from "../../../lib/ErrorLib.sol";

// all LRT have 2 major sources of liquidity, Uniswap V2, Balancer V2 or Curve

interface CurveFi {
    function exchange(int128 i, int128 j, uint256 dx, uint256 min_dy) external returns (uint256);
}

contract PendleLpStrategyLrt is PendleLpStrategyBase {
    using SafeERC20 for IERC20;
    uint256 public pricefeedPrecision;
    uint256 public syUnderlyingPrecision;
    uint256 public underlyingPrecision;
    uint256 public minReceivedAmountFactorUnderlyingSy;
    bytes public uniswapPathUnderlyingToSyUnderlying;
    bytes public uniswapPathSyUnderlyingToUnderlying;
    bytes32 public balancerPoolId;
    CurveFi public curvePool;
    IVault internal constant BALANCER_VAULT = IVault(0xBA12222222228d8Ba445958a75a0704d566BF2C8);

    enum Exchange {
        Curve,
        Uniswap,
        Balancer
    }
    Exchange public selectedExchange;

    struct ExchangeInitParams {
        bytes uniswapPathUnderlyingToSyUnderlying;
        bytes uniswapPathSyUnderlyingToUnderlying;
        bytes32 balancerPoolId;
        address curvePool;
        Exchange selectedExchange;
    }

    constructor() initializer {}

    function initialize(
        address _poolingManager,
        address _underlying,
        address _yieldToken,
        address _bridge,
        address _syUnderlying,
        ExchangeInitParams memory _exchangeParams,
        uint256 _maxSingleTrade
    ) external initializer {
        _initExchange(_exchangeParams);
        _initializePendleLpStrategy(_poolingManager, _underlying, _yieldToken, _bridge, _syUnderlying, _maxSingleTrade);
        _setMinReceivedAmountFactorUnderlyingSy((WAD * 990) / 1000);
    }

    function setUniswapPathUnderlyingToSyUnderlying(bytes memory _uniswapPathUnderlyingToSyUnderlying) external {
        _assertOnlyRoleOwner();
        if (_uniswapPathUnderlyingToSyUnderlying.length == 0) revert ErrorLib.InvalidUniswapPath();
        uniswapPathUnderlyingToSyUnderlying = _uniswapPathUnderlyingToSyUnderlying;
    }

    function setUniswapPathSyUnderlyingToUnderlying(bytes memory _uniswapPathSyUnderlyingToUnderlying) external {
        _assertOnlyRoleOwner();
        if (_uniswapPathSyUnderlyingToUnderlying.length == 0) revert ErrorLib.InvalidUniswapPath();
        uniswapPathSyUnderlyingToUnderlying = _uniswapPathSyUnderlyingToUnderlying;
    }

    function setBalancerPoolId(bytes32 _balancerPoolId) external {
        _assertOnlyRoleOwner();
        if (_balancerPoolId == bytes32(0)) revert ErrorLib.InvalidBalancerPool();
        balancerPoolId = _balancerPoolId;
    }

    function setCurvePool(address _curvePool) external {
        _assertOnlyRoleOwner();
        if (_curvePool == address(0)) revert ErrorLib.InvalidCurvePool();
        curvePool = CurveFi(_curvePool);
    }

    function switchAmm(Exchange _selectedExchange) external {
        _assertOnlyRoleOwner();
        address currentSpender = _addressToApproveSyUnderlyingAndUnderlying();
        IERC20 underlyingTokenDisp = IERC20(underlyingToken);
        IERC20 syUnderlyingTokenDisp = IERC20(syUnderlying);
        underlyingTokenDisp.forceApprove(address(currentSpender), 0);
        syUnderlyingTokenDisp.forceApprove(address(currentSpender), 0);
        if (_selectedExchange == Exchange.Uniswap) {
            if (uniswapPathUnderlyingToSyUnderlying.length == 0) revert ErrorLib.InvalidUniswapPath();
            if (uniswapPathSyUnderlyingToUnderlying.length == 0) revert ErrorLib.InvalidUniswapPath();
        }
        if (_selectedExchange == Exchange.Balancer) {
            if (balancerPoolId == bytes32(0)) revert ErrorLib.InvalidBalancerPool();
        }

        if (_selectedExchange == Exchange.Curve) {
            if (address(curvePool) == address(0)) revert ErrorLib.InvalidCurvePool();
        }
        selectedExchange = _selectedExchange;
        currentSpender = _addressToApproveSyUnderlyingAndUnderlying();
        underlyingTokenDisp.forceApprove(address(currentSpender), type(uint).max);
        syUnderlyingTokenDisp.forceApprove(address(currentSpender), type(uint).max);
    }

    function setMinReceivedAmountFactorUnderlyingSy(uint256 _minReceivedAmountFactorUnderlyingSy) external {
        _assertOnlyRoleOwner();
        _setMinReceivedAmountFactorUnderlyingSy(_minReceivedAmountFactorUnderlyingSy);
    }

    /// @inheritdoc PendleLpStrategyBase
    function _getSyUnderlyingFromUnderlying(uint256 _amount) internal override returns (uint256) {
        uint256 syUnderlyingAmount = _underlyingToSyUnderlying(_amount);
        uint256 amountOutMinimum = (minReceivedAmountFactorUnderlyingSy * syUnderlyingAmount) / (WAD);
        if (selectedExchange == Exchange.Balancer) {
            IVault.SingleSwap memory swapParams = IVault.SingleSwap({
                poolId: balancerPoolId,
                kind: IVault.SwapKind.GIVEN_IN,
                assetIn: IAsset(underlyingToken),
                assetOut: IAsset(syUnderlying),
                amount: _amount,
                userData: new bytes(0)
            });
            IVault.FundManagement memory fundsParams = IVault.FundManagement({
                sender: address(this),
                fromInternalBalance: false,
                recipient: payable(address(this)),
                toInternalBalance: false
            });
            return BALANCER_VAULT.swap(swapParams, fundsParams, amountOutMinimum, block.timestamp);
        } else if (selectedExchange == Exchange.Curve) {
            return curvePool.exchange(0, 1, _amount, amountOutMinimum);
        } else {
            ISwapRouter.ExactInputParams memory params = ISwapRouter.ExactInputParams({
                path: uniswapPathUnderlyingToSyUnderlying,
                recipient: address(this),
                deadline: block.timestamp,
                amountIn: _amount,
                amountOutMinimum: amountOutMinimum
            });
            return UNI_V3_ROUTER.exactInput(params);
        }
    }

    /// @inheritdoc PendleLpStrategyBase
    function _getUnderlyingFromSyUnderlying(uint256 _shares) internal override returns (uint256) {
        uint256 underlyingAmount = _syUnderlyingToUnderlying(_shares);
        uint256 amountOutMinimum = (minReceivedAmountFactorUnderlyingSy * underlyingAmount) / (WAD);
        if (selectedExchange == Exchange.Balancer) {
            IVault.SingleSwap memory swapParams = IVault.SingleSwap({
                poolId: balancerPoolId,
                kind: IVault.SwapKind.GIVEN_IN,
                assetIn: IAsset(syUnderlying),
                assetOut: IAsset(underlyingToken),
                amount: _shares,
                userData: new bytes(0)
            });
            IVault.FundManagement memory fundsParams = IVault.FundManagement({
                sender: address(this),
                fromInternalBalance: false,
                recipient: payable(address(this)),
                toInternalBalance: false
            });
            return (BALANCER_VAULT.swap(swapParams, fundsParams, amountOutMinimum, block.timestamp));
        } else if (selectedExchange == Exchange.Curve) {
            return curvePool.exchange(1, 0, _shares, amountOutMinimum);
        } else {
            ISwapRouter.ExactInputParams memory params = ISwapRouter.ExactInputParams({
                path: uniswapPathSyUnderlyingToUnderlying,
                recipient: address(this),
                deadline: block.timestamp,
                amountIn: _shares,
                amountOutMinimum: amountOutMinimum
            });
            return UNI_V3_ROUTER.exactInput(params);
        }
    }

    function _syUnderlyingToUnderlying(uint256 _amount) internal view returns (uint256) {
        return (_amount * sy.exchangeRate()) / (WAD);
    }

    function _underlyingToSyUnderlying(uint256 _amount) internal view returns (uint256) {
        return (_amount * WAD) / (sy.exchangeRate());
    }

    function _addressToApproveUnderlyingToSyUnderlying() internal view override returns (address) {
        return _addressToApproveSyUnderlyingAndUnderlying();
    }

    function _addressToApproveSyUnderlyingToUnderlying() internal view override returns (address) {
        return _addressToApproveSyUnderlyingAndUnderlying();
    }

    function _addressToApproveSyUnderlyingAndUnderlying() internal view returns (address) {
        if (selectedExchange == Exchange.Balancer) {
            return address(BALANCER_VAULT);
        } else if (selectedExchange == Exchange.Curve) {
            return address(curvePool);
        } else {
            return address(UNI_V3_ROUTER);
        }
    }

    function _setMinReceivedAmountFactorUnderlyingSy(uint256 _minReceivedAmountFactorUnderlyingSy) internal {
        if (_minReceivedAmountFactorUnderlyingSy > WAD || _minReceivedAmountFactorUnderlyingSy < (WAD * 98) / 100)
            revert ErrorLib.InvalidSlippage();
        minReceivedAmountFactorUnderlyingSy = _minReceivedAmountFactorUnderlyingSy;
    }

    function _initExchange(ExchangeInitParams memory _params) internal {
        selectedExchange = _params.selectedExchange;
        if (_params.selectedExchange == Exchange.Balancer) {
            if (_params.balancerPoolId == bytes32(0)) revert ErrorLib.InvalidBalancerPool();
            balancerPoolId = _params.balancerPoolId;
        } else if (_params.selectedExchange == Exchange.Uniswap) {
            if (_params.uniswapPathUnderlyingToSyUnderlying.length == 0) revert ErrorLib.InvalidUniswapPath();
            if (_params.uniswapPathSyUnderlyingToUnderlying.length == 0) revert ErrorLib.InvalidUniswapPath();
            uniswapPathUnderlyingToSyUnderlying = _params.uniswapPathUnderlyingToSyUnderlying;
            uniswapPathSyUnderlyingToUnderlying = _params.uniswapPathSyUnderlyingToUnderlying;
        } else if (_params.selectedExchange == Exchange.Curve) {
            if (_params.curvePool == address(0)) revert ErrorLib.InvalidCurvePool();
            curvePool = CurveFi(_params.curvePool);
        }
    }
}
"
