    "contracts/strategies/pendleLp/PendleLpStrategyBase.sol": {
      "content": "// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.20;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {Math} from "@openzeppelin/contracts/utils/math/Math.sol";
import {IPMarket} from "@pendle/core-v2/contracts/interfaces/IPMarket.sol";
import {IPActionAddRemoveLiqV3, LimitOrderData, ApproxParams} from "@pendle/core-v2/contracts/interfaces/IPActionAddRemoveLiqV3.sol";
import {PendleLpOracleLib, IStandardizedYield} from "@pendle/core-v2/contracts/oracles/PendlePtLpOracle.sol";
import {IPPtLpOracle} from "@pendle/core-v2/contracts/interfaces/IPPtLpOracle.sol";
import {ISwapRouter} from "@uniswap/v3-periphery/contracts/interfaces/ISwapRouter.sol";
import {StrategyBase} from "../StrategyBase.sol";
import {ErrorLib} from "../../lib/ErrorLib.sol";
import {IPenpie} from "../../interfaces/pendle/IPenpie.sol";
import {IMarketDepositHelper} from "../../interfaces/pendle/IMarketDepositHelper.sol";

/// @title Base contract for pendle strategy LP
/// @author @nimbora 2024
abstract contract PendleLpStrategyBase is StrategyBase {
    using SafeERC20 for IERC20;
    using Math for uint256;
    using PendleLpOracleLib for IPMarket;

    // Constants
    uint256 internal constant WAD = 10 ** 18;
    IPActionAddRemoveLiqV3 internal constant PENDLE_ROUTER =
        IPActionAddRemoveLiqV3(0x00000000005BBB0EF59571E58418F9a4357b68A0);
    IMarketDepositHelper internal constant MARKET_DEPOSIT_HELPER =
        IMarketDepositHelper(0x1C1Fb35334290b5ff1bF7B4c09130885b10Fc0f4);
    IERC20 internal constant PENDLE = IERC20(0x808507121B80c02388fAd14726482e061B8da827);
    IPPtLpOracle internal constant PTLPORACLE = IPPtLpOracle(0x66a1096C6366b2529274dF4f5D8247827fe4CEA8);
    IPenpie internal constant PENPIE = IPenpie(0x16296859C15289731521F199F0a5f762dF6347d0);
    address internal constant PENDLE_STAKING = 0x6E799758CEE75DAe3d84e09D40dc416eCf713652;
    ISwapRouter internal constant UNI_V3_ROUTER = ISwapRouter(0xE592427A0AEce92De3Edee1F18E0157C05861564);

    // Subject to modification
    uint32 public twapDuration;
    bytes public uniswapPathPendleUnderlying;
    IPMarket public market;
    IStandardizedYield public sy;
    address public syUnderlying;
    uint256 public minAmountToSell;
    // https://docs.pendle.finance/Developers/Contracts/PendleRouter#approxparams
    ApproxParams public receiveParams;
    uint256 public maxSingleTrade;
    uint256 public minReceivedAmountFactor;

    function _initializePendleLpStrategy(
        address _poolingManager,
        address _underlyingToken,
        address _yieldToken,
        address _bridge,
        address _syUnderlying,
        uint256 _maxSingleTrade
    ) internal {
        _strategyBase__init(_poolingManager, _underlyingToken, _yieldToken, _bridge);
        market = IPMarket(_yieldToken);
        if (market.isExpired()) revert ErrorLib.InvalidMarket();
        (sy, , ) = market.readTokens();
        if (sy.isValidTokenIn(_syUnderlying) == false) revert ErrorLib.InvalidSyUnderlyingToken();
        syUnderlying = _syUnderlying;
        IERC20(_underlyingToken).safeIncreaseAllowance(_addressToApproveUnderlyingToSyUnderlying(), type(uint).max);
        IERC20(_syUnderlying).safeIncreaseAllowance(_addressToApproveSyUnderlyingToUnderlying(), type(uint).max);
        IERC20(_syUnderlying).safeIncreaseAllowance(address(sy), type(uint).max);
        IERC20(address(sy)).safeIncreaseAllowance(address(PENDLE_ROUTER), type(uint).max);
        IERC20(_yieldToken).safeIncreaseAllowance(PENDLE_STAKING, type(uint).max);
        IERC20(_yieldToken).safeIncreaseAllowance(address(PENDLE_ROUTER), type(uint).max);
        PENDLE.safeIncreaseAllowance(address(UNI_V3_ROUTER), type(uint).max);
        twapDuration = 1200;
        minAmountToSell = type(uint).max;
        receiveParams = ApproxParams({
            guessMin: 0,
            guessMax: type(uint256).max,
            guessOffchain: 0,
            maxIteration: 256,
            eps: 1e14
        });
        _setMinReceivedAmountFactor((WAD * 98) / 100);
        maxSingleTrade = _maxSingleTrade;

        // check if pendle market oracle initialized for the twap duration
        _verifyOracleReady(address(market), twapDuration);
    }

    /**
     * @dev Replaces the current yield token market with a new one.
     * This function performs several critical actions in the process of changing the market:
     * 1. Validates the new market and the associated Standardized Yield (SY) token.
     * 2. Claims rewards from the current market and possibly trades them for additional SY tokens.
     * 3. Withdraws the liquidity from the current market and deposits it into the new market.
     * 4. Updates the contract's state to reference the new market and its associated tokens.
     *
     * @param _yieldToken The address of the new market to switch to.
     */
    function replaceMarket(address _yieldToken) external {
        _assertOnlyRoleOwner();

        // check new market valid
        IPMarket newMarket = IPMarket(_yieldToken);
        if (newMarket.isExpired()) revert ErrorLib.InvalidMarket();
        (IStandardizedYield newSy, , ) = newMarket.readTokens();
        if (newSy != sy) revert ErrorLib.InvalidSyToken();

        // claim rewards
        _harvestAndSellRewards();

        // Get potentially more SY if sold PENDLE
        uint256 underlyingBalance = IERC20(underlyingToken).balanceOf(address(this));
        if (underlyingBalance > 0) {
            sy.deposit(address(this), syUnderlying, _getSyUnderlyingFromUnderlying(underlyingBalance), 1);
        }

        // withdraw all LP to sy, do not care about maxSingleTrade as market is expired and there is no price impact
        uint256 withdrawAmount = _yieldBalance();
        MARKET_DEPOSIT_HELPER.withdrawMarket(address(market), withdrawAmount);
        LimitOrderData memory limit;
        PENDLE_ROUTER.removeLiquiditySingleSy(address(this), address(market), withdrawAmount, 1, limit);

        uint256 tradeSize = Math.min(IERC20(address(sy)).balanceOf(address(this)), maxSingleTrade);

        // check if pendle market oracle initialized for the twap duration
        _verifyOracleReady(address(market), twapDuration);

        // Deposit all SY to the new market
        (uint256 netLpOut, ) = PENDLE_ROUTER.addLiquiditySingleSy(
            address(this),
            address(newMarket),
            tradeSize,
            ((tradeSize * minReceivedAmountFactor) / newMarket.getLpToSyRate(twapDuration)),
            receiveParams,
            limit
        );

        IERC20(_yieldToken).safeIncreaseAllowance(PENDLE_STAKING, type(uint).max);
        IERC20(_yieldToken).safeIncreaseAllowance(address(PENDLE_ROUTER), type(uint).max);
        MARKET_DEPOSIT_HELPER.depositMarket(address(newMarket), netLpOut);
        // replace the market
        yieldToken = _yieldToken;
        market = newMarket;
    }

    /**
     * @notice Determines the address to approve for converting underlying tokens to SY tokens.
     * @dev Must be implemented by the derived contract to specify the approval address.
     * @return The address which will be approved to handle the underlying to SY conversion.
     */
    function _addressToApproveUnderlyingToSyUnderlying() internal view virtual returns (address);

    /**
     * @notice Determines the address to approve for converting SY tokens back to underlying tokens.
     * @dev Must be implemented by the derived contract to specify the approval address.
     * @return The address which will be approved to handle the SY to underlying conversion.
     */
    function _addressToApproveSyUnderlyingToUnderlying() internal view virtual returns (address);

    /**
     * @dev Converts a specified `amount` of underlying token to SY underlying token.
     * Must be implemented by the derived contract to define the conversion logic.
     * @param amount The amount of underlying token to convert.
     * @return SY underlying token amount.
     */
    function _getSyUnderlyingFromUnderlying(uint256 amount) internal virtual returns (uint256);

    /**
     * @dev Converts a specified `amount` of sy underlying token to underlying token.
     * Must be implemented by the derived contract to define the conversion logic.
     * @param amount The amount of sy underlying token to convert.
     * @return underlying token amount.
     */
    function _getUnderlyingFromSyUnderlying(uint256 amount) internal virtual returns (uint256);

    /**
     * @notice Sets the minimum amount of rewards that triggers selling PENDLE liquidity mining.
     * @dev Only callable by the role owner to adjust the threshold for selling rewards.
     * @param _minAmountToSell New minimum amount to trigger the sell operation.
     */
    function setMinAmountToSell(uint256 _minAmountToSell) external {
        _assertOnlyRoleOwner();
        minAmountToSell = _minAmountToSell;
    }

    /**
     * @notice Sets the duration for TWAP (Time Weighted Average Price) calculation.
     * @dev Only callable by the role owner to adjust the duration for TWAP.
     * @param _twapDuration New duration in seconds for the TWAP calculation.
     */
    function setTwapDuration(uint32 _twapDuration) external {
        _assertOnlyRoleOwner();
        twapDuration = _twapDuration;
        _verifyOracleReady(yieldToken, _twapDuration);
    }

    /**
     * @notice Sets the path for swapping Pendle to the underlying token on Uniswap.
     * @dev Only callable by the role owner to adjust the swap path used in trading operations.
     * @param _uniswapPathPendleUnderlying The new swap path encoded as bytes.
     */
    function setUniswapPathPendleUnderlying(bytes memory _uniswapPathPendleUnderlying) external {
        _assertOnlyRoleOwner();
        uniswapPathPendleUnderlying = _uniswapPathPendleUnderlying;
    }

    /**
     * @notice Sets the minimum receive amount factor when providing liquidity on Pendle market.
     * @dev Only callable by the role owner, WAD unit.
     * @param _minReceivedAmountFactor The new minimum receive amount factor.
     */
    function setMinReceivedAmountFactor(uint256 _minReceivedAmountFactor) external {
        _assertOnlyRoleOwner();
        _setMinReceivedAmountFactor(_minReceivedAmountFactor);
    }

    /**
     * @notice Sets the maximum amount of underlying token that can be deposited and withdrawn
     * in a single operation.
     * @dev Only callable by the role owner to adjust the maximum trade amount.
     * @param _maxSingleTrade New maximum single trade amount.
     */
    function setMaxSingleTrade(uint256 _maxSingleTrade) external {
        _assertOnlyRoleOwner();
        maxSingleTrade = _maxSingleTrade;
    }

    /**
     * @notice Transfers out any token from the contract.
     * @dev Can be used to recover tokens sent to the contract mistakenly. Only callable by the role owner.
     * @param _token The address of the token to be swept out of the contract.
     */
    function sweep(address _token) external {
        _assertOnlyRoleOwner();
        if (_token == underlyingToken || _token == address(PENDLE) || _token == address(address(sy)))
            revert ErrorLib.InvalidToken();
        IERC20(_token).safeTransfer(msg.sender, IERC20(_token).balanceOf(address(this)));
    }

    /**
     * @notice Sets the parameters for receiving yields and approximations.
     * @dev Only callable by the role owner to adjust the parameters used in yield calculation.
     * @param _receiveParams The structure containing the new parameters for yield receiving.
     */
    function setReceiveParams(ApproxParams memory _receiveParams) external {
        _assertOnlyRoleOwner();
        receiveParams = _receiveParams;
    }

    /// @inheritdoc	StrategyBase
    function addressToApprove() external view override returns (address) {
        return (address(this));
    }

    /// @inheritdoc	StrategyBase
    function depositCalldata(uint256 _amount) external view override returns (address target, bytes memory cdata) {
        target = address(this);
        cdata = abi.encodeWithSignature("deposit(uint256)", _amount);
    }

    /**
     * @dev Deposits the specified `_amount` of the underlying token into the market.
     * This function handles the process of converting the underlying token sy underlying tokens, depositing it into SY token and finally depositing it into LP.
     * The process includes:w
     * 1. Checking market expiry and caller's permissions.
     * 2. Harvesting and selling any accrued rewards.
     * 3. Transferring the underlying tokens from the caller to the contract.
     * 4. Converting the underlying tokens to sy underlying.
     * 5. Depositing the sy underlying tokens to sy.
     * 6. Depositing sy tokens into the market to receive liquidity tokens.
     * 7. Depositing LP tokens into penpie to boost PENDLE distribution.
     *
     * @param _amount The amount of the underlying token to be deposited.
     * @notice This function should only be called by the pooling manager.
     *         It uses the market's deposit logic and handles reward harvesting internally.
     */
    function deposit(uint256 _amount) external {
        if (market.isExpired()) revert ErrorLib.Expired();
        if (msg.sender != poolingManager) revert ErrorLib.CallerIsNotPoolingManager();
        // sold rewards will be in underlying token
        _harvestAndSellRewards();

        IERC20(underlyingToken).safeTransferFrom(msg.sender, address(this), _amount);

        // potential additional underlying after harvesting PENDLE or following a withdraw with dust underlying.
        // wrap token to sy and deposit to market
        sy.deposit(
            address(this),
            syUnderlying,
            _getSyUnderlyingFromUnderlying(IERC20(underlyingToken).balanceOf(address(this))),
            1
        );

        // Make sure received amount is fair
        // deposit sy into market
        uint256 tradeSize = Math.min(IERC20(address(sy)).balanceOf(address(this)), maxSingleTrade);
        LimitOrderData memory limit;
        (uint256 netLpOut, ) = PENDLE_ROUTER.addLiquiditySingleSy(
            address(this),
            address(market),
            // potential additiomal sy tokens when addLiquidity due to receiveParams precision + maxSingleTrade on deposit
            tradeSize,
            _getSyToLpAmount(tradeSize, minReceivedAmountFactor),
            receiveParams,
            limit
        );
        MARKET_DEPOSIT_HELPER.depositMarket(address(market), netLpOut);
    }

    /// @inheritdoc	StrategyBase
    function _withdraw(uint256 _amount) internal override returns (uint256) {
        // sold rewards will be in underlying token
        _harvestAndSellRewards();
        address self = address(this);
        uint256 underlyingBalance = IERC20(underlyingToken).balanceOf(self);
        uint256 withdrawAmount;
        if (underlyingBalance >= _amount) {
            // if there is enough underlying token, transfer it to pooling manager
            IERC20(underlyingToken).safeTransfer(poolingManager, _amount);
            return _amount;
        } else {
            // Look at required sy withdraw amount
            withdrawAmount = _amount - underlyingBalance;
            uint256 syBalance = IERC20(address(sy)).balanceOf(self);
            withdrawAmount = (withdrawAmount * WAD) / sy.exchangeRate();
            if (withdrawAmount > syBalance) {
                // Get additional LP to withdraw to get missing sy tokens
                withdrawAmount = Math.min((withdrawAmount - syBalance), maxSingleTrade);
                uint256 minSyOut = ((withdrawAmount * minReceivedAmountFactor) / WAD);
                withdrawAmount = _getSyToLpAmount(withdrawAmount, WAD);
                uint256 yieldBalance = _yieldBalance();
                if (withdrawAmount > yieldBalance) {
                    minSyOut = (yieldBalance * minSyOut) / withdrawAmount;
                    withdrawAmount = yieldBalance;
                }
                MARKET_DEPOSIT_HELPER.withdrawMarket(address(market), withdrawAmount);
                LimitOrderData memory limit;
                (withdrawAmount, ) = PENDLE_ROUTER.removeLiquiditySingleSy(
                    address(this),
                    address(market),
                    withdrawAmount,
                    minSyOut,
                    limit
                );
                withdrawAmount = withdrawAmount + syBalance;
            }

            // withdraw sy balance plus obtained sy fron withdrawn LP
            sy.redeem(address(this), withdrawAmount, syUnderlying, 0, false);
            _getUnderlyingFromSyUnderlying(IERC20(syUnderlying).balanceOf(address(this)));
            // sending only requested amount to pooling manager, the rest will be deposited in the next deposit
            withdrawAmount = Math.min(_amount, IERC20(underlyingToken).balanceOf(address(this)));
            IERC20(underlyingToken).safeTransfer(poolingManager, withdrawAmount);
            return withdrawAmount;
        }
    }

    function _getSyToLpAmount(uint256 _tradeSize, uint256 delta) internal view returns (uint256) {
        return ((_tradeSize * delta) / market.getLpToSyRate(twapDuration));
    }

    function _setMinReceivedAmountFactor(uint256 _minReceivedAmountFactor) internal {
        if (_minReceivedAmountFactor > WAD || _minReceivedAmountFactor < (WAD * 95) / 100)
            revert ErrorLib.InvalidSlippage();
        minReceivedAmountFactor = _minReceivedAmountFactor;
    }

    /// @inheritdoc	StrategyBase
    function _nav() internal view override returns (uint256) {
        // underlying and sy tokens can be left in the contract.
        uint256 syBalance = IERC20(address(sy)).balanceOf(address(this));
        uint256 syValue = 0;
        if (syBalance > 0) {
            syValue = (sy.exchangeRate() * syBalance) / WAD;
        }
        return (_yieldToUnderlying(_yieldBalance()) + IERC20(underlyingToken).balanceOf(address(this)) + syValue);
    }

    /// @inheritdoc	StrategyBase
    function _yieldBalance() internal view override returns (uint256) {
        return MARKET_DEPOSIT_HELPER.balance(address(market), address(this));
    }

    /// @inheritdoc	StrategyBase
    function _underlyingToYield(uint256 _amount) internal view override returns (uint256) {
        // price of one LP token in WAD, oracle price is in WAD
        // https://docs.pendle.finance/Developers/Integration/HowToIntegratePtAndLpOracle
        uint256 price = market.getLpToAssetRate(twapDuration);
        // amount of LP for underlying amount _amount
        return (_amount * WAD) / price;
    }

    /// @inheritdoc	StrategyBase
    function _yieldToUnderlying(uint256 _amount) internal view override returns (uint256) {
        return (_amount * market.getLpToAssetRate(twapDuration)) / WAD;
    }

    /// @notice Harvest rewards from penpie and sell it from more underlying tokens
    function _harvestAndSellRewards() internal {
        address[] memory stakingTokens = new address[](1);
        stakingTokens[0] = address(market);
        PENPIE.multiclaim(stakingTokens);
        address self = address(this);
        uint256 balance = PENDLE.balanceOf(self);
        if (balance > minAmountToSell) {
            ISwapRouter.ExactInputParams memory params = ISwapRouter.ExactInputParams({
                path: uniswapPathPendleUnderlying,
                recipient: self,
                deadline: block.timestamp,
                amountIn: balance,
                amountOutMinimum: 0 // it's only rewards, so we can sell it for any price
            });
            UNI_V3_ROUTER.exactInput(params);
        }
    }

    function _verifyOracleReady(address marketToCheck, uint32 duration) internal view {
        (bool increaseCardinalityRequired, , bool oldestObservationSatisfied) = PTLPORACLE.getOracleState(
            marketToCheck,
            duration
        );
        if (increaseCardinalityRequired) revert ErrorLib.IncreaseCardinalityRequired();
        if (!oldestObservationSatisfied) revert ErrorLib.OldestObservationNotSatisfied();
    }
}
"
