pragma solidity 0.8.20;

import "./IRateProvider.sol";

interface IClayMain {
    function getExchangeRate() external view returns (uint256);
}

/**
 * @title csETH Rate Provider
 * @notice Returns the value of csETH in terms of ETH
 */
contract csETHRateProvider is IRateProvider {
    IClayMain public immutable claymain;

    constructor(address _claymain) {
        claymain = IClayMain(_claymain);
    }

    /**
     * @return uint256 the value of csETH in terms of ETH
     */
    function getRate() external view override returns (uint256) {
        return claymain.getExchangeRate();
    }
}
