      "content": "// SPDX-License-Identifier: MIT

pragma solidity 0.8.22;

import { IAsset } from "./IAsset.sol";

interface IBalancerVault {
    enum UserBalanceOpKind {
        DEPOSIT_INTERNAL,
        WITHDRAW_INTERNAL,
        TRANSFER_INTERNAL,
        TRANSFER_EXTERNAL
    }

    /**
