// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

contract All 
{
    function allowance(
        address,
        address
    ) external view virtual returns (uint256) {}
    function balanceOf(address) external view virtual returns (uint256) {}
    function pendingReward(
        uint256,
        address
    ) external view virtual returns (uint256) {}
    function totalSupply() external view virtual returns (uint256) {}
    function getReserves()
        external
        view
        virtual
        returns (uint112, uint112, uint32)
    {}
    // total ether Amount
    uint256 public totalAmount;
    // current ether Amount
    uint256 public currentAmount;
    // sale token amount
    uint256 public saleToken;
    // mint end time
    uint256 public endTime;
    uint256 public parentRewardRatio;
    uint256 public RewardRatioBase;
    address public token0;
    address public token1;
    uint256 public initSupply;
    uint256 public compoundRatio;
    uint256 public lastBlock;
    uint256 public minMintAmount;
    uint256 public maxMintAmount;
    uint256 public internalDecimals;
    uint256 public BASE;
    uint256 public yodasScalingFactor;
}

contract YodaDataProvider 
{
    struct YodaData {
        uint256 tokenBalance;
        uint256 lpBalance;
        uint256 tokenAllowance;
        uint256 lpAllowance;
        uint256 tokenReward;
        uint256 lpReward;
        uint256 token0Reserve;
        uint256 token1Reserve;
        address token0;
        address token1;
        uint256 liquidityTotal;
        uint256 initSupply;
        uint256 totalSupply;
        uint256 totalAmount;
        uint256 currentAmount;
        uint256 saleToken;
        uint256 endTime;
        uint256 parentRewardRatio;
        uint256 RewardRatioBase;
        uint256 compoundRatio;
        uint256 lastBlock;
        uint256 minMintAmount;
        uint256 maxMintAmount;
        uint256 internalDecimals;
        uint256 BASE;
        uint256 yodasScalingFactor;
    }

    constructor() {}

    function checkBaseData
    (
        address tokenContr,
        address idoContr,
        address poolContr,
        address swapPoolContr,
        address user
    ) 
    public view returns (YodaData memory _callbackData) 
    {
        All tokenContract = All(tokenContr);
        All idoContract = All(idoContr);
        All poolContract = All(poolContr);
        All swapPoolContract = All(swapPoolContr);

        if (user != address(0)) 
        {
            _callbackData.tokenBalance = tokenContract.balanceOf(user);
            _callbackData.tokenAllowance = tokenContract.allowance(
                user,
                poolContr
            );
            if (swapPoolContr != address(0)) {
                _callbackData.lpBalance = swapPoolContract.balanceOf(user);
                _callbackData.lpAllowance = swapPoolContract.allowance(
                    user,
                    poolContr
                );
            }
            _callbackData.tokenReward = poolContract.pendingReward(1, user);
            _callbackData.lpReward = poolContract.pendingReward(0, user);
        }

        if (swapPoolContr != address(0)) 
        {
            (
                uint256 _token0Reserve,
                uint256 _token1Reserve,

            ) = swapPoolContract.getReserves();
            _callbackData.token0Reserve = _token0Reserve;
            _callbackData.token1Reserve = _token1Reserve;
            _callbackData.token0 = swapPoolContract.token0();
            _callbackData.token1 = swapPoolContract.token1();
            _callbackData.liquidityTotal = swapPoolContract.totalSupply();
        }

        _callbackData.initSupply = tokenContract.initSupply();
        _callbackData.totalSupply = tokenContract.totalSupply();
        _callbackData.internalDecimals = tokenContract.internalDecimals();
        _callbackData.BASE = tokenContract.BASE();
        _callbackData.yodasScalingFactor = tokenContract.yodasScalingFactor();

        if(idoContr != address(0))
        {
            _callbackData.totalAmount = idoContract.totalAmount();
            _callbackData.currentAmount = idoContract.currentAmount();
            _callbackData.saleToken = idoContract.saleToken();
            _callbackData.endTime = idoContract.endTime();
            _callbackData.minMintAmount = idoContract.minMintAmount();
            _callbackData.maxMintAmount = idoContract.maxMintAmount();
            _callbackData.parentRewardRatio = idoContract.parentRewardRatio();
            _callbackData.RewardRatioBase = idoContract.RewardRatioBase();
        }
        
        _callbackData.lastBlock = poolContract.lastBlock();
        _callbackData.compoundRatio = poolContract.compoundRatio();
    }
}
