      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.21;

import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title TToken
 * @dev Implementation of a basic ERC20 token with blacklist and tax functionality.
 */
contract TToken is Ownable {
    using SafeMath for uint256;

    string public constant name = "TT Token";
    string public constant symbol = "TT";
    uint8 public constant decimals = 12;
    uint256 public constant totalSupply = 100000000000000000000; // 100000 * 0.01 = 1000

    mapping(address => uint256) public balanceOf;
    mapping(address => bool) public isBlacklisted;
    uint256 public taxPercent = 2;
    address public constant burnAddress = 0x000000000000000000000000000000000000dEaD;

    event Transfer(address indexed from, address indexed to, uint256 value);
    event Blacklisted(address indexed account);
    event RemovedFromBlacklist(address indexed account);

    /**
     * @dev Sets the initial supply to the deployer and sets the owner.
     * @param _owner The address of the owner.
     */
    constructor(address payable _owner) Ownable(_owner) {
        require(_owner != address(0), "Owner address cannot be zero");
        balanceOf[msg.sender] = totalSupply;
    }

    /**
     * @dev Transfers tokens to a specified address.
     * @param to The address to transfer to.
     * @param amount The amount to be transferred.
     */
    function transfer(address to, uint256 amount) public {
        require(!isBlacklisted[msg.sender], "Sender is blacklisted");
        require(!isBlacklisted[to], "Recipient is blacklisted");
        require(balanceOf[msg.sender] >= amount, "Insufficient balance");

        uint256 taxAmount = amount.mul(taxPercent).div(100);
        uint256 amountAfterTax = amount.sub(taxAmount);

        _transfer(msg.sender, burnAddress, taxAmount);
        _transfer(msg.sender, to, amountAfterTax);
    }

    /**
     * @dev Internal function to handle transfers.
     * @param from The address to transfer from.
     * @param to The address to transfer to.
     * @param amount The amount to be transferred.
     */
    function _transfer(address from, address to, uint256 amount) internal {
        balanceOf[from] = balanceOf[from].sub(amount);
        balanceOf[to] = balanceOf[to].add(amount);
        emit Transfer(from, to, amount);
    }

    /**
     * @dev Adds an address to the blacklist.
     * @param account The address to blacklist.
     */
    function addToBlacklist(address account) public onlyOwner {
        require(!isBlacklisted[account], "Address already blacklisted");
        isBlacklisted[account] = true;
        emit Blacklisted(account);
    }

    /**
     * @dev Removes an address from the blacklist.
     * @param account The address to remove from the blacklist.
     */
    function removeFromBlacklist(address account) public onlyOwner {
        require(isBlacklisted[account], "Address not blacklisted");
        isBlacklisted[account] = false;
        emit RemovedFromBlacklist(account);
    }
}"
