    enum Rarity {
        None,
        Legendary,
        Epic,
        Rare,
        Common
    }

    struct TokenRarity {
        uint256 tokenID;
        uint256 revealBlock;
    }

    string public prefixURI;
    uint256 public mintPrice;
    uint256 public maxSupply;
    uint256 public revealDelayed;
    bool public enableUnStake;
    bool public enableMint;

    mapping(uint256 => TokenRarity) public tokenRarity;

    function initialize()
    public
    initializerERC721A
    initializer
    {
        __ERC721x_init("Hash War", "hawa");

        prefixURI = "https://prod.api.hawahash.xyz/gateway/token/";

        mintPrice = 0.01 ether;
        maxSupply = 10000;
        revealDelayed = 1;
        enableUnStake = false;
        approvedContract[address(this)] = true;
        enableMint = true;
    }

    function mint(uint256 count) public payable {
        require(enableMint && count > 0);
        require(totalSupply() + count <= maxSupply, "sold out");
        require(msg.value >= mintPrice * count, "insufficient value");

        uint256 currentTokenID = totalSupply() + 1;
        safeMint(msg.sender, count);

        for (uint256 i = 0; i < count;) {
            tokenRarity[currentTokenID] = TokenRarity(
                currentTokenID,
                block.number + revealDelayed
            );

        unchecked {
            currentTokenID++;
            i++;
        }
        }
